INSERT [dbo].[Services] ([Id], [TenantCode], [ComId], [Action], [Content], [Env], [ConnKey], [RoleIds], [VendorName], [Annonymous], [IsPublicInTenant], [Active], [InsertedDate], [InsertedBy], [UpdatedDate], [UpdatedBy]) VALUES (N'b82a1esdss', N'System', N'Feature', N'GetByName', N'let user = JSON.parse(claims);
let a = JSON.parse(args);
let roles = JSON.parse(JSON.stringify(user.RoleIds)).join('','');


let result = {    
	Query: `select * from Feature where active = 1 and Name = ''${a.Name}''`,
	XQuery: `select p.* 
				from feature f
				join FeaturePolicy p on f.Id = p.FeatureId
				join STRING_SPLIT(''${roles}'', '','', 1) r on p.RoleId = r.[value]
				where f.Name = ''${a.Name}'';
			select cg.* 
				from feature f
				join ComponentGroup cg on f.Id = cg.FeatureId
				where f.Name = ''${a.Name}'' and cg.Active = 1; 
			select c.* 
				from feature f
				join ComponentGroup cg on f.Id = cg.FeatureId
				join Component c on cg.Id = c.ComponentGroupId
				where f.Name = ''${a.Name}'' and cg.Active = 1 and c.Active = 1;`
};
result = JSON.stringify(result);', N'Test', N'default', NULL, NULL, NULL, 1, 1, CAST(N'2023-11-21T00:00:00.0000000+07:00' AS DateTimeOffset), N'1', NULL, NULL)
