CREATE PROCEDURE [dbo].[CloneFeature] 
@target INT
AS
BEGIN
    DECLARE @oldFeatureID INT;
    DECLARE @newFeatureId INT;
    DECLARE @newComponentId INT; --
    DECLARE @Id [INT];
    DECLARE @FeatureId [INT];
    DECLARE @Name [NVARCHAR](50);
    DECLARE @Label [NVARCHAR](50);
    DECLARE @Description [NVARCHAR](200);
    DECLARE @ClassName [VARCHAR](50);
    DECLARE @IsTab [BIT];
    DECLARE @TabGroup [VARCHAR](50);
    DECLARE @IsVertialTab [BIT];
    DECLARE @Responsive [BIT];
    DECLARE @Events [VARCHAR](200);
    DECLARE @Width [VARCHAR](50);
    DECLARE @Style [VARCHAR](200);
    DECLARE @Column [INT];
    DECLARE @Row [INT];
    DECLARE @PolicyId [INT];
    DECLARE @Hidden [BIT];
    DECLARE @ParentId [INT];
    DECLARE @Order [INT];
    DECLARE @Active [BIT];
    DECLARE @InsertedDate [DATETIME2](7);
    DECLARE @InsertedBy [INT];
    DECLARE @UpdatedDate [DATETIME2](7);
    DECLARE @UpdatedBy [INT];
    DECLARE @Disabled [BIT];
    DECLARE @XsCol [INT];
    DECLARE @SmCol [INT];
    DECLARE @LgCol [INT];
    DECLARE @XlCol [INT];
    DECLARE @XxlCol [INT];
