    DECLARE @XlOuterColumn [INT];
    DECLARE @XxlOuterColumn [INT];
    DECLARE @RoleId [INT];
    DECLARE @Icon [NVARCHAR](100);
    DECLARE @IgnoreSync [BIT];
    DECLARE @BadgeMonth [INT];
    DECLARE @IsPrivate [INT]; --
	--
	DECLARE @maxCount int;
    DECLARE @oldName NVARCHAR(500);
	SELECT @oldFeatureID = Id,@oldName = Name
    FROM dbo.Feature
    WHERE id = @target;
    PRINT 'Old feature ID: ' + CAST(@oldFeatureID AS NVARCHAR(50));
    IF (@oldFeatureID IS NOT NULL)
    BEGIN
	
	SELECT @maxCount = COUNT(id) + 1 FROM dbo.Feature WHERE name LIKE @oldName + '%';

        INSERT INTO dbo.Feature
        (
            Name,
            Label,
            ParentId,
            [Order],
            ClassName,
            Style,
            StyleSheet,
            Script,
            EVENTS,
            Icon,
            IsDevider,
            IsGroup,
            IsMenu,
            IsPublic,
            StartUp,
            ViewClass,
            EntityId,
            Description,
            Active,
            InsertedDate,
            InsertedBy,
            UpdatedDate,
            UpdatedBy,
            IsSystem,
            IgnoreEncode,
            RequireJS,
            GuiInfo,
            RoleId,
            IsPermissionInherited,
            FeatureGroup,
            InheritParentFeature
        )
        SELECT Name + CAST(@maxCount AS NVARCHAR(500)) ,
               Label,
               ParentId,
               [Order],
               ClassName,
               Style,
               StyleSheet,
               Script,
               EVENTS,
               Icon,
               IsDevider,
               IsGroup,
               IsMenu,
               IsPublic,
               StartUp,
               ViewClass,
               EntityId,
               Description,
               Active,
               InsertedDate,
               InsertedBy,
               UpdatedDate,
               UpdatedBy,
               IsSystem,
               IgnoreEncode,
               RequireJS,
               GuiInfo,
               RoleId,
               IsPermissionInherited,
               FeatureGroup,
               InheritParentFeature
        FROM dbo.Feature
        WHERE Id = @oldFeatureID;
        SET @newFeatureId = SCOPE_IDENTITY();
        INSERT INTO dbo.FeaturePolicy
        (
            FeatureId,
            RoleId,
            CanRead,
            CanWrite,
            CanDelete,
            Active,
            InsertedDate,
            InsertedBy,
            UpdatedDate,
            UpdatedBy,
            CanDeactivate,
            LockDeleteAfterCreated,
            LockUpdateAfterCreated,
            EntityId,
            RecordId,
            UserId,
            CanShare,
            [Desc]
        )
        SELECT @newFeatureId,
               RoleId,
               CanRead,
               CanWrite,
               CanDelete,
               Active,
               InsertedDate,
               InsertedBy,
               UpdatedDate,
               UpdatedBy,
               CanDeactivate,
               LockDeleteAfterCreated,
               LockUpdateAfterCreated,
               EntityId,
               RecordId,
               UserId,
               CanShare,
               [Desc]
        FROM dbo.FeaturePolicy
        WHERE FeatureId = @oldFeatureID;
        PRINT 'created FeaturePolicy';
        INSERT INTO dbo.GridPolicy
        (
            FeatureId,
            EntityId,
            FieldName,
            [Order],
            GroupOrder,
            ShortDesc,
            Description,
            IsPivot,
            ReferenceId,
            FilterTemplate,
            RefClass,
            DataSource,
            GroupName,
            FormatCell,
            FormatRow,
            PlainText,
            Width,
            MinWidth,
            MaxWidth,
            PRECISION,
            Validation,
            TextAlign,
            Frozen,
            Hidden,
            EVENTS,
            ClassName,
            Style,
            ChildStyle,
            Icon,
            AdvancedSearch,
            HasFilter,
            Editable,
            Disabled,
            ComponentType,
            PopulateField,
            CascadeField,
            SUMMARY,
            SummaryColSpan,
            ComponentId,
            DefaultVal,
            Active,
            InsertedDate,
            InsertedBy,
            UpdatedDate,
            UpdatedBy,
            RoleId,
            IgnoreSync,
            AutoFit,
            IsPrivate,
            BasicSearch
        )
        SELECT @newFeatureId,
               EntityId,
               FieldName,
               [Order],
               GroupOrder,
               ShortDesc,
               Description,
               IsPivot,
               ReferenceId,
               FilterTemplate,
               RefClass,
               DataSource,
               GroupName,
               FormatCell,
               FormatRow,
               PlainText,
               Width,
               MinWidth,
               MaxWidth,
               PRECISION,
               Validation,
               TextAlign,
               Frozen,
               Hidden,
               EVENTS,
               ClassName,
               Style,
               ChildStyle,
               Icon,
               AdvancedSearch,
               HasFilter,
               Editable,
               Disabled,
               ComponentType,
               PopulateField,
               CascadeField,
               SUMMARY,
               SummaryColSpan,
               ComponentId,
               DefaultVal,
               Active,
               InsertedDate,
               InsertedBy,
               UpdatedDate,
               UpdatedBy,
               RoleId,
               IgnoreSync,
               AutoFit,
               IsPrivate,
               BasicSearch
        FROM dbo.GridPolicy
        WHERE FeatureId = @oldFeatureID;
        PRINT 'Created feature policy'; ---
        DECLARE cursorComponentGroup CURSOR FOR
        SELECT Id,
               FeatureId,
               Name,
               Label,
               Description,
               ClassName,
               IsTab,
               TabGroup,
               IsVertialTab,
               Responsive,
               EVENTS,
               Width,
               Style,
               [Column],
               ROW,
               PolicyId,
               Hidden,
               ParentId,
               [Order],
               Active,
               InsertedDate,
               InsertedBy,
               UpdatedDate,
               UpdatedBy,
               Disabled,
               XsCol,
