               XxlOuterColumn,
               RoleId,
               Icon,
               IgnoreSync,
               IsPrivate,
               BadgeMonth
        FROM dbo.ComponentGroup
        WHERE FeatureId = @oldFeatureID;
        OPEN cursorComponentGroup;
        FETCH NEXT FROM cursorComponentGroup
        INTO @Id,
             @FeatureId,
             @Name,
             @Label,
             @Description,
             @ClassName,
             @IsTab,
             @TabGroup,
             @IsVertialTab,
             @Responsive,
             @Events,
             @Width,
             @Style,
             @Column,
             @Row,
             @PolicyId,
             @Hidden,
             @ParentId,
