             @XsOuterColumn, @SmOuterColumn, @LgOuterColumn, @XlOuterColumn, @XxlOuterColumn, @RoleId, @Icon,
             @IgnoreSync, @IsPrivate, @BadgeMonth);
            SET @newComponentId = SCOPE_IDENTITY();
            INSERT INTO dbo.Component
