        voucher.InvoiceDate = null;
        voucher.InvNotes = this.EditForm.Entity.Notes;
        voucherDetail.forEach(item => {
            item.Id = this.Uuid7.NewGuid();
            item.InsertedBy = this.Token.UserId;
            item.DescriptionIdText = item.Description ? item.Description.Name : null;
            item.HblNo = item.Shipment.HblNo;
            item.FileNo = item.File.Code;
            if (item.CurrencyId == this.EditForm.Entity.CurrencyId) {
                item.ExchangeRateINV = this.Decimal(1);
            }
            else {
                item.ExchangeRateINV = item.ExchangeRateINV || voucher.ExchangeRateINV;
            }
            var vat = this.Decimal(item.Vat || 0);
            item.Quantity = this.Decimal(item.Quantity);
            item.ExAmount = this.Decimal(this.Decimal(item.Amount).times(item.ExchangeRateINV).toFixed(0));
            item.ExTotalAmount = this.Decimal(this.Decimal(item.ExAmount).times(item.Quantity).toFixed(0));
            if (vat.isNegative()) {
                item.ExAmountTax = this.Decimal(item.Vat).abs();
            } else {
                item.ExAmountTax = this.Decimal(this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toFixed(0));
            }
            item.ExTotalAmountTax = this.Decimal(this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax).toFixed(0));
            item.IsManual = true;
            item.IsLock = false;
            item.NoSubmit = false;
            item.IsInvoice = false;
            item.IsPayment = false;
            item.IsInvoice = false;
            item.IsPaymentAcc = false;
            item.IsDebtAcc = false;
            item.IsPaid = false;
        });
        voucher.VoucherDetail = voucherDetail;
        if (voucher.VoucherDetail && voucher.VoucherDetail.length > 0) {
            const calculateTotal = (field) => voucher.VoucherDetail.reduce((acc, x) => acc.plus(this.Decimal(x[field] || 0)), this.Decimal(0));
            if (voucher.IsMultiple) {
                const totalAmount = calculateTotal("ExTotalAmount");
                const amountTax = calculateTotal("ExAmountTax");
                voucher.TotalAmountTax = this.Decimal(totalAmount.plus(amountTax).toFixed(0));
                voucher.TotalAmount = totalAmount;
                voucher.AmountTax = amountTax;
            }
            else {
                const totalAmount = this.Decimal(calculateTotal("ExTotalAmount").toFixed(0));
                const amountTax = this.Decimal((totalAmount.times(voucher.VatInv || new this.Decimal(0)).div(100)).toFixed(0));
                const totalAmountTax = totalAmount.plus(amountTax);
                voucher.TotalAmountTax = totalAmountTax;
                voucher.TotalAmount = totalAmount;
                voucher.AmountTax = amountTax;
            }
        }
        else {
            voucher.TotalAmountTax = this.Decimal(0);
            voucher.TotalAmount = this.Decimal(0);
            voucher.AmountTax = this.Decimal(0);
        }
        var check = [...new Set(voucher.VoucherDetail.map(x => x.InvoiceNo).filter(x => x != null))];
        if (check.length > 1) {
            voucher.InvoiceNo = null;
        }
        else {
            var detail = voucher.VoucherDetail.find(x => x.InvoiceNo);
            if (detail) {
                voucher.InvoiceNo = detail.InvoiceNo;
            }
        }
        if (check) {
            this.EditForm.Entity.ReferencesIds = check.join(",");
        }
        const grouped = voucher.VoucherDetail.reduce((acc, x) => {
            const key = `${x.FileNo}_${x.HblNo}`;
            if (!acc[key]) acc[key] = [];
            acc[key].push(x.Description ? x.Description.Description : null);
            return acc;
        }, {});

        const descriptions = Object.entries(grouped)
            .map(([key, names]) => {
                const [fileNo, hblNo] = key.split("_");
                const nameString = names.length > 1 ? `${names.join(", ")}` : names[0];
                return `${nameString} (Số lô: ${fileNo}, Số HBL: ${hblNo})`;
            })
            .join("; ");
        voucher.Description = descriptions;
        this.OpenPopup("debt-accounting-editor", voucher);
    },
