return {
    StartSchedule: async () => {
        var rs = await this.Client.PostAsync(this.Entity, "/api/StartSchedule");
        this.Toast.Success("Start schedule mail success");
        this.EditForm.Entity = rs;
        this.EditForm.UpdateView(false, false);
        var gridDetail = this.EditForm.OpenFrom.ChildCom.find(x => x.IsListView);
        if (gridDetail) {
            gridDetail.ReloadData();
        }
    },
    PauseSchedule: async () => {
        var rs = await this.Client.PostAsync(this.Entity, "/api/PauseSchedule");
        this.Toast.Success("Pause schedule mail success");
        this.EditForm.Entity = rs;
        this.EditForm.UpdateView(false, false);
        var gridDetail = this.EditForm.OpenFrom.ChildCom.find(x => x.IsListView);
        if (gridDetail) {
            gridDetail.ReloadData();
        }
    }
}

