return {
    OnSave: function (form, gridview) {
        var todoId = 1;
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (!detail || (detail && detail.AllListViewItem.length == 0)) {
            return;
        }
        this.Entity.ServiceText = detail.AllListViewItem.flatMap(x => x.Entity).flatMap(x => x.Service.Name).filter(x => x).join(", ");
        var entitys = detail.AllListViewItem.map(x => x.Entity);
        this.Entity.UserReceiverIds = entitys.filter(x => x.UserReceiverId).map(x => x.UserReceiverId).join(',');
        this.Entity.UserReceiverIdsText = entitys.filter(x => x.UserReceiverId).map(x => x.UserReceiver.FullName).join(',');
        this.Entity.GroupReceiverIds = entitys.filter(x => x.UserReceiverId).map(x => x.GroupReceiverId).join(',');
        this.Entity.GroupReceiverIdsText = entitys.filter(x => x.GroupReceiverId).map(x => x.GroupReceiver.Name).join(',');
        this.Entity.StatusIdsText = entitys.filter(x => x.Status).map(x => x.Status.Name).join(',');
        this.Entity.StatusId = entitys.reduce((max, current) => {
            return current.StatusId > max.StatusId ? current : max;
        }, detail.AllListViewItem.flatMap(x => x.Entity)[0]).StatusId;
        var check = entitys.every(x => x.ProgressId == todoId);
        if (check) {
            this.Entity.ProgressId = todoId;
        }
        detail.AllListViewItem.forEach(row => {
            var entity = row.Entity;
            if ((!row.Entity.UserReceiverId && !row.Entity.GroupReceiverId)) {
                row.Entity.StatusId = 1;
                row.Entity.IsSend = false;
            }
            if (row.Entity.StatusId == 1 && (row.Entity.UserReceiverId != null || row.Entity.GroupReceiverId != null)) {
                row.Entity.StatusId = 2;
                row.Entity.ProgressId = todoId;
            }
            if ([3, 2].some(y => y === row.Entity.ProgressId)) {
                this.Entity.ProgressId = row.Entity.ProgressId;
            }
        });
    },
    SendNotification: function (form, gridview) {
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (!detail || (detail && detail.AllListViewItem.length == 0)) {
            return;
        }
        detail.AllListViewItem.forEach(row => {
            if (!row.Entity.IsSend && (row.Entity.UserReceiverId != null || row.Entity.GroupReceiverId != null)) {
                row.Entity.IsSend = true;
                var patchVM = row.GetPatchVM();
                row.SendEntity().then();
            };
        });
    },
    OnSaveAdd: function (form, gridview) {
        this.Entity = {
            Id: this.Uuid7.NewGuid(),
            StatusId: 1,
            VoucherTypeId: 1,
            ProgressId: null,
            TypeId: 1,
            SaleId: this.Token.UserId
        }
    },
    OnSend: async function (btn) {
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (!detail || (detail && detail.AllListViewItem.length == 0)) {
            return;
        }
        var listViewItem = detail.AllListViewItem.filter(x => x.Entity.UserReceiverId == this.Token.UserId);
        for (let item of listViewItem) {
            item.Entity.IsDecline = false, item.Entity.IsApproved = false, item.Entity.UserDeclineId = null, item.Entity.UserApprovedId = null, item.Entity.StatusId = 2
        }
    },
    OnDecline: async function (btn, form) {
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (!detail || (detail && detail.AllListViewItem.length == 0)) {
            return;
        }
        var listViewItem = detail.AllListViewItem.filter(x => x.Entity.UserReceiverId == this.Token.UserId);
        for (let item of listViewItem) {
            item.Entity.IsApproved = false,
                item.Entity.IsDecline = true,
                item.Entity.UserDeclineId = this.Token.UserId,
                item.Entity.UserApprovedId = null, item.Entity.StatusId = 4
        }
    },
    OnApproved: async function () {
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (!detail || (detail && detail.AllListViewItem.length == 0)) {
            return;
        }
        var listViewItem = detail.AllListViewItem.filter(x => x.Entity.UserReceiverId == this.Token.UserId);
        for (let item of listViewItem) {
            item.Entity.IsDecline = false, item.Entity.IsApproved = true; item.Entity.UserDeclineId = null, item.Entity.UserApprovedId = this.Token.UserId, item.Entity.StatusId = 3
        }
    },
    Edit: function (btn, entity) {
        if (!entity.UserReceiverId && !entity.GroupReceiverId) {
            this.Toast.Warning("Please select user or group receiver");
            return;
        }
        entity.EtaDate = entity.EtaDate || this.Entity.EtaDate;
        entity.EtdDate = entity.EtdDate || this.Entity.EtdDate;
        entity.PolId = entity.PolId || this.Entity.PolId;
        entity.PodId = entity.PodId || this.Entity.PodId;
        entity.StatusId = entity.StatusId || 2;
        entity.DefaultFee = null;
        entity.Pricing = null;
        if (this.EditForm.Dirty) {
            this.EditForm.SavePatch().then(() => {
                this.OpenPopup("inquiry-detail-editor", entity);
            })
        }
        else {
            this.OpenPopup("inquiry-detail-editor", entity);
        }
    },
    SaveAs: async function (btn) {
        debugger;
        var newEntity = JSON.parse(JSON.stringify(this.Entity));
        this.Entity = newEntity;
        this.Entity.Id = this.Uuid7.NewGuid();
        this.Entity.Code = null;
        this.Entity.StatusId = 1;
        this.Entity.UserReceiverId = null;
        this.Entity.GroupReceiverId = null;
        this.Entity.InsertedBy = this.Token.UserId;
        this.Entity.ProgressId = null;
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (detail && detail.AllListViewItem.length > 0) {
            detail.Entity = this.Entity;
            detail.AllListViewItem.forEach(row => {
                row.Entity.Id = this.Uuid7.NewGuid();
                row.Entity.InquiryId = this.Entity.Id;
                row.Entity.PricingId = null;
                row.Entity.QuotationId = null;
                row.Entity.ProgressId = "00741544-0000-0000-8000-04cd471a9375";
                row.Entity.TotalAmount = null;
                row.Entity.StatusId = 1;
                row.Entity.IsSend = false;
                row.Entity.UserReceiverId = null;
                row.Entity.GroupReceiverId = null;
                row.Entity.UserDeclineId = null;
                row.Entity.UserApprovedId = null;
                row.Entity.IsDecline = false;
                row.Entity.InsertedBy = this.Token.UserId;
                row.Entity.DefaultFee = null;
                detail.LoadMasterData([row.Entity]).then(() => {
                    row.Children.forEach(cell => {
                        cell.Entity = row.Entity;
                    });
                    row.UpdateView(true);
                })
            });
            detail.UpdateView(true);
        }
        var detailContainer = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityContainer");
        if (detailContainer && detailContainer.AllListViewItem.length > 0) {
            detailContainer.Entity = this.Entity;
            detailContainer.AllListViewItem.forEach(row => {
                row.Entity.Id = this.Uuid7.NewGuid();
                row.Entity.InquiryId = this.Entity.Id;
                row.Entity.InsertedBy = this.Token.UserId;
                row.Entity.StatusId = 1;
                detailContainer.LoadMasterData([row.Entity]).then(() => {
                    row.Children.forEach(cell => {
                        cell.Entity = row.Entity;
                    });
                    row.UpdateView(true);
                })
            });
            detailContainer.UpdateView(true);
        }
        this.UpdateView(true, false);
        this.Dirty = true;
        this.Toast.Success("Save as success");
    },
    CreateQuote: async function (com) {
        var datas = await com.RunQuerys();
        var selecteds = datas[0];
        var newFees = JSON.parse(JSON.stringify(selecteds));
        var typeId = 2;
        var name = "sea-quote-editor";
        var notFreight = true;
        var detail = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        if (!detail || (detail && detail.AllListViewItem.length == 0)) {
            return;
        }
        var mostEntity = detail.AllListViewItem.find(x => x.Entity.Service.Name.toUpperCase().includes('AIR') || x.Entity.Service.Name.toUpperCase().includes('SEA'));
        if (!mostEntity) {
            notFreight = false;
            mostEntity = detail.AllListViewItem[0];
        }
        if (mostEntity.Entity.Service.Name.toUpperCase().includes('AIR')) {
            typeId = 3;
            name = "air-quote-editor";
        }
        else if (mostEntity.Entity.Service.Name.toUpperCase().includes('SEA')) {
            typeId = 2;
            name = "sea-quote-editor";
        }
        else if (mostEntity.Entity.Service.Name.toUpperCase().includes('TRUCKING')) {
            var log = detail.AllListViewItem.find(x => x.Entity.Service.Name.toUpperCase().includes('LOGISTICS'));
            if (log) {
                mostEntity = log;
                typeId = 5;
                name = "logistics-quote-editor";
            }
            else {
                typeId = 4;
                name = "trucking-quote-editor";
            }
        }
        else {
            typeId = 5;
            name = "logistics-quote-editor";
        }
        var pricing = datas[1].find(x => x.Id == mostEntity.Entity.PricingId)
        var quote = {
            Id: this.Uuid7.NewGuid(),
            VoucherTypeId: 2,
            PricingId: mostEntity.Entity.PricingId,
            InquiryDetailId: mostEntity.Entity.Id,
            StatusId: 1,
            ShipmentTypeId: "00f60c98-0000-0000-8000-43ed4d1e672c",
            TypeId: typeId,
            FromDate: this.EditForm.Entity.FromDate,
            ToDate: this.EditForm.Entity.ToDate,
            ServiceDate: this.dayjs().format('YYYY-MM-DD'),
            AgentId: pricing.PartnerTypeIdsText && pricing.PartnerTypeIdsText.includes('Agent')
                && pricing.ResidenceTypeIdText && pricing.ResidenceTypeIdText.toUpperCase().includes('OVERSEA') ? pricing.VendorId : null,
            CustomerId: this.EditForm.Entity.CustomerId,
            CommodityId: this.EditForm.Entity.CommodityId,
            IncotermId: this.EditForm.Entity.IncotermId,
            EtdDate: this.EditForm.Entity.EtdDate,
            EtaDate: this.EditForm.Entity.EtaDate,
            DeliveryTerm: this.EditForm.Entity.DeliveryTerm,
            Pickup: this.EditForm.Entity.Pickup,
            Delivery: this.EditForm.Entity.Delivery,
            Quantity: this.EditForm.Entity.Quantity,
            Note: mostEntity.Entity.Note,
            UnitId: this.EditForm.Entity.UnitId,
            DeminsionText: this.EditForm.Entity.DeminsionText,
            CW: this.EditForm.Entity.CW,
            GW: this.EditForm.Entity.GW,
            CBM: this.EditForm.Entity.CBM,
            Note: this.EditForm.Entity.Note,
            ServiceId: mostEntity.Entity.ServiceId,
            PolId: mostEntity.Entity.PolId,
            PodId: mostEntity.Entity.PodId,
            IsSimple: false
        };
        var config = JSON.parse(localStorage.getItem("SalesFunction"));
        quote.StatusId = 1;
        quote.NoApproved = config["APPROVE_QUOTATION"] ? true : false;
        var quoD = {
            Id: this.Uuid7.NewGuid(),
            InquiryId: quote.Id,
            OtherFee: false,
            PolId: mostEntity.Entity.PolId,
            Pol: mostEntity.Entity.Pol,
            PodId: mostEntity.Entity.PodId,
            Pod: mostEntity.Entity.Pod,
            FinalDestinationId: mostEntity.Entity.PodId,
            PlaceDeliveryId: mostEntity.Entity.PodId,
            PlaceDeliveryId: mostEntity.Entity.PodId,
            ScheduleIds: pricing ? pricing.ScheduleIds : null,
            TransitTime: pricing ? pricing.TransitTime : null,
            ViaId: pricing ? pricing.ViaId : null,
            VendorId: pricing ? pricing.VendorId : null,
            CarrierId: pricing ? pricing.CarrierId : null,
            PlaceReceiptId: mostEntity.Entity.PolId,
            LinkId: mostEntity.Entity.Id,
            DisableRow: true,
        };
        quote.InquiryDetail = [quoD];
        var containerGrid = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityContainer");
        var containers = containerGrid.AllListViewItem.map(x => x.Entity).filter(x => x.ContainerTypeId) || [];
        containers.forEach(x => {
            x.Id = this.Uuid7.NewGuid();
            x.Quantity = x.Quantity;
            x.ContainerTypeId = x.ContainerTypeId;
            x.InquiryId = quote.Id;
        });
        quote.EntityContainer = containers || [];
        newFees.forEach(item => {
            var actCont = containers.find(x => x.ContainerTypeId == item.UnitId);
            item.Id = this.Uuid7.NewGuid();
            item.DescriptionId = item.DescriptionId;
            item.InquiryId = quote.Id;
            item.RouteId = `${(quoD.Pol.Code || '')} => ${(quoD.Pod.Code || '')}`;
            item.Route = {
                Name: item.RouteId,
                Id: item.RouteId,
            };
            item.Cost = item.Amount;
            item.MinLCLCost = item.MinUnitPrice;
            item.MinQuantityCost = item.MinUnitPrice;
            if (item.IsObh) {
                item.MinLCLRate = item.MinLCLCost;
                item.Rate = item.Cost;
                item.MinQuantityRate = item.MinQuantityCost;
            }
            item.PolId = quoD.PolId;
            item.PodId = quoD.PodId;
            item.FinalDestinationId = quoD.PodId;
            item.PlaceDeliveryId = quoD.PodId;
            item.PlaceReceiptId = quoD.PolId;
            item.PayeeId = item.VendorId != quoD.VendorId ? item.VendorId : null;
            item.DisableRow = true;
            item.OtherFee = true;
            item.IsGrossWeight = item.IsGrossWeight;
            item.GroupFee = item.GroupFee;
            if (this.EditForm.Entity.UnitId == item.OtherUnitId) {
                item.Quantity = this.EditForm.Entity.Quantity;
            }
            else {
                if (actCont && item.IsContainer) {
                    item.Quantity = actCont.Quantity;
                }
            }
            if (item.IsCBM) {
                item.Quantity = this.EditForm.Entity.CBM;
            }
            if (item.IsKGS) {
                item.Quantity = this.EditForm.Entity.CW;
            }
            if (item.IsGrossWeight) {
                item.Quantity = this.EditForm.Entity.GW;
            }
        });
        quote.DefaultFee = newFees;
        this.OpenPopup(name, quote);
    },
    SendMail: function () {
        var entity = {
            To: this.Entity.Email,
            Text: "",
            Subject: ""
        }
        this.OpenPopup("sendmail-editor", entity);
    }
}

