return {
    OnSave: function (form, gridview) {
        if (this.Entity.ActionId == 1) {
            this.Entity.ActionId = 2;
        }
    },
    CalcPrice: function (com, entity) {
        entity.IsManual = true;
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.TotalAmount = this.Decimal(entity.Quantity || 0).times(this.Decimal(entity.Amount || 0));
        entity.ExAmount = this.Decimal(entity.Amount || 0).times(this.Decimal(entity.ExchangeRateINV || 0));
        entity.ExTotalAmount = (entity.Quantity || new this.Decimal(0)).times(entity.ExAmount || new this.Decimal(0));
        if ((entity.Vat || new this.Decimal(0)).isNegative()) {
            entity.ExAmountTax = this.Decimal(entity.Vat).abs();
            entity.AmountTax = this.Decimal(entity.Vat).abs();
        } else {
            entity.ExAmountTax = (entity.ExTotalAmount).times(entity.Vat || new this.Decimal(0)).div(100);
            entity.AmountTax = (entity.TotalAmount).times(entity.Vat || new this.Decimal(0)).div(100);
        }
        entity.ExTotalAmountTax = entity.ExTotalAmount.plus(entity.ExAmountTax);
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.AmountTax);
        com.Parent.UpdateView(false);
    },
    CalcUnitPrice: function (com, entity) {
        entity.IsManual = true;
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.ExTotalAmountTax = this.Decimal(entity.ExTotalAmountTax || 0);
        if ((entity.Vat || new this.Decimal(0)).isNegative()) {
            entity.ExAmountTax = this.Decimal(entity.Vat).abs();
            entity.ExTotalAmount = entity.ExTotalAmountTax.minus(entity.ExAmountTax);
        } else {
            entity.ExTotalAmount = entity.ExTotalAmountTax.div(new this.Decimal(1).plus((entity.Vat || new this.Decimal(0)).div(100)));
            entity.ExAmountTax = entity.ExTotalAmount.times(entity.Vat || new this.Decimal(0)).div(100);
        }
        entity.ExAmount = entity.ExTotalAmount.div(entity.Quantity || new this.Decimal(1));
        com.Parent.UpdateView(false);
    },
    ChangeFileNo: function (com, entity) {
        this.Entity.MblNos = com.MatchedItems.map(x => x.MblNo).filter(x => x).join(',')
        this.EditForm.UpdateView(false, false, "MblNos");
    },
    Invoice: function (com, entity) {
        if (this.Entity.Id.startsWith("-")) {
            var grid = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
            var data = grid.AllListViewItem.filter(x => x.Selected && !x.Entity.NoSubmit);
            if (!data || data.length == 0) {
                this.Toast.Warning("Please selected fee issued");
            }
            else {
                this.EditForm.SaveSelectedDetail().then(() => {
                    if (this.OpenFrom) {
                        var pricing = this.OpenFrom.ChildCom.find(x => x.IsListView);
                        if (pricing) {
                            pricing.ClearSelected();
                            pricing.ActionFilter();
                        }
                    }
                });
            }
        }
        else {
            if (this.Entity.ActionId == 3) {
                this.EditForm.OpenConfig("Are you sure you want to void this invoice?", () => {
                    this.SavePatch();
                }, () => { }, false, [], true);
            }
            else {
                this.EditForm.SavePatch();
            }
        }
    },
    AddInvoice: function (com, entity) {
        var issueInvoice = JSON.parse(JSON.stringify(this.Entity));
        issueInvoice.Id = this.Uuid7.NewGuid();
        issueInvoice.StatusId = this.SalesFunction["APPROVE_VAT_INV"] ? 3 : 1;
        issueInvoice.NoApproved = this.SalesFunction["APPROVE_VAT_INV"] ? true : false;
        issueInvoice.Code = null;
        issueInvoice.VoucherTypeId = 12;
        issueInvoice.ProgressId = 1;
        issueInvoice.TypeId = 3;
        issueInvoice.UserApprovedIds = null;
        issueInvoice.UserViewIds = null;
        issueInvoice.InsertedBy = this.Token.UserId;
        issueInvoice.PostDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        issueInvoice.InvoiceDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        this.EditForm.Entity = issueInvoice;
        this.EditForm.UpdateView(true, false);
        var comShipmentInvoiceDetail = this.EditForm.FirstCom("VoucherDetail");
        comShipmentInvoiceDetail.ReloadData();
    },
    IntUpdateFee: 0,
    ChangeExchangeRate: function () {
        var gridParent = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
        if (gridParent.AllListViewItem && gridParent.AllListViewItem.length > 0) {
            gridParent.AllListViewItem.forEach(item => {
                if (item.Entity.CurrencyId == this.EditForm.Entity.CurrencyId) {
                    item.Entity.ExchangeRateINV = this.Decimal(1);
                }
                else {
                    item.Entity.ExchangeRateINV = this.EditForm.Entity.ExchangeRateINV;
                }
                item.Entity.IsManual = true;
                item.Entity.IsLock = false;
                item.Entity.IsInvoice = false;
                item.Entity.IsPayment = false;
                item.Entity.ExchangeRateINV = this.Decimal(item.Entity.ExchangeRateINV);
                var vat = this.Decimal(item.Entity.Vat || 0)
                item.Entity.Quantity = this.Decimal(item.Entity.Quantity);
                item.Entity.ExAmount = this.Decimal(this.Decimal(item.Entity.Amount).times(item.Entity.ExchangeRateINV).toFixed(0));
                item.Entity.ExTotalAmount = this.Decimal(this.Decimal(item.Entity.ExAmount).times(item.Entity.Quantity).toFixed(0));
                item.Entity.ExAmountTax = this.Decimal(this.Decimal(item.Entity.ExTotalAmount).times(vat.div(this.Decimal(100))).toFixed(0));
                item.Entity.ExTotalAmountTax = this.Decimal(this.Decimal(item.Entity.ExTotalAmount).plus(item.Entity.ExAmountTax).toFixed(0));
                item.UpdateView(true);
            });
        }
    },
    ChangeDocsType: function () {
        var gridParent = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
        if (gridParent.AllListViewItem && gridParent.AllListViewItem.length > 0) {
            gridParent.AllListViewItem.forEach(item => {
                if (this.EditForm.Entity.DocsTypeId != 2) {
                    item.Entity.Docs = item.Entity.HblNo;
                }
                else {
                    item.Entity.Docs = item.Entity.ShipmentInvoiceCode;
                }
                item.UpdateView(true);
            });
        }
    },
    CheckCurrency: function (com, entity) {
        if (entity.CurrencyId == this.EditForm.Entity.CurrencyId) {
            entity.ExchangeRateINV = this.Decimal(1);
        }
        else {
            entity.ExchangeRateINV = this.EditForm.Entity.ExchangeRateINV;
        }
        this.CalcPrice(com, entity);
    },
    ViewDetail: function (grid, entity) {
        this.Entity = entity;
        this.EditForm.UpdateView(false, false);
    },
    AddContextMenu: function (gridview, context) {
        if (this.Entity.ActionId == 2) {
            var data = gridview.AllListViewItem.filter(x => x.Selected && x.Entity.IsInvoice);
            if (data && data.length > 0) {
                context.MenuItems.push({
                    Icon: "fas fa-page-break",
                    Text: "Split Fee",
                    Click: () => this.SplitFee(data.map(x => x.Entity.Id), gridview)
                });
            }
        }
        if (this.Entity.Id.startsWith('-')) {
            var data2 = gridview.AllListViewItem.filter(x => x.Selected && !x.Entity.IsInvoice);
            if (data2 && data2.length > 0) {
                context.MenuItems.push({
                    Icon: "fas fa-plus",
                    Text: "Add Fee",
                    Click: () => this.AddFee(data2, gridview)
                });
            }
        }
    },
    SplitFee: function (data, gridview) {
        var form = gridview.EditForm.OpenConfig("Do do want split fee!", async () => {
            var rs = await gridview.EditForm.Client.PostAsync({ ShipmentInvoiceDetailId: data, ShipmentInvoiceId: this.EditForm.Entity.Id }, "/api/SplitFee");
            if (rs) {
                gridview.ApplyFilter();
                var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
                comShipmentInvoice.ReloadData().then(() => {
                    if (this.EditForm.Entity.Id) {
                        var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                        if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                            item.Element.click();
                        }
                    }
                    else {
                        if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                            comShipmentInvoice.Item[0].Element.click();
                        }
                    }
                });
            }
        }, () => { }, false, []);
    },
    AddFee: function (data, gridview) {
        var shipment = gridview.EditForm.Meta.GridPolicies.find(x => x.FieldName == "ViewShipmentInvoiceId");
        var form = gridview.EditForm.OpenConfig("Do do want add fee!", async () => {
            for (const element of data) {
                let patchVM = {
                    Table: "ShipmentFee",
                    Changes: [
                        { Field: "Id", Value: element.Entity.ShipmentFeeId },
                        { Field: "DescriptionId", Value: element.Entity.DescriptionId },
                        { Field: "TotalAmountTax", Value: element.Entity.TotalAmountTax },
                        { Field: "TotalAmount", Value: element.Entity.TotalAmount },
                        { Field: "AmountTax", Value: element.Entity.AmountTax },
                        { Field: "Amount", Value: element.Entity.Amount },
                        { Field: "Quantity", Value: element.Entity.Quantity },
                        { Field: "UnitId", Value: element.Entity.UnitId },
                        { Field: "Vat", Value: element.Entity.Vat },
                        { Field: "Tax", Value: element.Entity.Tax },
                        { Field: "ExchangeRateINV", Value: element.Entity.ExchangeRateINV }
                    ]
                };
                await this.Client.PatchAsync(patchVM);
            }
            var rs = await gridview.EditForm.Client.PostAsync({ ShipmentInvoiceId: gridview.Entity.ViewShipmentInvoiceId, ShipmentInvoiceDetailId: data.map(x => x.Entity.ShipmentFeeId) }, "/api/AddFee");
            if (rs) {
                gridview.ApplyFilter();
                var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
                comShipmentInvoice.ReloadData().then(() => {
                    if (this.EditForm.Entity.Id) {
                        var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                        if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                            item.Element.click();
                        }
                    }
                    else {
                        if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                            comShipmentInvoice.Item[0].Element.click();
                        }
                    }
                });
            }
        }, () => { }, true, [shipment]);
    },
    ChangeCurrency: function () {
        this.EditForm.Entity.ExchangeRateINV = this.Decimal(1).div(this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateUSD"))[this.EditForm.Entity.Currency.Code]));
        this.EditForm.UpdateView(true, false, "ExchangeRateINV", "ExchangeRateINV2");
        this.ChangeExchangeRate();
    },
    AddDetail: function () {
        var newItem = JSON.parse(JSON.stringify(this.EditForm.Meta.GridPolicies.filter(x => x.EntityId == "AddCharge")));
        this.EditForm.Entity.SearchData = null;
        this.EditForm.Entity.FeeType = "1,2,3,4";
        this.EditForm.Entity.VendorIds = this.EditForm.Entity.VendorIds || this.EditForm.Entity.CompanyId;
        if (!this.EditForm.Entity.TypeId) {
            this.Toast.Warning("Please choose voucher type!");
            return;
        }
        var form = this.EditForm.OpenConfig("Please select fee!", async () => {
            var grid = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "SearchData");
            var gridParent = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
            var feeIds = new Set(gridParent.Item.map(x => x.Entity.ShipmentFeeId));
            var datas = grid.AllListViewItem.filter(x => x.Selected && !x.Entity.DebtId && !feeIds.has(x.Entity.Id)).map(x => x.Entity);
            form.DisposeAfterYes = true;
            if (!datas || datas.length == 0) {
                this.Toast.Warning("Please select fee VAT Invoice");
                form.DisposeAfterYes = false;
                return;
            }
            if (!this.EditForm.Entity.IsMultiple) {
                const vats = new Set(datas.map(x => x.Vat));
                if (vats.size > 1) {
                    this.Toast.Warning("There are multiple VAT types. Please select only one VAT type.");
                    form.DisposeAfterYes = false;
                    return;
                }
                this.EditForm.Entity.VatInv = datas[0].Vat;
            }
            const exchangeRateINVs = new Set(
                datas.map(x => x.ExchangeRateINV).filter(x => x && Number(x) !== 1)
            );
            if (exchangeRateINVs.size > 1) {
                this.EditForm.OpenConfig("There are multiple ExchangeRate types. Please select only one ExchangeRate.", () => {
                }, () => { }, false, [], true);
            }
            const firstExchangeRateINV = Array.from(exchangeRateINVs)[0] || null;
            this.EditForm.Entity.ExchangeRateINV = firstExchangeRateINV || this.Decimal(1).div(this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateUSD"))["VND"]));
            this.EditForm.UpdateView(false, false, "VatInv", "ExchangeRateINV");
            datas.forEach(item => {
                item.ShipmentFeeId = item.Id;
                item.Id = this.Uuid7.NewGuid();
                item.VoucherId = this.EditForm.Entity.Id;
                item.IsManual = true;
                item.IsLock = false;
                item.NoSubmit = false;
                item.IsInvoice = false;
                item.IsPayment = false;
                item.IsInvoice = false;
                item.IsPaymentAcc = false;
                item.IsDebtAcc = false;
                item.IsPaid = false;
                item.DescriptionIdText = item.Description ? item.Description.Name : null;
                item.FileNo = item.File.Code;
                item.HblNo = item.Shipment.HblNo;
                if (item.CurrencyId == this.EditForm.Entity.CurrencyId) {
                    item.ExchangeRateINV = this.Decimal(1);
                }
                else {
                    item.ExchangeRateINV = item.ExchangeRateINV || this.EditForm.Entity.ExchangeRateINV;
                }
                var vat = this.Decimal(item.Vat || 0);
                item.Quantity = this.Decimal(item.Quantity);
                item.ExAmount = this.Decimal(this.Decimal(item.Amount).times(item.ExchangeRateINV).toFixed(0));
                item.ExTotalAmount = this.Decimal(this.Decimal(item.ExAmount).times(item.Quantity).toFixed(0));
                if (vat.isNegative()) {
                    item.ExAmountTax = this.Decimal(item.Vat).abs();
                } else {
                    item.ExAmountTax = this.Decimal(this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toFixed(0));
                }
                item.ExTotalAmountTax = this.Decimal(this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax).toFixed(0));
                item.InvoiceNo = item.InvoiceCode || item.InvoiceNo || item.ShipmentInvoiceCode || this.EditForm.Entity.InvoiceNo;
                item.InvoiceCode = item.InvoiceCode || this.EditForm.Entity.InvoiceNo;
                item.InvoiceDate = this.EditForm.Entity.InvoiceDate;
                item.SeriNo = this.EditForm.Entity.SeriNo;
                item.Form = this.EditForm.Entity.Form;
                item.PartnerId = this.EditForm.Entity.PartnerId;
                item.EinvoiceLink = this.EditForm.Entity.EinvoiceLink;
                item.EinvoiceCode = this.EditForm.Entity.EinvoiceCode;
            });
            gridParent.LoadMasterData(datas).then(() => {
                gridParent.ClearSelected();
                gridParent.AddRows(datas).then(() => {
                    this.EditForm.Dirty = true;
                    this.EditForm.ShowSimple();
                });
            });
        }, () => { }, true, newItem, false, { Column: 24 });
    },
    ToWords: function () {
        function convertNumberToWords(number) {
            if (isNaN(number)) return "Dữ liệu nhập không hợp lệ";
            const units = ["", "nghìn", "triệu", "tỷ", "nghìn tỷ", "triệu tỷ", "tỷ tỷ"];
            const numbers = ["không", "một", "hai", "ba", "bốn", "năm", "sáu", "bảy", "tám", "chín"];
            function readThreeDigits(num) {
                let result = "";
                const hundred = Math.floor(num / 100);
                const ten = Math.floor((num % 100) / 10);
                const unit = num % 10;
                if (hundred > 0) {
                    result += numbers[hundred] + " trăm ";
                    if (ten === 0 && unit > 0) {
                        result += "lẻ ";
                    }
                }
                if (ten > 1) {
                    result += numbers[ten] + " mươi ";
                    if (unit === 1) {
                        result += "mốt ";
                    } else if (unit === 5) {
                        result += "lăm ";
                    } else if (unit > 0) {
                        result += numbers[unit] + " ";
                    }
                } else if (ten === 1) {
                    result += "mười ";
                    if (unit === 1) {
                        result += "một ";
                    } else if (unit === 5) {
                        result += "lăm ";
                    } else if (unit > 0) {
                        result += numbers[unit] + " ";
                    }
                } else if (ten === 0 && unit > 0) {
                    result += numbers[unit] + " ";
                }
                return result.trim();
            }
            function readNumber(num) {
                if (num === 0) return numbers[0];
                let result = "";
                let i = 0;
                while (num > 0) {
                    const segment = num % 1000;
                    if (segment > 0) {
                        const segmentInWords = readThreeDigits(segment);
                        result = segmentInWords + " " + units[i] + " " + result;
                    }
                    num = Math.floor(num / 1000);
                    i++;
                }
                return result.trim();
            }
            if (number == 0) return "Không đồng";
            return readNumber(number) + " đồng";
        }
        let amount = this.EditForm.Entity.TotalAmountTax;
        if (amount !== undefined && amount !== null) {
            let amountText = convertNumberToWords(amount);
            amountText = amountText.charAt(0).toUpperCase() + amountText.slice(1);
            this.EditForm.Entity.AmountText = amountText.trim();
        } else {
            this.EditForm.Entity.AmountText = "";
        }
        this.EditForm.UpdateView(false, false, "AmountText");
        return null;
    },
    CalcTotal: function () {
        var com = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
        if (com.Item && com.Item.length > 0) {
            const calculateTotal = (field) => com.Item.filter(x => !x.Entity.IsAbs).reduce((acc, x) => acc.plus(this.Decimal(x.Entity[field] || 0)), this.Decimal(0));
            if (this.EditForm.Entity.IsMultiple) {
                const totalAmount = calculateTotal("ExTotalAmount");
                const amountTax = calculateTotal("ExAmountTax");
                this.EditForm.Entity.TotalAmountTax = this.Decimal(totalAmount.plus(amountTax).toFixed(0));
                this.EditForm.Entity.TotalAmount = totalAmount;
                this.EditForm.Entity.AmountTax = amountTax;
            }
            else {
                const totalAmount = this.Decimal(calculateTotal("ExTotalAmount").toFixed(0));
                const amountTax = this.Decimal((totalAmount.times(this.EditForm.Entity.VatInv || new this.Decimal(0)).div(100)).toFixed(0));
                const totalAmountTax = totalAmount.plus(amountTax);
                this.EditForm.Entity.TotalAmountTax = totalAmountTax;
                this.EditForm.Entity.TotalAmount = totalAmount;
                this.EditForm.Entity.AmountTax = amountTax;
            }
            this.EditForm.UpdateView(false, false, "TotalAmountTax", "TotalAmount", "AmountTax");
            this.ToWords();
        }
        else {
            this.EditForm.Entity.TotalAmountTax = this.Decimal(0);
            this.EditForm.Entity.TotalAmount = this.Decimal(0);
            this.EditForm.Entity.AmountTax = this.Decimal(0);
            this.EditForm.UpdateView(false, false, "TotalAmountTax", "TotalAmount", "AmountTax");
            this.ToWords();
        }
        var check = [...new Set(com.Item.map(x => x.Entity.InvoiceNo).filter(x => x != null))];
        if (check.length > 1) {
            this.EditForm.Entity.InvoiceNo = null;
            this.EditForm.Entity.InvoiceDate = null;
            this.EditForm.Entity.SeriNo = null;
            this.EditForm.Entity.Form = null;
            this.EditForm.Entity.PartnerId = null;
            this.EditForm.Entity.EinvoiceLink = null;
            this.EditForm.Entity.EinvoiceCode = null;
        }
        else {
            var detail = com.Item.find(x => x.Entity.InvoiceNo);
            if (detail) {
                this.EditForm.Entity.InvoiceNo = detail.Entity.InvoiceNo;
                this.EditForm.Entity.InvoiceDate = detail.Entity.InvoiceDate;
                this.EditForm.Entity.SeriNo = detail.Entity.SeriNo;
                this.EditForm.Entity.Form = detail.Entity.Form;
                this.EditForm.Entity.PartnerId = detail.Entity.PartnerId;
                this.EditForm.Entity.EinvoiceLink = detail.Entity.EinvoiceLink;
                this.EditForm.Entity.EinvoiceCode = detail.Entity.EinvoiceCode;
            }
        }
        if (check) {
            this.EditForm.Entity.ReferencesIds = check.join(",") || this.EditForm.Entity.ReferencesIds;;
        }
        this.EditForm.UpdateView(false, false, "InvoiceNo", "InvoiceDate", "SeriNo", "Form", "PartnerId", "EinvoiceLink", "ReferencesIds");
        if (!this.EditForm.Entity.IsDescription) {
            const grouped = com.Item.filter(x => x.Entity.DescriptionId).reduce((acc, x) => {
                const key = `${x.Entity.FileNo}_${x.Entity.HblNo}`;
                if (!acc[key]) acc[key] = [];
                acc[key].push(x.Entity.Description ? x.Entity.Description.Description : null);
                return acc;
            }, {});
            const descriptions = Object.entries(grouped)
                .map(([key, names]) => {
                    const [fileNo, hblNo] = key.split("_");
                    const nameString = names.length > 1 ? `${names.join(", ")}` : names[0];
                    return `${nameString} (Số lô: ${fileNo}, Số HBL: ${hblNo})`;
                })
                .join("; ");

            this.EditForm.Entity.Description = descriptions;
        }
        this.EditForm.UpdateView(false, false, "Description");
    },
    ChangeDataFee: function () {
        window.clearTimeout(this.EditForm.IntUpdateFee);
        this.EditForm.IntUpdateFee = window.setTimeout(() => {
            var component = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "SearchData");
            component.ReloadData().then(() => {
                component.Item.forEach(item => {
                    if (!item.Entity.DebtId) {
                        item.Selected = true;
                    }
                    else {
                        item.Selected = false;
                    }
                });
            });
        }, 200);
    },
    FirstLoadGrid: function () {
        window.clearTimeout(this.EditForm.IntUpdateFee);
        this.EditForm.IntUpdateFee = window.setTimeout(() => {
            var component = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "SearchData");
            component.Item.forEach(item => {
                if (!item.Entity.DebtId) {
                    item.Selected = true;
                }
                else {
                    item.Selected = false;
                }
            });
        }, 200);
    },
    ShowSimple: function () {
        var grid = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
        if (!this.EditForm.Entity.IsMultiple) {
            if (this.EditForm.Entity.IsSimple) {
                grid.HideColumn("Vat", "ExAmountTax", "ExTotalAmountTax", "ExchangeRateINV", "AmountTax", "VatAccId", "InvoiceNo", "InvoiceDate", "SeriNo", "AmountDifference", "DifferenceAccId", "OBHAccId", "InvoiceConfigId", "EinvoiceLink", "FileNo", "HblNo", "Form", "EinvoiceCode");
            }
            else {
                grid.HideColumn("Vat", "ExAmountTax", "ExTotalAmountTax", "AmountTax", "VatAccId");
            }
        }
        else {
            if (this.EditForm.Entity.IsSimple) {
                grid.HideColumn("ExchangeRateINV", "InvoiceNo", "InvoiceDate", "SeriNo", "AmountDifference", "DifferenceAccId", "OBHAccId", "InvoiceConfigId", "EinvoiceLink", "FileNo", "HblNo", "Form", "EinvoiceCode");
            }
            else {
                grid.HideColumn();
            }
        }
        this.EditForm.UpdateView(false, false, "VatInv");
    },
    FirstShowSimple: function () {
        window.clearTimeout(this.EditForm.IntUpdateFee);
        this.EditForm.IntUpdateFee = window.setTimeout(() => {
            this.EditForm.ShowSimple();
        }, 200);
    },
    ChangeVatAcc: function () {
        var grid = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
        grid.Item.forEach(item => {
            item.Entity.VatAccId = this.EditForm.Entity.VatAccId;
            item.UpdateView();
        });
    },
    UpdateINV: function () {
        if (!this.EditForm.Entity.PartnerId && this.EditForm.Entity.InvoiceNo) {
            this.EditForm.Entity.PartnerId = this.EditForm.Entity.VendorId;
            this.EditForm.UpdateView(false, false, "PartnerId");
        }
        var grid = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "VoucherDetail");
        grid.Item.forEach(item => {
            if (this.EditForm.Entity.TypeId == 3) {
                item.Entity.DebitAccId = this.EditForm.Entity.AccId || item.Entity.DebitAccId;
