return {
    OnSave: function (form, gridview) {
        if (this.Entity.ActionId == 1) {
            this.Entity.ActionId = 2;
        }
    },
    CalcPrice: function (com, entity) {
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.Amount = this.Decimal(entity.Amount || 0);
        entity.TotalAmount = (entity.Quantity || this.Decimal(0)).times(entity.Amount || this.Decimal(0));
        if ((entity.Vat || this.Decimal(0)).isNegative()) {
            entity.AmountTax = this.Decimal(entity.Vat).abs();
        } else {
            entity.AmountTax = (entity.TotalAmount).times(entity.Vat || this.Decimal(0)).div(100);
        }
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.AmountTax);
        if (entity.TypeId == 2) {
            entity.Payable = entity.TotalAmountTax.times(this.Decimal(entity.ExchangeRateINV));
        }
        else {
            entity.Receivable = entity.TotalAmountTax.times(this.Decimal(entity.ExchangeRateINV));
        }
        com.Parent.UpdateView(false);
    },
    UpdateParent: function () {
        var component = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "ShipmentInvoice");
        component.ReloadData();
    },
    ChangeFileNo: function (com, entity) {
        this.Entity.MblNos = com.MatchedItems.map(x => x.MblNo).filter(x => x).join(',')
        this.EditForm.UpdateView(false, false, "MblNos");
    },
    Invoice: function (com, entity) {
        if (this.Entity.Id.startsWith("-")) {
            var grid = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "ShipmentInvoiceDetail");
            var data = grid.AllListViewItem.filter(x => x.Selected && !x.Entity.NoSubmit);
            if (!data || data.length == 0) {
                this.Toast.Warning("Please selected fee issued");
            }
            else {
                this.EditForm.SaveSelectedDetail().then(() => {
                    var component = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "ShipmentInvoice");
                    component.ReloadData().then();
                    if (this.EditForm.Entity.ReceiverIds) {

                    }
                });
            }
        }
        else {
            if (this.Entity.ActionId == 3) {
                this.EditForm.OpenConfig("Are you sure you want to void this invoice?", () => {
                    this.SavePatch();
                }, () => { }, false, [], true);
            }
            else {
                this.EditForm.SavePatch();
            }
        }
    },
    AddInvoice: function (com, entity) {
        var hblGrid = this.EditForm.Parent.ChildCom.find(x => x.Meta.FieldName == "ShipmentDetail");
        var issueInvoice = JSON.parse(JSON.stringify(this.Entity));
        issueInvoice.Id = this.Uuid7.NewGuid();
        issueInvoice.StatusId = 1;
        issueInvoice.ExchangeRateINV = this.Decimal(1).div(this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateUSD"))[this.EditForm.Entity.Currency.Code]));
        issueInvoice.Code = null;
        issueInvoice.FeeTypeId = 0;
        issueInvoice.TypeId = 1;
        issueInvoice.ActionId = 1;
        issueInvoice.UserApprovedIds = null;
        issueInvoice.ShipmentInvoiceId = null;
        debugger;
        issueInvoice.HblIds = this.EditForm.HblIds;
        issueInvoice.FileIds = this.EditForm.FileIds;
        issueInvoice.RequestTypeId = null;
        issueInvoice.DeadlineDate = null;
        issueInvoice.ReceiverIds = null;
        issueInvoice.Remark = null;
        issueInvoice.SeqKey = null;
        issueInvoice.RevisedDate = null;
        issueInvoice.DueDate = null;
        issueInvoice.IsSendPartner = false;
        issueInvoice.PostOtherDate = null;
        issueInvoice.OtherCode = null;
        issueInvoice.PaymentMethodId = null;
        issueInvoice.ProgressId = 1;
        issueInvoice.UserViewIds = null;
        issueInvoice.InsertedBy = this.UserId;
        issueInvoice.PostDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        issueInvoice.InvoiceDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        this.EditForm.Entity = issueInvoice;
        this.EditForm.UpdateView(true, false);
        var comShipmentInvoiceDetail = this.EditForm.FirstCom("ShipmentInvoiceDetail");
        comShipmentInvoiceDetail.ReloadData();
        var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
        comShipmentInvoice.ReloadData().then(() => {
            if (this.EditForm.Entity.Id) {
                var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                    item.Element.click();
                }
            }
            else {
                if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                    comShipmentInvoice.Item[0].Element.click();
                }
            }
        })
    },
    IntUpdateFee: 0,
    ChangeDataFee: function () {
        window.clearTimeout(this.EditForm.IntUpdateFee);
        this.EditForm.IntUpdateFee = window.setTimeout(() => {
            var component = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "ShipmentInvoiceDetail");
            component.ReloadData().then(() => {
                component.Dirty = true;
                component.Item.forEach(item => {
                    if (!item.Entity.IsLock) {
                        item.Selected = true;
                    }
                    else {
                        item.Selected = false;
                    }
                });
            });
        });
    },
    ViewDetail: function (grid, entity) {
        this.Entity = entity;
        this.EditForm.UpdateView(false, false);
    },
    AddContextMenu: function (gridview, context) {
        if (this.Entity.ActionId == 2 && this.Entity.ProgressId == 1) {
            var data = gridview.AllListViewItem.filter(x => x.Selected && x.Entity.IsLock);
            if (data && data.length > 0) {
                context.MenuItems.push({
                    Icon: "fas fa-page-break",
                    Text: "Split Fee",
                    Click: () => this.SplitFee(data.map(x => x.Entity.Id), gridview)
                });
            }
        }
        if (this.Entity.Id.startsWith('-') && this.Entity.ProgressId == 1) {
            var data2 = gridview.AllListViewItem.filter(x => x.Selected && !x.Entity.IsLock);
            if (data2 && data2.length > 0) {
                context.MenuItems.push({
                    Icon: "fas fa-plus",
                    Text: "Add Fee",
                    Click: () => this.AddFee(data2, gridview)
                });
            }
        }
    },
    SplitFee: function (data, gridview) {
        var form = gridview.EditForm.OpenConfig("Do do want split fee!", async () => {
            var rs = await gridview.EditForm.Client.PostAsync({ ShipmentInvoiceDetailId: data, ShipmentInvoiceId: this.EditForm.Entity.Id }, "/api/SplitFee");
            if (rs) {
                gridview.ApplyFilter();
                var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
                comShipmentInvoice.ReloadData().then(() => {
                    if (this.EditForm.Entity.Id) {
                        var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                        if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                            item.Element.click();
                        }
                    }
                    else {
                        if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                            comShipmentInvoice.Item[0].Element.click();
                        }
                    }
                });
            }
        }, () => { }, false, []);
    },
    AddFee: function (data, gridview) {
        var shipment = gridview.EditForm.Meta.GridPolicies.find(x => x.FieldName == "ViewShipmentInvoiceId");
        var form = gridview.EditForm.OpenConfig("Do do want add fee!", async () => {
            for (const element of data) {
                let patchVM = {
                    Table: "ShipmentFee",
                    Changes: [
                        { Field: "Id", Value: element.Entity.ShipmentFeeId },
                        { Field: "DescriptionId", Value: element.Entity.DescriptionId },
                        { Field: "TotalAmountTax", Value: element.Entity.TotalAmountTax },
                        { Field: "TotalAmount", Value: element.Entity.TotalAmount },
                        { Field: "AmountTax", Value: element.Entity.AmountTax },
                        { Field: "Amount", Value: element.Entity.Amount },
                        { Field: "Quantity", Value: element.Entity.Quantity },
                        { Field: "UnitId", Value: element.Entity.UnitId },
                        { Field: "Vat", Value: element.Entity.Vat },
                        { Field: "Tax", Value: element.Entity.Tax },
                        { Field: "ExchangeRateINV", Value: element.Entity.ExchangeRateINV }
                    ]
                };
                await this.Client.PatchAsync(patchVM);
            }
            var rs = await gridview.EditForm.Client.PostAsync({ ShipmentInvoiceId: gridview.Entity.ViewShipmentInvoiceId, ShipmentInvoiceDetailId: data.map(x => x.Entity.ShipmentFeeId) }, "/api/AddFee");
            if (rs) {
                gridview.ApplyFilter();
                var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
                comShipmentInvoice.ReloadData().then(() => {
                    if (this.EditForm.Entity.Id) {
                        var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                        if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                            item.Element.click();
                        }
                    }
                    else {
                        if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                            comShipmentInvoice.Item[0].Element.click();
                        }
                    }
                });
            }
        }, () => { }, true, [shipment]);
    },
    ChangeCurrency: function () {
        this.EditForm.Entity.ExchangeRateINV = this.Decimal(1).div(this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateUSD"))[this.EditForm.Entity.Currency.Code]));
        this.EditForm.UpdateView(true, false, "ExchangeRateINV", "ExchangeRateINV2");
        this.ChangeDataFee();
    },
    VatInvoice: async function (com) {
        var entity = {
            Id: this.Uuid7.NewGuid(),
            StatusId: this.SalesFunction["APPROVE_VAT_INV"] ? 3 : 1,
            NoApproved: this.SalesFunction["APPROVE_VAT_INV"] ? true : false,
            VoucherTypeId: 12,
            HblIds: this.EditForm.Entity.HblIds,
            FileIds: this.EditForm.Entity.FileIds,
            CustomerId: this.EditForm.Entity.VendorIds,
            InsertedBy: this.Token.UserId,
            FeeTypeId: 0,
            InvoiceIds: this.EditForm.Entity.Id,
            TypeId: 3,
            ObhTypeId: 1,
            ActionId: 2,
            PostDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss'),
            InvoiceDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss')
        }
        var company = this.EditForm.Entity.VendorIdsMasterData;
        if (company.AssignmentInvId) {
            company = company.AssignmentInv;
        }
        entity.DueDate = this.EditForm.Entity.DueDate;
        entity.CompanyId = company.Id;
        entity.CompanyIdText = company.CompanyNameInv;
        entity.CompanyAddress = company.AddressInv;
        entity.TaxCode = company.TaxCode;
        var datas = await com.RunQuerys();
        var vatDetail = datas[0];
        vatDetail.forEach(item => {
            item.ShipmentFeeId = item.Id;
            item.Id = this.Uuid7.NewGuid();
            item.ShipmentInvoiceId = entity.Id;
            item.Docs = item.HblNo;
            item.IsManual = true;
            item.IsLock = false;
            item.IsInvoice = false;
            item.IsPayment = false;
            if (item.CurrencyId == this.EditForm.Entity.CurrencyId) {
                item.ExchangeRateINV = this.Decimal(1);
            }
            else {
                item.ExchangeRateINV = this.EditForm.Entity.ExchangeRateINV;
            }
            var vat = this.Decimal(item.Vat || 0)
            item.Quantity = this.Decimal(item.Quantity);
            item.ExAmount = this.Decimal(this.Decimal(item.Amount).times(item.ExchangeRateINV).toFixed(0));
            item.ExTotalAmount = this.Decimal(this.Decimal(item.ExAmount).times(item.Quantity).toFixed(0));
            item.ExAmountTax = this.Decimal(this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toFixed(0));
            item.ExTotalAmountTax = this.Decimal(this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax).toFixed(0));
        });
        entity.ShipmentInvoiceDetail = vatDetail;
        this.OpenPopup("vatinvoice-editor", entity);
    }
}

