Script:
return {
    Add: function (btn, entity) {
        var entity = {
            TypeId: 1,
            VoucherTypeId: 8,
            StatusId: 1,
            ActionId: 1,
            InsertedBy: this.Token.UserId,
            UserId: this.Token.UserId,
            UserCode: this.Token.Code,
            PositionId: this.Token.PositionId,
            DepartmentId: this.Token.DepartmentId,
            VoucherDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss')
        }
        this.OpenPopup("advance-request-editor", entity);
    },
    AddReim: function (btn, entity) {
        var entity = {
            TypeId: 2,
            VoucherTypeId: 9,
            StatusId: 1,
            ActionId: 1,
            InsertedBy: this.Token.UserId,
            UserId: this.Token.UserId,
            UserCode: this.Token.Code,
            PositionId: this.Token.PositionId,
            DepartmentId: this.Token.DepartmentId,
            VoucherDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss')
        }
        this.OpenPopup("reimbursement-form-editor", entity);
    },
    Edit: function (btn, entity) {
        if (entity.TypeId === 1) {
            this.OpenPopup("advance-request-editor", entity);
        } else if (entity.TypeId === 2) {
            this.OpenPopup("reimbursement-form-editor", entity);
        }
    }
}

