return {
    CalcWeight: function (form, gridview) {
        var detail = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityDimension");
        if ((detail && detail.AllListViewItem.length > 0)) {
            var rowData = detail.AllListViewItem.map(x => x.Entity);
            var volumeWeight = rowData.reduce((a, b) => a.plus(((new this.Decimal(b.Width || 0)).times(new this.Decimal(b.Height || 0)).times(new this.Decimal(b.Length || 0))).times((new this.Decimal(b.Quantity || 0))).div(new this.Decimal(6000))), new this.Decimal(0));
            var kgs = rowData.reduce((a, b) => a.plus(((new this.Decimal(b.Width || 0)).times(new this.Decimal(b.Height || 0)).times(new this.Decimal(b.Length || 0))).times((new this.Decimal(b.Quantity || 0))).div(new this.Decimal(1000000))), new this.Decimal(0));
            this.EditForm.Entity.VolumeWeight = volumeWeight || new this.Decimal(0);
            this.EditForm.Entity.Kgs = kgs || new this.Decimal(0);
            this.EditForm.UpdateView(false, false, "VolumeWeight", "Kgs");
        }
    },
    SetDefaultCW: function (form, gridview) {
        var cw = this.EditForm.Entity.VolumeWeight.greaterThan(this.EditForm.Entity.GW) ? this.EditForm.Entity.VolumeWeight : this.EditForm.Entity.GW;
        this.EditForm.Entity.CW = this.EditForm.Entity.CW || cw;
        this.EditForm.Entity.CBM = this.EditForm.Entity.CBM || this.EditForm.Entity.Kgs;
        this.EditForm.UpdateView(false, false, "CBM", "CW");
        this.EditForm.Dirty = true;
    },
    CreateBookingNotes: function (btn) {
        if (this.EditForm.Entity.BookingNoteId) {
            this.OpenPopup("air-booking-note-editor", { Id: this.EditForm.Entity.BookingNoteId }, true);
            return;
        }
        var form = this.EditForm.OpenConfig("Do you want create air booking notes!", () => {
            var booking = JSON.parse(JSON.stringify(this.EditForm.Entity));
            booking.Id = this.Uuid7.NewGuid();
            booking.StatusId = 3;
            booking.BookingLocalId = this.EditForm.Entity.Id;
            booking.BookingNoteId = null;
            booking.BookingOrderId = null;
            booking.VoucherTypeId = 4;
            booking.TypeId = 5;
            booking.InsertedBy = this.Token.UserId;
            booking.BookingNo = null;
            booking.ClosingTimeDate = booking.CutOffDate;
            booking.BookingDate = this.dayjs().format('YYYY-MM-DD');
            var inquiryContainer = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityDimension");
            var items = inquiryContainer.AllListViewItem.map(x => x.Entity);
            var newItems = JSON.parse(JSON.stringify(items));
            newItems.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.EntityId = booking.Id;
            })
            booking.EntityDimension = newItems;
            this.OpenPopup("air-booking-note-editor", booking);
        }, () => { });
    },
    CreateBookingOrder: function (btn) {
        if (this.EditForm.Entity.BookingOrderId) {
            this.OpenPopup("air-booking-order-editor", { Id: this.EditForm.Entity.BookingOrderId }, true);
            return;
        }
        var form = this.EditForm.OpenConfig("Do you want create air booking order!", async () => {
            var transit = (await btn.RunQuerys())[0];
            var booking = JSON.parse(JSON.stringify(this.EditForm.Entity));
            booking.Id = this.Uuid7.NewGuid();
            booking.BookingLocalId = this.EditForm.Entity.Id;
            booking.BookingNoteId = null;
            booking.BookingOrderId = null;
            booking.StatusId = 3;
            booking.VoucherTypeId = 5;
            booking.TypeId = 2;
            booking.InsertedBy = this.Token.UserId;
            booking.ClosingTimeDate = this.EditForm.Entity.CutOffDate;
            booking.BookingNo = null;
            booking.BookingDate = this.dayjs().format('YYYY-MM-DD');
            var bookingDimension = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityDimension");
            var itemsDimension = bookingDimension.AllListViewItem.map(x => x.Entity);
            var newItemsDimension = JSON.parse(JSON.stringify(itemsDimension));
            newItemsDimension.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.EntityId = booking.Id;
            });
            if (transit) {
                transit.forEach(item => {
                    item.Id = this.Uuid7.NewGuid();
                    item.EntityId = booking.Id;
                });
                booking.EntityTransit = transit;
            }
            booking.EntityDimension = newItemsDimension;
            this.OpenPopup("air-booking-order-editor", booking);
        }, () => { });
    },
    ForwardBookingLocal: function (form, gridview) {
        var newItem = JSON.parse(JSON.stringify(this.EditForm.ChildCom.filter(x => x.Meta.FieldName == "ReceiverIds").map(x => x.Meta)));
        newItem.forEach(item => {
            item.FieldName = "ForwardId";
            item.IsMultiple = false;
            item.Label = "Forward";
        });
        var form = this.EditForm.OpenConfig("Please select user forward!", () => {
            if (!this.Entity.ForwardId) {
                return;
            }
            this.EditForm.ActForwordEntity();
        }, () => { }, true, newItem);
    },
    CreateShipment: function (btn) {
        var featureName = "air-export-editor";
        switch (this.Entity.ServiceId) {
            case 1:
                featureName = "air-export-editor";
                break;
            case 2:
                featureName = "air-import-editor";
                break;
            default:
                break;
        }
        if (this.EditForm.Entity.ShipmentId) {
            this.OpenPopup(featureName, { Id: this.EditForm.Entity.ShipmentId, BookingLocalId: this.EditForm.Entity.Id }, true);
            return;
        }
        var form = this.EditForm.OpenConfig("Do you want to create shipment air!", async () => {
            var transit = (await btn.RunQuerys())[0];
            var shipment = JSON.parse(JSON.stringify(this.EditForm.Entity));
            var hblNo = shipment.HblNo;
            var bookingNo = shipment.BookingNo;
            shipment.Id = this.Uuid7.NewGuid();
            shipment.BookingLocalId = this.EditForm.Entity.Id;
            shipment.Code = null;
            shipment.BookingNo = shipment.ReferenceNo || shipment.RefNo;
            shipment.HblNo = null;
            shipment.ParentId = null;
            shipment.StatusId = 3;
            shipment.VoucherTypeId = 6;
            shipment.FreightId = shipment.PaymentTermId;
            shipment.ShipmentDate = this.dayjs().format('YYYY-MM-DD');
            shipment.TypeId = shipment.ServiceId;
            shipment.InsertedBy = this.Token.UserId;
            shipment.BookingDate = this.dayjs().format('YYYY-MM-DD');
            shipment.VesselVoy = transit && transit[0] ? transit[0].TransitNo : null;
            var bookingDetail = this.EditForm.FirstCom("BookingDetail");

            var entityDimension = this.EditForm.FirstCom("EntityDimension");
            var items = entityDimension.AllListViewItem.map(x => x.Entity);
            var newItems = JSON.parse(JSON.stringify(items));
            newItems.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.EntityId = shipment.Id;
            });
            shipment.EntityDimension = newItems;
            shipment.EntityTransit = [{
                Id: this.Uuid7.NewGuid(),
                EntityId: shipment.Id,
                PortTransitId: shipment.PortTransitId,
                EtaTsDate: shipment.EtaTsDate,
                EtdTsDate: shipment.EtdTsDate,
                TransitNo: shipment.FlightNoConnect,
            }];
            var shipmentDetail = JSON.parse(JSON.stringify(shipment));
            shipmentDetail.ParentId = shipment.Id;
            shipmentDetail.Id = this.Uuid7.NewGuid();
            var newItems2 = JSON.parse(JSON.stringify(items));
            newItems2.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.EntityId = shipmentDetail.Id;
            });
            shipmentDetail.EntityDimension1 = newItems2;
            shipmentDetail.ShipmentFreight = [
                {
                    Id: this.Uuid7.NewGuid(),
                    ShipmentId: shipmentDetail.Id,
                    PmTypeId: 1,
                },
                {
                    Id: this.Uuid7.NewGuid(),
                    ShipmentId: shipmentDetail.Id,
                    PmTypeId: 2,
                }
            ];
            shipmentDetail.EntityAttachedIn = [
                {
                    Id: this.Uuid7.NewGuid(),
                    ShipmentId: shipmentDetail.Id,
                    Name: "Air house bill",
                    TypeId: 1,
                },
                {
                    Id: this.Uuid7.NewGuid(),
                    Name: "Booking Oder",
                    ShipmentId: shipmentDetail.Id,
                    TypeId: 1,
                },
                {
                    Id: this.Uuid7.NewGuid(),
                    Name: "Other",
                    ShipmentId: shipmentDetail.Id,
                    TypeId: 1,
                }
            ];
            let agent = this.Entity.Agent;
            let agentText = "";
            if (agent) {
                agentText += `${(agent.CompanyName || '')}\n`;
                agentText += `${(agent.Address || '')}\n`;
                agentText += `Tell: ${(agent.PhoneNumber || '')} Fax: ${(agent.Fax || '')}\n`;
                agentText += `Contact person: ${(agent.ContactName || '')}`;
            }
            shipmentDetail.ShipperDes = shipment.ShipperDes;
            shipmentDetail.ConsigneeDes = shipment.ConsigneeDes;
            shipmentDetail.HblNo = hblNo;
            shipmentDetail.BookingNo = shipmentDetail.ReferenceNo || bookingNo;
            shipmentDetail.PolId = shipment.PolId;
            shipmentDetail.AirportDepartureId = shipment.PolId;
            shipmentDetail.PlaceId = shipment.PolId;
            shipmentDetail.PodId = shipment.PodId;
            shipmentDetail.EtaDate = shipment.EtaDate;
            shipmentDetail.EtdDate = shipment.EtdDate;
            shipmentDetail.ExecutedDate = shipment.EtdDate;
            shipmentDetail.To1 = shipment.Pod ? shipment.Pod.Code : null;
            var entityTransit = shipment.EntityTransit[0];
            if (entityTransit) {
                shipmentDetail.To2 = !this.Entity.PortTransit ? "" : this.Entity.PortTransit.Code;
                shipmentDetail.CFlights1 = entityTransit.TransitNo;
                shipmentDetail.CFlights1Date = entityTransit.EtdTsDate;
            }
            shipmentDetail.ByFirstCarrier = this.Entity.Vendor ? this.Entity.Vendor.Name : null;
            shipmentDetail.FlightsDate = this.Entity.EtdDate;
            shipmentDetail.VesselVoy = shipment.VesselVoy;
            shipmentDetail.CommodityId = this.Entity.CommodityId;
            shipmentDetail.PortTransitId = this.Entity.PortTransitId;
            shipmentDetail.PlaceReceiptId = this.Entity.PolId;
            shipmentDetail.PlaceDeliveryId = this.Entity.PodId;
            shipmentDetail.SaillingDate = this.Entity.EtdDate;
            shipmentDetail.IssueDate = this.Entity.EtaDate;
            shipmentDetail.PlaceIssueId = this.Entity.PolId;
            shipmentDetail.ForDeliveryGoodsId = this.Entity.AgentId;
            shipmentDetail.ForDeliveryGoodsDes = agentText.trim();
            shipmentDetail.WTVALId = this.Entity.PaymentTermId
            var cw = this.Decimal(shipmentDetail.GW || 0).greaterThan(this.Decimal(this.EditForm.Entity.VolumeWeight || 0)) ? this.Decimal(shipmentDetail.GW || 0) : this.Decimal(shipmentDetail.VolumeWeight || 0);
            shipmentDetail.CW = cw;

            if (!shipmentDetail.PlaceReceiptId) {
                shipmentDetail.PlaceReceiptId = shipment.PolId;
            }
            if (!shipmentDetail.PlaceDeliveryId) {
                shipmentDetail.PlaceDeliveryId = shipment.PodId;
            }
            var itemsFee = bookingDetail.AllListViewItem.filter(x => !x.GroupRow && x.Entity).map(x => x.Entity);
            var feeItems = JSON.parse(JSON.stringify(itemsFee.filter(x => !x.IsBuying && !x.IsAdv && !x.IsAdvCustomer && !x.IsAdvProvider)));
            var fee1Items = JSON.parse(JSON.stringify(itemsFee.filter(x => x.IsBuying && !x.IsAdv && !x.IsAdvCustomer && !x.IsAdvProvider)));
            var fee3Items = JSON.parse(JSON.stringify(itemsFee.filter(x => x.IsAdv || x.IsAdvCustomer || x.IsAdvProvider)));
            feeItems.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.ShipmentId = shipmentDetail.Id;
                item.TypeId = 1;
                if (item.IsObh) {
                    var obhFee = fee1Items.find(x => x.DescriptionId == item.DescriptionId && x.IsObh);
                    item.ObhId = obhFee.VendorId;
                }
            })
            shipmentDetail.ShipmentFee = feeItems;
            fee1Items = fee1Items.filter(x => !x.IsObh);
            fee1Items.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.ShipmentId = shipmentDetail.Id;
                item.TypeId = 2;
            })
            fee3Items.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.ShipmentId = shipmentDetail.Id;
                item.TypeId = 3;
            })
            shipmentDetail.ShipmentFee2 = fee1Items;
            shipmentDetail.ShipmentFee3 = fee3Items;
            this.OpenPopup(featureName, shipment, false, { ShipmentDetail: shipmentDetail, Dirty: true, _dirtyShipmentDetail: true });
        }, () => { });
    }
}

