var groupedItems = {};
var listItem = detail.AllListViewItem.filter(x => !x.GroupRow).map(x => x.Entity);
var sellingRate = listItem.filter(x => !x.IsBuying && !x.IsObh && x.TotalAmount);
var buyingRate = listItem.filter(x => x.IsBuying && !x.IsObh && x.TotalAmount);
var obh = listItem.filter(x => !x.IsBuying && x.IsObh && x.TotalAmount);
var vnd = {
    CurrencyText: "VND",
    TotalRevenue: (sellingRate.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount).times(b.ExchangeRateVND || 1) || 0), this.Decimal(0))),
    TotalCost: (buyingRate.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount).times(b.ExchangeRateVND || 1) || 0), this.Decimal(0))),
    TotalObh: (obh.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount).times(b.ExchangeRateVND || 1) || 0), this.Decimal(0))),
}
vnd.Profit = vnd.TotalRevenue.sub(vnd.TotalCost);
vnd.ProfitRate = (vnd.Profit.div(vnd.TotalRevenue || this.Decimal(1))).times(this.Decimal(100));
var usd = {
    CurrencyText: "USD",
    TotalRevenue: (sellingRate.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount).times(b.ExchangeRateUSD || 1) || 0), this.Decimal(0))),
    TotalCost: (buyingRate.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount).times(b.ExchangeRateUSD || 1) || 0), this.Decimal(0))),
    TotalObh: (obh.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount).times(b.ExchangeRateUSD || 1) || 0), this.Decimal(0))),
}
usd.Profit = usd.TotalRevenue.sub(usd.TotalCost);
usd.ProfitRate = (usd.Profit.div(usd.TotalRevenue || this.Decimal(1))).times(this.Decimal(100));
return [vnd, usd];

