return {
    CopyFrom: function () {
        if (this.Entity.Code) {
            var from = JSON.parse(JSON.stringify(this.Entity.Code));
            var code = from;
            code.Id = this.Entity.Id;
            code.ComponentGroupId = this.Entity.ComponentGroupId;
            code.FeatureId = this.Entity.FeatureId;
            code.EntityId = this.Entity.EntityId;
            code.ParentId = this.Entity.ParentId;
            this.EditForm.Entity = code;
            this.EditForm.UpdateView(true);
        }
    }
}

