return {
    GetInfo: async function (component, entity) {
        if (this.Entity.TaxCode == null || this.Entity.TaxCode == "") {
            this.Toast.Warning("Not find taxcode");
            return;
        }
        this.Toast.Warning("Waiting search infomation by taxcode");
        const response = await fetch('https://api.vietqr.io/v2/business/' + this.Entity.TaxCode);
        const data = await response.json();
        if (data.data == null) {
            this.Toast.Warning("Not find taxcode");
            return;
        }
        this.Entity.Name = data.data.shortName || data.data.internationalName;
        this.Entity.CompanyName = data.data.internationalName;
        this.Entity.CompanyNameInv = data.data.name;
        this.Entity.Address = data.data.address;
        this.Entity.AddressInv = data.data.address;
        this.UpdateView(false, false, "Name", "CompanyName", "CompanyNameInv", "Address", "AddressInv");
        this.Toast.Success("Update taxcode success");
    },
    ToastInfo: function (component) {
        this.Toast.Success("Input search tax code")
    },
    SendMail: function () {
        if (!this.Entity.Email || this.Entity.Email == "") {
            this.Toast.Warning("Email is requied");
            return;
        }
        var entity = {
            To: this.Entity.Email,
            Text: "",
            Subject: ""
        }
        this.OpenPopup("sendmail-editor", entity);
    },
    OnSave: function (form, gridview) {
        var partnerContact = this.EditForm.FirstCom("PartnerContact");
        if ((partnerContact && partnerContact.Item.length > 0)) {
            var mainRow = partnerContact.Item.find(item => item.Entity.IsMain) || partnerContact.Item[0];
            if (mainRow) {
                this.Entity.ContactName = mainRow.Entity.ContactName;
                this.Entity.ContactPhoneNumber = mainRow.Entity.ContactPhoneNumber;
                this.Entity.ContactEmail = mainRow.Entity.ContactEmail;
            }
        }
        var partnerBankAccount = this.EditForm.FirstCom("PartnerBankAccount");
        if ((partnerBankAccount && partnerBankAccount.Item.length > 0)) {
            var mainRow = partnerBankAccount.Item.find(item => item.Entity.IsMain) || partnerBankAccount.Item[0];
            if (mainRow) {
                this.Entity.AccountName = mainRow.Entity.AccountName;
                this.Entity.AccountNumber = mainRow.Entity.AccountNumber;
                this.Entity.BankId = mainRow.Entity.BankId;
                this.Entity.AccountBranch = mainRow.Entity.AccountBranch;
                this.Entity.SwiftCode = mainRow.Entity.SwiftCode;
            }
            mainRow = partnerBankAccount.Item.find(item => item.Entity.IsReceives) || partnerBankAccount.Item[0];
            if (mainRow) {
                this.Entity.ReceivesAccountName = mainRow.Entity.AccountName;
                this.Entity.ReceivesAccountNumber = mainRow.Entity.AccountNumber;
                this.Entity.ReceivesBankId = mainRow.Entity.BankId;
                this.Entity.ReceivesAccountBranch = mainRow.Entity.AccountBranch;
                this.Entity.ReceivesSwiftCode = mainRow.Entity.SwiftCode;
            }
        }
    },
    SaveAs: async function (btn) {
        var newEntity = JSON.parse(JSON.stringify(this.Entity));
        this.Entity = newEntity;
        this.Entity.Id = this.Uuid7.NewGuid();
        this.Entity.Code = null;
        this.Entity.SaleId = this.Token.UserId;
        this.Entity.ActionId = 1;
        this.UpdateView(true);
        this.Dirty = true;
        this.Toast.Success("Save as success");
    },
    OnSaveAdd: function (form, gridview) {
        this.Entity = {
            Id: this.Uuid7.NewGuid(),
            Code: null,
            ServiceId: 1,
            TypeId: 3,
            ActionId: 1,
            SaleId: this.Token.UserId
        }
    },
    CreateUser: async function (data, gridview) {
        if (this.EditForm.Dirty) {
            await this.SavePatch();
        }
        if (!this.Entity.Email) {
            this.Toast.Warning("Please input email!");
            return;
        }
        var form = this.EditForm.OpenConfig("Do do want create user send to email: " + this.Entity.Email, async () => {
            var rs = await this.EditForm.Client.PostAsync(this.Entity, "/api/CreateUser");
            if (rs) {
                this.Toast.Success("Create user success please check email!");
            }
        }, () => { }, false, []);
    }
}

