return {
    Add: function () {
        var config = JSON.parse(localStorage.getItem("SalesFunction"));
        var cus = {
            IsPublic: config["DEFAULT_CREATE_OBJECTS_PUBLIC"] ? false : true,
            ServiceId: 1,
            TypeId: 3,
            ActionId: config["DEFAULT_CREATE_OBJECTS_APPROVE"] ? 1 : 3,
            SaleId: this.Token.UserId
        }
        this.OpenPopup("customer-editor", cus);
    },
    Edit: function (btn, entity) {
        this.OpenPopup("customer-editor", entity);
    }
}

