return {
    OnSave: function (form, gridview) {
        var detailFee = gridview.find(x => x.IsListView && x.Meta.FieldName == "DefaultFee");
        if (detailFee && detailFee.AllListViewItem.length > 0) {
            detailFee.AllListViewItem.filter(x => !x.GroupRow).forEach((item, index) => {
                item.Entity.Order = index;
            });
        }
        else {
            this.Entity.PricingId = null;
        }
    },
    Declined: function () {
        var detail = this.OpenFrom.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
        this.OpenFrom.Entity.StatusId = (detail.AllListViewItem.flatMap(x => x.Entity).reduce((min, current) => {
            return current.StatusId < min.StatusId ? current : min;
        }, detail.AllListViewItem.flatMap(x => x.Entity)[0])).StatusId;
        this.OpenFrom.Dirty = true;
        this.OpenFrom.SavePatch();
    },
    SetDefault: function () {
        var pricing = this.GET("Pricing");
        pricing.ClearSelected();
        if (pricing.AllListViewItem.length > 0) {
            pricing.AllListViewItem[0].Selected = true;
        }
    },
    CalcPrice: function (com, entity) {
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.Amount = this.Decimal(entity.Amount || 0);
        entity.TotalAmount = (entity.Quantity || this.Decimal(0)).times(entity.Amount || this.Decimal(0));
        entity.Tax = (entity.TotalAmount).times(entity.Vat || this.Decimal(0)).div(100);
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.Tax);
        com.Parent.UpdateView(false);
        this.CalcTotalAmount();
    },
    CalcTotalAmount: function () {
        var feeGrid = this.GET('DefaultFee');
        if (feeGrid.AllListViewItem.length == 0) {
            this.Entity.PricingId = null;
            this.Entity.TotalAmount = this.Decimal(0);
            this.UpdateView(false, false, "TotalAmount");
            var pricingGrid = this.GET('Pricing');
            pricingGrid.ReloadData();
            return;
        }
        var totalAmount = feeGrid.AllListViewItem.map(x => x.Entity).reduce((a, b) => a.plus(b.TotalAmountTax || this.Decimal(0)), this.Decimal(0));
        this.Entity.TotalAmount = totalAmount || this.Decimal(0);
        this.UpdateView(false, false, "TotalAmount");
    },
    CheckCurrency: function (com, entity) {
        com.Parent.UpdateView(false);
    },
    LoadPricing: async function (com, entity) {
        var gridView = this.GET("DefaultFee");
        var gridViewPricing = this.GET("Pricing");
        if (gridViewPricing.AllListViewItem.length == 0) {
            return;
        }
        if (gridView.AllListViewItem.length > 0) {
            this.EditForm.Entity.StatusId = 3;
            this.EditForm.Entity.ProgressId = 3;
            this.EditForm.ActApprovedEntity().then(() => {
                this.EditForm.OpenFrom.Dirty = true;
                this.EditForm.OpenFrom.SavePatch();
            });
            return;
        }
        var selecteds = await com.RunQuery();
        var newFees = JSON.parse(JSON.stringify(selecteds[0]));
        var containerGrid = this.OpenFrom.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityContainer");
        var containers = containerGrid.AllListViewItem.map(x => x.Entity).filter(x => x.ContainerTypeId) || [];
        if (containers.length > 0 && (this.Entity.Service.Name.toUpperCase().includes('FCL') || this.Entity.Service.Name.toUpperCase().includes('LOGISTICS'))) {
            var containerTypeIds = containers.map(x => x.ContainerTypeId);
            newFees = newFees.filter(x => containerTypeIds.some(y => y == x.UnitId) || x.UnitId == '00111748-0000-0000-8000-4b773588d590');
        }
        var items = newFees.map(item => {
            var it = {
                Id: this.Uuid7.NewGuid(),
                DescriptionId: item.DescriptionId,
                InquiryDetailId: this.Entity.Id,
                Quantity: this.Decimal(1),
                GroupFee: item.GroupFee,
                IsGrossWeight: item.IsGrossWeight,
                VendorId: item.ActVendorId,
                UnitId: item.UnitId,
                OtherUnitId: item.OtherUnitId,
                Note: item.Note,
                IsFreight: item.IsFreight,
                IsObh: item.IsObh,
                CurrencyId: item.CurrencyId,
                Amount: this.Decimal(item.UnitPrice || 0),
                MinUnitPrice: this.Decimal(item.MinUnitPrice || 0),
                Vat: item.Vat ? this.Decimal(item.Vat) : null,
            };
            it.Quantity = this.Decimal(it.Quantity || 0);
            it.Amount = this.Decimal(it.Amount || 0);
            it.TotalAmount = (it.Quantity || this.Decimal(0)).times(it.Amount || this.Decimal(0));
            it.Tax = (it.TotalAmount).times(it.Vat || this.Decimal(0)).div(100);
            it.TotalAmountTax = it.TotalAmount.plus(it.Tax);
            return it;
        });
        items = items.sort((a, b) => a.UnitId - b.UnitId);
        gridView.AddRows(items).then(() => {
            this.CalcTotalAmount();
            this.EditForm.Entity.ProgressId = 3;
            this.EditForm.Entity.PricingId = selecteds[0][0].PricingId;
            this.EditForm.Entity.StatusId = 3;
            this.EditForm.Dirty = true;
            this.EditForm.SavePatch().then(() => {
                this.EditForm.ActApprovedEntity().then(() => {
                    this.EditForm.OpenFrom.Dirty = true;
                    this.EditForm.OpenFrom.SavePatch();
                });
            });
        });
    },
    UnLoadPricing: async function (com, entity) {
        var gridView = this.GET("DefaultFee");
        gridView.AllListViewItem.forEach(item => {
            item.Selected = true;
        });
        var deletedItems = gridView.GetSelectedRows();
        gridView.HardDeleteConfirmed(deletedItems).then(deletedIds => {
            this.EditForm.Entity.TotalAmount = this.Decimal(0);
            this.EditForm.Entity.StatusId = 2;
            this.EditForm.Entity.PricingId = null;
            this.EditForm.Entity.ProgressId = 1;
            this.EditForm.Dirty = true;
            this.EditForm.SavePatch().then(() => {
                this.EditForm.OpenFrom.Dirty = true;
                this.EditForm.OpenFrom.SavePatch();
            });
        });
    },
    BeforeDecline: async function (com, entity) {
        this.EditForm.Entity.ProgressId = 3;
    },
    CreatePricing: async function (com, entity) {
        var pricing = {
            Id: this.Uuid7.NewGuid(),
            FromDate: this.dayjs().format('YYYY-MM-DD'),
            InsertedBy: this.Token.UserId,
            StatusId: 1,
            TypeId: 1,
            PolId: this.Entity.PolId,
            PodId: this.Entity.PodId,
            CustomerId: this.EditForm.OpenFrom.Entity.CustomerId,
            CommodityId: this.EditForm.OpenFrom.Entity.CommodityId,
        };
        var name = "sea-pricing-editor";
        if (this.Entity.Service.Name.toUpperCase().includes('AIR')) {
            pricing.TypeId = 2;
            name = "air-pricing-editor";
        }
        else if (this.Entity.Service.Name.toUpperCase().includes('TRUCKING')) {
            pricing.TypeId = 3;
            pricing.ServiceId = this.Entity.ServiceId;
            name = "trucking-pricing-editor";
        }
        else if (this.Entity.Service.Name.toUpperCase().includes('LOGISTICS')) {
            pricing.TypeId = 4;
            name = "logistics-pricing-editor";
        }
        this.OpenPopup(name, pricing);
    },
    CreateQuote: async function (com, entity) {
        var gridView = this.GET("DefaultFee");
        if (gridView.AllListViewItem.length == 0) {
            this.Toast.Warning("Please add pricing fee details!");
            return;
        }
        var pricingGrid = this.GET("Pricing");
        var pricing = pricingGrid.AllListViewItem.find(x => x.Entity.Id == this.Entity.PricingId);
        var selecteds = gridView.AllListViewItem.filter(x => !x.GroupRow).map(x => x.Entity);
        var newFees = JSON.parse(JSON.stringify(selecteds));
        var typeId = 2;
        var name = "sea-quote-editor";
        if (this.Entity.Service.Name.toUpperCase().includes('AIR')) {
            typeId = 3;
            name = "air-quote-editor";
        }
        else if (this.Entity.Service.Name.toUpperCase().includes('SEA')) {
            typeId = 2;
            name = "sea-quote-editor";
        }
        else if (this.Entity.Service.Name.toUpperCase().includes('TRUCKING')) {
            typeId = 4;
            name = "trucking-quote-editor";
        }
        else {
            typeId = 5;
            name = "logistics-quote-editor";
        }
        var quote = {
            Id: this.Uuid7.NewGuid(),
            VoucherTypeId: 2,
            PricingId: this.Entity.PricingId,
            InquiryDetailId: this.Entity.Id,
            StatusId: this.SalesFunction["APPROVE_QUOTATION"] ? 3 : 1,
            NoApproved: this.SalesFunction["APPROVE_QUOTATION"] ? true : false,
            ShipmentTypeId: 1,
            TypeId: typeId,
            FromDate: pricing ? pricing.Entity.FromDate : null,
            ToDate: pricing ? pricing.Entity.ToDate : null,
            ServiceDate: this.dayjs().format('YYYY-MM-DD'),
            AgentId: (pricing && pricing.Vendor) ? (pricing.Vendor.PartnerTypeIdsText && pricing.Vendor.PartnerTypeIdsText.includes('Agent')
                && pricing.Vendor.ResidenceTypeIdText && pricing.Vendor.ResidenceTypeIdText.toUpperCase().includes('OVERSEA') ? pricing.VendorId : null) : null,
            CustomerId: this.EditForm.OpenFrom.Entity.CustomerId,
            CommodityId: this.EditForm.OpenFrom.Entity.CommodityId,
            IncotermId: this.EditForm.OpenFrom.Entity.IncotermId,
            EtdDate: this.EditForm.OpenFrom.Entity.EtdDate,
            EtaDate: this.EditForm.OpenFrom.Entity.EtaDate,
            DeliveryTerm: this.EditForm.OpenFrom.Entity.DeliveryTerm,
            Pickup: this.EditForm.OpenFrom.Entity.Pickup,
            Delivery: this.EditForm.OpenFrom.Entity.Delivery,
            Quantity: this.EditForm.OpenFrom.Entity.Quantity,
            Note: this.Entity.Note,
            UnitId: this.EditForm.OpenFrom.Entity.UnitId,
            DeminsionText: this.EditForm.OpenFrom.Entity.DeminsionText,
            CW: this.EditForm.OpenFrom.Entity.CW,
            GW: this.EditForm.OpenFrom.Entity.GW,
            CBM: this.EditForm.OpenFrom.Entity.CBM,
            Note: this.EditForm.OpenFrom.Entity.Note,
            ServiceId: this.Entity.ServiceId,
            PolId: this.Entity.PolId,
            PodId: this.Entity.PodId,
            IsSimple: false,
            DemFree: pricing.Entity.DemFree,
            DetFree: pricing.Entity.DetFree,
            PkEmptyId: pricing.Entity.PkEmptyId,
            ReturnsId: pricing.Entity.ReturnsId
        };
        var config = JSON.parse(localStorage.getItem("SalesFunction"));
        var containerGrid = this.OpenFrom.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "EntityContainer");
        var containers = JSON.parse(JSON.stringify(containerGrid.AllListViewItem.map(x => x.Entity).filter(x => x.ContainerTypeId) || []));
        newFees.forEach(item => {
            var actCont = containers.find(x => x.ContainerTypeId == item.UnitId);
            item.Id = this.Uuid7.NewGuid();
            item.DescriptionId = item.DescriptionId;
            item.InquiryId = quote.Id;
            item.RouteId = `${(this.Entity.Pol.Code || '')} => ${(this.Entity.Pod.Code || '')}`;
            item.Route = {
                Name: item.RouteId,
                Id: item.RouteId,
            };
            item.Cost = item.Amount;
            item.MinLCLCost = item.MinUnitPrice;
            item.MinQuantityCost = item.MinUnitPrice;
            if (item.IsObh) {
                item.MinLCLRate = item.MinLCLCost;
                item.Rate = item.Cost;
                item.MinQuantityRate = item.MinQuantityCost;
            }
            item.PolId = this.Entity.PolId;
            item.PodId = this.Entity.PodId;
            item.FinalDestinationId = this.Entity.PodId;
            item.PlaceDeliveryId = this.Entity.PodId;
            item.PlaceReceiptId = this.Entity.PolId;
            item.PayeeId = item.VendorId != pricing.Entity.VendorId ? item.VendorId : null;
            item.DisableRow = true;
            item.IsGrossWeight = item.IsGrossWeight;
            item.OtherFee = true;
            if (this.OpenFrom.Entity.UnitId == item.OtherUnitId) {
                item.Quantity = this.OpenFrom.Entity.Quantity;
            }
            else {
                if (actCont && item.IsContainer) {
                    item.Quantity = actCont.Quantity;
                }
            }
            if (item.IsCBM) {
                item.Quantity = this.OpenFrom.Entity.CBM;
            }
            if (item.IsKGS) {
                item.Quantity = this.OpenFrom.Entity.CW;
            }
            if (item.IsGrossWeight) {
                item.Quantity = this.OpenFrom.Entity.GW;
            }
        });
        containers.forEach(x => {
            x.Id = this.Uuid7.NewGuid();
            x.Quantity = x.Quantity;
            x.ContainerTypeId = x.ContainerTypeId;
            x.InquiryId = quote.Id;
        });
        quote.EntityContainer = containers || [];
        quote.InquiryDetail = [{
            Id: this.Uuid7.NewGuid(),
            InquiryId: quote.Id,
            OrtherFee: false,
            PolId: this.Entity.PolId,
            PodId: this.Entity.PodId,
            FinalDestinationId: this.Entity.PodId,
            PlaceDeliveryId: this.Entity.PodId,
            PlaceDeliveryId: this.Entity.PodId,
            ScheduleIds: pricing ? pricing.Entity.ScheduleIds : null,
            VendorId: pricing ? pricing.Entity.VendorId : null,
            TransitTime: pricing ? pricing.Entity.TransitTime : null,
            ViaId: pricing ? pricing.Entity.ViaId : null,
            CarrierId: pricing ? pricing.Entity.CarrierId : null,
            PlaceReceiptId: this.Entity.PolId,
            DisableRow: true,
        }];
        quote.DefaultFee = newFees;
        this.OpenPopup(name, quote);
    },
    Edit: function (btn, entity) {
        if (entity.TypeId == 1) {
            this.OpenPopup("sea-pricing-editor", entity);
        }
        else if (entity.TypeId == 2) {
            this.OpenPopup("air-pricing-editor", entity);

        }
        else if (entity.TypeId == 3) {
            this.OpenPopup("trucking-pricing-editor", entity);
        }
        else if (entity.TypeId == 4) {
            this.OpenPopup("logistics-pricing-editor", entity);
        }
    },
    Discussion: function () {
        this.Entity.TableName = 'InquiryDetail';
        this.Entity.Icon = 'fal fa-quote-left';
        this.OpenPopup("chat-editor", this.Entity);
    }
}

