return {
    OnSave: function (form, gridview) {
        if (this.Entity.ActionId == 1) {
            this.Entity.ActionId = 2;
        }
    },
    CalcPrice: function (com, entity) {
        var exUSD = this.Decimal(this.EditForm.Entity.ExchangeRateINV);
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.Amount = this.Decimal(entity.Amount || 0);
        entity.TotalAmount = (entity.Quantity || this.Decimal(0)).times(entity.Amount || this.Decimal(0));
        var vat = this.Decimal(entity.Vat || 0);
        if (vat.isNegative()) {
            entity.AmountTax = this.Decimal(entity.Vat).abs();
        } else {
            entity.AmountTax = (entity.TotalAmount).times(entity.Vat || this.Decimal(0)).div(100);
        }
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.AmountTax);
        if (entity.CurrencyCode == "USD") {
            entity.ExchangeRateINV = exUSD;
        }
        else {
            entity.ExchangeRateINV = this.Decimal(entity.ExchangeRateINV);
        }
        entity.ExchangeRateINV2 = entity.ExchangeRateINV.div(exUSD);
        if (this.EditForm.Entity.CurrencyCode != "VND") {
            entity.ExchangeRateINV = this.Decimal(1);
            entity.ExAmount = this.Decimal(entity.Amount).times(entity.ExchangeRateINV)
            entity.ExTotalAmount = this.Decimal(entity.ExAmount).times(entity.Quantity)
            if (vat.isNegative()) {
                entity.ExAmountTax = this.Decimal(entity.Vat).abs().times(entity.ExchangeRateINV)
            } else {
                entity.ExAmountTax = this.Decimal(entity.ExTotalAmount).times(vat.div(this.Decimal(100)))
            }
            entity.ExTotalAmountTax = this.Decimal(entity.ExTotalAmount).plus(entity.ExAmountTax);
            if (entity.TypeId === 2 || ([3, 4].includes(entity.TypeId) && !entity.IsAdvCustomer)) {
                entity.Payable = entity.ExTotalAmountTax;
                entity.Receivable = null;
            }
            else {
                entity.Receivable = entity.ExTotalAmountTax;
                entity.Payable = null;
            }
        }
        else {
            entity.ExAmount = (this.Decimal(entity.Amount).times(entity.ExchangeRateINV)).toDP(0);
            entity.ExTotalAmount = (this.Decimal(entity.ExAmount).times(entity.Quantity)).toDP(0);
            if (vat.isNegative()) {
                entity.ExAmountTax = (this.Decimal(entity.Vat).abs().times(entity.ExchangeRateINV)).toDP(0);
            } else {
                entity.ExAmountTax = (this.Decimal(entity.ExTotalAmount).times(vat.div(this.Decimal(100)))).toDP(0);
            }
            entity.ExTotalAmountTax = this.Decimal(entity.ExTotalAmount).plus(entity.ExAmountTax);
            if (entity.TypeId === 2 || ([3, 4].includes(entity.TypeId) && !entity.IsAdvCustomer)) {
                entity.Payable = entity.ExTotalAmountTax;
                entity.Receivable = null;
            }
            else {
                entity.Receivable = entity.ExTotalAmountTax;
                entity.Payable = null;
            }
        }
        com.Parent.UpdateView(false);
    },
    ChangeExchangeRate2: function (com, entity) {
        entity.ExchangeRateINV = this.Decimal(entity.ExchangeRateINV);
        if (entity.CurrencyCode == "USD") {
            this.EditForm.Entity.ExchangeRateINV = entity.ExchangeRateINV;
            this.EditForm.Entity.ExchangeRateINV2 = this.Decimal(1).div(this.EditForm.Entity.ExchangeRateINV);
            this.EditForm.UpdateView(true, false, "ExchangeRateINV");
        }
        var component = this.GET("ShipmentInvoiceDetail");
        var exUSD = this.Decimal(this.Entity.ExchangeRateINV);
        if (this.EditForm.Entity.CurrencyCode != "VND") {
            component.Item.filter(x => x.Entity.CurrencyId == entity.CurrencyId || x.Entity.CurrencyCode == "USD").forEach(item => {
                if (!item.Entity.IsLock) {
                    item.Selected = true;
                    item.Entity.ExchangeRateINV = this.Decimal(1);
                    item.Entity.ExchangeRateINV2 = item.Entity.ExchangeRateINV.div(exUSD);
                    var vat = this.Decimal(item.Entity.Vat || 0);
                    item.Entity.ExAmount = this.Decimal(item.Entity.Amount).times(item.Entity.ExchangeRateINV)
                    item.Entity.ExTotalAmount = this.Decimal(item.Entity.ExAmount).times(item.Entity.Quantity)
                    if (vat.isNegative()) {
                        item.Entity.ExAmountTax = this.Decimal(item.Entity.Vat).abs().times(item.Entity.ExchangeRateINV)
                    } else {
                        item.Entity.ExAmountTax = this.Decimal(item.Entity.ExTotalAmount).times(vat.div(this.Decimal(100)))
                    }
                    item.Entity.ExTotalAmountTax = this.Decimal(item.Entity.ExTotalAmount).plus(item.Entity.ExAmountTax);
                    if (item.Entity.TypeId === 2 || ([3, 4].includes(item.Entity.TypeId) && !item.Entity.IsAdvCustomer)) {
                        item.Entity.Payable = item.Entity.ExTotalAmountTax;
                        item.Entity.Receivable = null;
                    }
                    else {
                        item.Entity.Receivable = item.Entity.ExTotalAmountTax;
                        item.Entity.Payable = null;
                    }
                    item.UpdateView(false);
                }
                else {
                    item.Selected = false;
                }
            });
        }
        else {
            component.Item.filter(x => x.Entity.CurrencyId == entity.CurrencyId || x.Entity.CurrencyCode == "USD").forEach(item => {
                if (!item.Entity.IsLock) {
                    item.Selected = true;
                    if (item.Entity.CurrencyCode == "USD") {
                        item.Entity.ExchangeRateINV = exUSD;
                    }
                    else {
                        item.Entity.ExchangeRateINV = entity.ExchangeRateINV;
                    }
                    item.Entity.ExchangeRateINV2 = item.Entity.ExchangeRateINV.div(exUSD);
                    var vat = this.Decimal(item.Entity.Vat || 0);
                    item.Entity.ExAmount = (this.Decimal(item.Entity.Amount).times(item.Entity.ExchangeRateINV)).toDP(0);
                    item.Entity.ExTotalAmount = (this.Decimal(item.Entity.ExAmount).times(item.Entity.Quantity)).toDP(0);
                    if (vat.isNegative()) {
                        item.Entity.ExAmountTax = (this.Decimal(item.Entity.Vat).abs().times(item.Entity.ExchangeRateINV)).toDP(0);
                    } else {
                        item.Entity.ExAmountTax = (this.Decimal(item.Entity.ExTotalAmount).times(vat.div(this.Decimal(100)))).toDP(0);
                    }
                    item.Entity.ExTotalAmountTax = this.Decimal(item.Entity.ExTotalAmount).plus(item.Entity.ExAmountTax);
                    if (item.Entity.TypeId === 2 || ([3, 4].includes(item.Entity.TypeId) && !item.Entity.IsAdvCustomer)) {
                        item.Entity.Payable = item.Entity.ExTotalAmountTax;
                        item.Entity.Receivable = null;
                    }
                    else {
                        item.Entity.Receivable = item.Entity.ExTotalAmountTax;
                        item.Entity.Payable = null;
                    }
                    item.UpdateView(false);
                }
                else {
                    item.Selected = false;
                }
            });
        }
    },
    UpdateParent: function () {
        var component = this.GET("ShipmentInvoice");
        component.ReloadData();
    },
    ChangeFileNo: function (com, entity) {
        this.Entity.MblNos = com.MatchedItems.map(x => x.MblNo).filter(x => x).join(',')
        this.EditForm.UpdateView(false, false, "MblNos");
    },
    Invoice: function (com, entity) {
        if (this.Entity.Id.startsWith("-")) {
            var grid = this.GET("ShipmentInvoiceDetail");
            var data = grid.AllListViewItem.filter(x => x.Selected && !x.Entity.IsLock);
            if (!data || data.length == 0) {
                this.Toast.Warning("Please selected fee issued");
            }
            else {
                this.EditForm.SaveSelectedDetail().then(() => {
                    var component = this.GET("ShipmentInvoice");
                    component.ReloadData().then();
                    if (this.EditForm.Entity.ReceiverIds) {

                    }
                });
            }
        }
        else {
            if (this.Entity.ActionId == 3) {
                this.EditForm.OpenConfig("Are you sure you want to void this invoice?", () => {
                    this.SavePatch();
                }, () => { }, false, [], true);
            }
            else {
                this.EditForm.SavePatch();
            }
        }
    },
    AddInvoice: function (com, entity) {
        var hblGrid = this.GET("ShipmentDetail");
        var issueInvoice = JSON.parse(JSON.stringify(this.Entity));
        issueInvoice.Id = this.Uuid7.NewGuid();
        issueInvoice.StatusId = 1;
        issueInvoice.ExchangeRateINV = issueInvoice.CurrencyCode ? this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))["USD"]) : this.Decimal(1);
        issueInvoice.Code = null;
        issueInvoice.FeeTypeId = "ds.TypeId in (1,2,3,4)";
        issueInvoice.ActionId = 1;
        issueInvoice.UserApprovedIds = null;
        issueInvoice.ShipmentInvoiceId = null;
        issueInvoice.HblIds = this.EditForm.HblIds || issueInvoice.HblIds;
        issueInvoice.FileIds = this.EditForm.FileIds || issueInvoice.FileIds;
        issueInvoice.RequestTypeId = null;
        issueInvoice.DeadlineDate = null;
        issueInvoice.ReceiverIds = null;
        issueInvoice.Remark = null;
        issueInvoice.SeqKey = null;
        issueInvoice.RevisedDate = null;
        issueInvoice.DueDate = null;
        issueInvoice.IsSendPartner = false;
        issueInvoice.PostOtherDate = null;
        issueInvoice.OtherCode = null;
        issueInvoice.PaymentMethodId = null;
        issueInvoice.ProgressId = 1;
        issueInvoice.UserViewIds = null;
        issueInvoice.InsertedBy = this.UserId;
        issueInvoice.PostDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        issueInvoice.InvoiceDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        this.EditForm.Entity = issueInvoice;
        this.EditForm.UpdateView(true, false);
        this.ChangeDataFee();
        var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
        comShipmentInvoice.ReloadData().then(() => {
            if (this.EditForm.Entity.Id) {
                var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                    item.Element.click();
                }
            }
            else {
                if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                    comShipmentInvoice.Item[0].Element.click();
                }
            }
        })
    },
    IntUpdateFee: 0,
    ChangeDataFee: function () {
        var exUSD = this.Decimal(this.Entity.ExchangeRateINV);
        var exVND = this.Decimal(1);
        window.clearTimeout(this.EditForm.IntUpdateFee);
        this.EditForm.IntUpdateFee = window.setTimeout(() => {
            var component = this.GET("ShipmentInvoiceDetail");
            component.ReloadData().then(() => {
                if (this.EditForm.Entity.CurrencyCode != "VND") {
                    component.Item.forEach(item => {
                        if (!item.Entity.IsLock) {
                            component.Dirty = true;
                            item.Selected = true;
                            item.Entity.ExchangeRateINV = this.Decimal(1);
                            item.Entity.ExchangeRateINV2 = item.Entity.ExchangeRateINV.div(exUSD);
                            var vat = this.Decimal(item.Entity.Vat || 0);
                            item.Entity.ExAmount = this.Decimal(item.Entity.Amount).times(item.Entity.ExchangeRateINV)
                            item.Entity.ExTotalAmount = this.Decimal(item.Entity.ExAmount).times(item.Entity.Quantity)
                            if (vat.isNegative()) {
                                item.Entity.ExAmountTax = this.Decimal(item.Entity.Vat).abs().times(item.Entity.ExchangeRateINV)
                            } else {
                                item.Entity.ExAmountTax = this.Decimal(item.Entity.ExTotalAmount).times(vat.div(this.Decimal(100)))
                            }
                            item.Entity.ExTotalAmountTax = this.Decimal(item.Entity.ExTotalAmount).plus(item.Entity.ExAmountTax);
                            if (item.Entity.TypeId === 2 || ([3, 4].includes(item.Entity.TypeId) && !item.Entity.IsAdvCustomer)) {
                                item.Entity.Payable = item.Entity.ExTotalAmountTax;
                                item.Entity.Receivable = null;
                            }
                            else {
                                item.Entity.Receivable = item.Entity.ExTotalAmountTax;
                                item.Entity.Payable = null;
                            }
                            item.UpdateView(false);
                        }
                        else {
                            item.Selected = false;
                        }
                    });
                }
                else {
                    component.Item.forEach(item => {
                        if (!item.Entity.IsLock) {
                            component.Dirty = true;
                            item.Selected = true;
                            if (item.Entity.CurrencyCode == "USD") {
                                item.Entity.ExchangeRateINV = exUSD;
                            }
                            else {
                                item.Entity.ExchangeRateINV = this.Decimal(item.Entity.ExchangeRateINV || this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))[item.Entity.CurrencyCode]));
                            }
                            item.Entity.ExchangeRateINV2 = item.Entity.ExchangeRateINV.div(exUSD);
                            var vat = this.Decimal(item.Entity.Vat || 0);
                            item.Entity.ExAmount = (this.Decimal(item.Entity.Amount).times(item.Entity.ExchangeRateINV)).toDP(0);
                            item.Entity.ExTotalAmount = (this.Decimal(item.Entity.ExAmount).times(item.Entity.Quantity)).toDP(0);
                            if (vat.isNegative()) {
                                item.Entity.ExAmountTax = (this.Decimal(item.Entity.Vat).abs().times(item.Entity.ExchangeRateINV)).toDP(0);
                            } else {
                                item.Entity.ExAmountTax = (this.Decimal(item.Entity.ExTotalAmount).times(vat.div(this.Decimal(100)))).toDP(0);
                            }
                            item.Entity.ExTotalAmountTax = this.Decimal(item.Entity.ExTotalAmount).plus(item.Entity.ExAmountTax);
                            if (item.Entity.TypeId === 2 || ([3, 4].includes(item.Entity.TypeId) && !item.Entity.IsAdvCustomer)) {
                                item.Entity.Payable = item.Entity.ExTotalAmountTax;
                                item.Entity.Receivable = null;
                            }
                            else {
                                item.Entity.Receivable = item.Entity.ExTotalAmountTax;
                                item.Entity.Payable = null;
                            }
                            item.UpdateView(false);
                        }
                        else {
                            item.Selected = false;
                        }
                    });
                }
            });
        });
    },
    ViewDetail: function (grid, entity) {
        this.Entity = entity;
        this.EditForm.UpdateView(false, false);
    },
    AddContextMenu: function (gridview, context) {
        if (this.Entity.ActionId == 2 && this.Entity.ProgressId == 1) {
            var data = gridview.AllListViewItem.filter(x => x.Selected && x.Entity.IsLock);
            if (data && data.length > 0) {
                context.MenuItems.push({
                    Icon: "fas fa-page-break",
                    Text: "Split Fee",
                    Click: () => this.SplitFee(data.map(x => x.Entity.Id), gridview)
                });
            }
        }
        if (this.Entity.Id.startsWith('-') && this.Entity.ProgressId == 1) {
            var data2 = gridview.AllListViewItem.filter(x => x.Selected && !x.Entity.IsLock);
            if (data2 && data2.length > 0) {
                context.MenuItems.push({
                    Icon: "fas fa-plus",
                    Text: "Add Fee",
                    Click: () => this.AddFee(data2, gridview)
                });
            }
        }
    },
    SplitFee: function (data, gridview) {
        var form = gridview.EditForm.OpenConfig("Do do want split fee!", async () => {
            var rs = await gridview.EditForm.Client.PostAsync({ ShipmentInvoiceDetailId: data, ShipmentInvoiceId: this.EditForm.Entity.Id }, "/api/SplitFee");
            if (rs) {
                gridview.ApplyFilter();
                var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
                comShipmentInvoice.ReloadData().then(() => {
                    if (this.EditForm.Entity.Id) {
                        var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                        if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                            item.Element.click();
                        }
                    }
                    else {
                        if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                            comShipmentInvoice.Item[0].Element.click();
                        }
                    }
                });
            }
        }, () => { }, false, []);
    },
    AddFee: function (data, gridview) {
        var shipment = gridview.EditForm.Meta.GridPolicies.find(x => x.FieldName == "ViewShipmentInvoiceId");
        var form = gridview.EditForm.OpenConfig("Do do want add fee!", async () => {
            for (const element of data) {
                let patchVM = {
                    Table: "ShipmentFee",
                    Changes: [
                        { Field: "Id", Value: element.Entity.ShipmentFeeId },
                        { Field: "DescriptionId", Value: element.Entity.DescriptionId },
                        { Field: "TotalAmountTax", Value: element.Entity.TotalAmountTax },
                        { Field: "TotalAmount", Value: element.Entity.TotalAmount },
                        { Field: "AmountTax", Value: element.Entity.AmountTax },
                        { Field: "Amount", Value: element.Entity.Amount },
                        { Field: "Quantity", Value: element.Entity.Quantity },
                        { Field: "UnitId", Value: element.Entity.UnitId },
                        { Field: "Vat", Value: element.Entity.Vat },
                        { Field: "Tax", Value: element.Entity.Tax },
                        { Field: "ExchangeRateINV", Value: element.Entity.ExchangeRateINV },
                        { Field: "ExchangeRateVND", Value: element.Entity.ExchangeRateINV },
                        { Field: "ExchangeRateUSD", Value: element.Entity.ExchangeRateINV2 },
                        { Field: "ExTotalAmountTax", Value: element.Entity.ExTotalAmountTax },
                        { Field: "ExTotalAmount", Value: element.Entity.ExTotalAmount },
                        { Field: "ExAmountTax", Value: element.Entity.ExAmountTax },
                        { Field: "ExAmount", Value: element.Entity.ExAmount },
                    ]
                };
                await this.Client.PatchAsync(patchVM);
            }
            var rs = await gridview.EditForm.Client.PostAsync({ ShipmentInvoiceId: gridview.Entity.ViewShipmentInvoiceId, ShipmentInvoiceDetailId: data.map(x => x.Entity.ShipmentFeeId) }, "/api/AddFee");
            if (rs) {
                gridview.ApplyFilter();
                var comShipmentInvoice = this.EditForm.FirstCom("ShipmentInvoice");
                comShipmentInvoice.ReloadData().then(() => {
                    if (this.EditForm.Entity.Id) {
                        var item = comShipmentInvoice.Item.find(x => x.Entity.Id == this.EditForm.Entity.Id);
                        if (comShipmentInvoice && comShipmentInvoice.Item && item) {
                            item.Element.click();
                        }
                    }
                    else {
                        if (comShipmentInvoice && comShipmentInvoice.Item && comShipmentInvoice.Item[0]) {
                            comShipmentInvoice.Item[0].Element.click();
                        }
                    }
                });
            }
        }, () => { }, true, [shipment]);
    },
    ChangeCurrency: function () {
        this.EditForm.Entity.ExchangeRateINV = this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))[this.EditForm.Entity.Currency.Code]);
        this.EditForm.Entity.ExchangeRateINV2 = this.Decimal(1).div(this.EditForm.Entity.ExchangeRateINV);
        this.EditForm.UpdateView(true, false, "ExchangeRateINV", "ExchangeRateINV2");
        this.ChangeDataFee();
    },
    ChangeExchangeRate: function () {
        this.EditForm.Entity.ExchangeRateINV2 = this.Decimal(1).div(this.EditForm.Entity.ExchangeRateINV);
        this.EditForm.UpdateView(true, false, "ExchangeRateINV2");
        this.ChangeDataFee();
    },
    VatInvoice: function (com) {
        if (this.EditForm.Entity.VatInvoiceId) {
            this.OpenPopup("vatinvoice-editor", { Id: this.EditForm.Entity.VatInvoiceId }, true);
            return;
        }
        var exUSD = this.Decimal(this.Entity.ExchangeRateINV);
        var entity = {
            Id: this.Uuid7.NewGuid(),
            StatusId: this.SalesFunction["APPROVE_VAT_INV"] ? 3 : 1,
            NoApproved: this.SalesFunction["APPROVE_VAT_INV"] ? true : false,
            VoucherTypeId: 12,
            IssueInvoiceId: this.Entity.Id,
            HblIds: this.EditForm.Entity.HblIds,
            FileIds: this.EditForm.Entity.FileIds,
            CustomerId: this.EditForm.Entity.VendorIds,
            InsertedBy: this.Token.UserId,
            InvoiceIds: this.EditForm.Entity.Id,
            TypeId: 3,
            IsMultiple: true,
            ObhTypeId: 1,
            ActionId: 2,
            PostDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss'),
            InvoiceDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss')
        }
        var company = this.EditForm.Entity.VendorIdsMasterData;
        if (company.AssignmentInvId) {
            company = company.AssignmentInv;
        }
        entity.DueDate = this.EditForm.Entity.DueDate;
        entity.CompanyId = company.Id;
        entity.CompanyIdText = company.CompanyNameInv;
        entity.CompanyAddress = company.AddressInv;
        entity.TaxCode = company.TaxCode;
        var component = this.GET("ShipmentInvoiceDetail");
        var vatDetail = JSON.parse(JSON.stringify(component.Item.map(x => x.Entity)));
        vatDetail.forEach(item => {
            item.Id = this.Uuid7.NewGuid();
            item.ShipmentFeeId = item.ShipmentFeeId;
            item.InsertedBy = this.Token.UserId;
            item.DescriptionIdText = item.Description ? item.Description.Name : null;
            if (this.EditForm.Entity.CurrencyCode != "VND") {
                item.ExchangeRateINV = this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))[item.CurrencyCode]);
                item.ExchangeRateINV2 = item.ExchangeRateINV.div(exUSD);
                var vat = this.Decimal(item.Vat || 0);
                item.ExAmount = (this.Decimal(item.Amount).times(item.ExchangeRateINV)).toDP(0);
                item.ExTotalAmount = (this.Decimal(item.ExAmount).times(item.Quantity)).toDP(0);
                if (vat.isNegative()) {
                    item.ExAmountTax = (this.Decimal(item.Vat).abs().times(item.ExchangeRateINV)).toDP(0);
                } else {
                    item.ExAmountTax = (this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100)))).toDP(0);
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax);
            }
            else {
                item.ExchangeRateINV = item.ExchangeRateINV;
                item.ExchangeRateINV2 = item.ExchangeRateINV2;
            }
            item.IsManual = true;
            item.IsLock = false;
            item.NoSubmit = false;
            item.IsInvoice = false;
            item.IsPayment = false;
            item.IsInvoice = false;
            item.IsPaymentAcc = false;
            item.IsDebtAcc = false;
            item.IsPaid = false;
        });
        entity.ShipmentInvoiceDetail = vatDetail;
        if (entity.ShipmentInvoiceDetail && entity.ShipmentInvoiceDetail.length > 0) {
            const calculateTotal = (field) => entity.ShipmentInvoiceDetail.reduce((acc, x) => acc.plus(this.Decimal(x[field] || 0)), this.Decimal(0));
            if (entity.IsMultiple) {
                const totalAmount = calculateTotal("ExTotalAmount").toDP(0);
                const amountTax = calculateTotal("ExAmountTax").toDP(0);
                entity.TotalAmountTax = totalAmount.plus(amountTax);
                entity.TotalAmount = totalAmount;
                entity.AmountTax = amountTax;
            }
            else {
                const totalAmount = calculateTotal("ExTotalAmount").toDP(0);
                const amountTax = (totalAmount.times(entity.VatInv || new this.Decimal(0)).div(100)).toDP(0);
                const totalAmountTax = totalAmount.plus(amountTax);
                entity.TotalAmountTax = totalAmountTax;
                entity.TotalAmount = totalAmount;
                entity.AmountTax = amountTax;
            }
        }
        else {
            entity.TotalAmountTax = this.Decimal(0);
            entity.TotalAmount = this.Decimal(0);
            entity.AmountTax = this.Decimal(0);
        }
        this.OpenPopup("vatinvoice-editor", entity, false, { Dirty: true });
    }
}

