return {
    CreateBookingLocal: function (btn) {
        var defaultFee = this.GET("DefaultFee");
        var components = [];
        var form = this.EditForm.OpenConfig("Please double check async booking local!", () => {
            var featureName = "sea-booking-local-editor";
            var config = JSON.parse(localStorage.getItem("SalesFunction"));
            var booking = JSON.parse(JSON.stringify(this.EditForm.Entity));
            let consigneText = "";
            let consignee = this.EditForm.Entity.Consignee;
            if (consignee) {
                consigneText += `${(consignee.CompanyName || '')}\n`;
                consigneText += `${(consignee.Address || '')}\n`;
                consigneText += `Tell: ${(consignee.PhoneNumber || '')} Fax: ${(consignee.Fax || '')}\n`;
                consigneText += `Contact person: ${(consignee.ContactName || '')}`;
            }
            let customerText = "";
            let customer = this.EditForm.Entity.Customer;
            if (customer) {
                customerText += `${(customer.CompanyName || '')}\n`;
                customerText += `${(customer.Address || '')}\n`;
                customerText += `Tell: ${(customer.PhoneNumber || '')} Fax: ${(customer.Fax || '')}\n`;
                customerText += `Contact person: ${(customer.ContactName || '')}`;
            }
            let shipper = this.EditForm.Entity.Shipper;
            let shipperText = "";
            if (shipper) {
                shipperText += `${(shipper.CompanyName || '')}\n`;
                shipperText += `${(shipper.Address || '')}\n`;
                shipperText += `Tell: ${(shipper.PhoneNumber || '')} Fax: ${(shipper.Fax || '')}\n`;
                shipperText += `Contact person: ${(shipper.ContactName || '')}`;
            }
            booking.ConsigneeDes = consigneText != "" ? consigneText : this.EditForm.Entity.ConsigneeIdText;
            if (booking.Service.Name.includes("Import")) {
                booking.ConsigneeDes = customerText != "" ? customerText : customerText;
                booking.ConsigneeId = booking.CustomerId;
            }
            else {
                booking.ConsigneeDes = consigneText != "" ? consigneText : this.EditForm.Entity.ConsigneeIdText;
            }
            if (booking.Service.Name.includes("Export")) {
                booking.ShipperDes = shipperText != "" ? shipperText : customerText;
                booking.ShipperId = booking.CustomerId;
            }
            else {
                booking.ShipperDes = shipperText != "" ? shipperText : this.EditForm.Entity.ShipperIdText;
            }
            booking.Id = this.Uuid7.NewGuid();
            booking.QuotationCode = this.EditForm.Entity.Code;
            booking.QuotationId = this.EditForm.Entity.Id;
            booking.CreatedOn = this.dayjs().format('YYYY-MM-DD');
            booking.SaleId = this.EditForm.Entity.Customer ? this.EditForm.Entity.Customer.SaleId : null;
            booking.PolId = route.Entity.PolId;
            booking.PodId = route.Entity.PodId;
            booking.PickupAddress = route.Entity.Pickup;
            booking.DeliveryAddress = route.Entity.Delivery;
            booking.PortTransitId = route.Entity.ViaId;
            booking.FinalDestinationId = route.Entity.FinalDestinationId;
            booking.PlaceDeliveryId = route.Entity.PlaceDeliveryId;
            booking.SpecialRequest = this.EditForm.Entity.Subject;
            booking.StatusId = config["APPROVE_BOOKINGLOCAL"] ? 3 : 1;
            booking.NoApproved = config["APPROVE_BOOKINGLOCAL"] ? true : false;
            booking.ContPickupAtId = this.EditForm.Entity.PkEmptyId;
            booking.InsertedBy = this.Token.UserId;
            booking.VoucherTypeId = 3;
            if (this.EditForm.Entity.Vendor && this.EditForm.Entity.Vendor.PartnerTypeIdsText && this.EditForm.Entity.Vendor.PartnerTypeIdsText.toUpperCase().includes('AGENT')
                && this.EditForm.Entity.Vendor.ResidenceTypeIdText && this.EditForm.Entity.Vendor.ResidenceTypeIdText.toUpperCase().includes('OVERSEA')) {
                booking.AgentId = this.EditForm.Entity.VendorId;
                booking.VendorId = this.EditForm.Entity.CarrierId;
            }
            else {
                booking.AgentId = null;
                booking.VendorId = this.EditForm.Entity.VendorId;
                booking.CarrierId = this.EditForm.Entity.CarrierId;
            }
            var inquiryContainer = this.GET("EntityContainer");
            var items = inquiryContainer.AllListViewItem.map(x => x.Entity);
            var newItems = JSON.parse(JSON.stringify(items));
            var inquiryDetail = this.GET("InquiryDetail");
            newItems.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.BookingId = booking.Id;
            })
            booking.BookingContainer = newItems;
            var bookingDetail = [];
            var sellingRate = JSON.parse(JSON.stringify(defaultFee.AllListViewItem.filter(x => !x.GroupRow && x.Entity.Rate && this.Decimal(x.Entity.Rate).greaterThan(0)).map(x => x.Entity)));
            var buyingRate = JSON.parse(JSON.stringify(defaultFee.AllListViewItem.filter(x => !x.GroupRow && x.Entity.Cost && this.Decimal(x.Entity.Cost).greaterThan(0)).map(x => x.Entity)));
            booking.TypeId = 9;
            featureName = "logistics-booking-local-editor";
            buyingRate.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.BookingId = booking.Id;
                if (!item.IsContainer) {
                    item.UnitId = item.OtherUnitId;
                }
                item.Amount = this.Decimal(item.Cost || 0);
                item.VendorId = item.PayeeId || this.EditForm.Entity.VendorId;
                item.IsBuying = true;
                if (item.IsCBM) {
                    item.Quantity = !item.Quantity ? this.Decimal.max(this.Decimal(this.EditForm.Entity.CBM || 1), this.Decimal(item.MinLCLCost || 1)) : this.Decimal(item.Quantity);
                }
                else {
                    item.Quantity = item.Quantity ? this.Decimal(item.Quantity) : this.Decimal(1)
                }
                item.TotalAmount = (item.Quantity || this.Decimal(0)).times(item.Amount || new this.Decimal(0));
                item.AmountTax = (item.TotalAmount).times(this.Decimal(item.Vat || 0)).div(100);
                item.TotalAmountTax = item.TotalAmount.plus(item.AmountTax);
                bookingDetail.push(item);
            });
            sellingRate.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.BookingId = booking.Id;
                if (!item.IsContainer) {
                    item.UnitId = item.OtherUnitId;
                }
                item.Amount = this.Decimal(item.Rate || 0);
                item.VendorId = item.PayerId || this.EditForm.Entity.CustomerId;
                if (item.IsCBM) {
                    item.Quantity = !item.Quantity ? this.Decimal.max(this.Decimal(this.EditForm.Entity.CBM || 1), this.Decimal(item.MinLCLRate || 1)) : this.Decimal(item.Quantity);
                }
                else {
                    item.Quantity = item.Quantity ? this.Decimal(item.Quantity) : this.Decimal(1)
                }
                item.TotalAmount = (item.Quantity || this.Decimal(0)).times(item.Amount || new this.Decimal(0));
                item.AmountTax = (item.TotalAmount).times(this.Decimal(item.Vat || 0)).div(100);
                item.TotalAmountTax = item.TotalAmount.plus(item.AmountTax);
                bookingDetail.push(item);
            });
            booking.BookingDetail = bookingDetail.sort((a, b) => {
                const vendorCompare = a.VendorId.localeCompare(b.VendorId);
                if (vendorCompare !== 0) {
                    return vendorCompare;
                }
                return b.Amount - a.Amount;
            });
            this.OpenPopup(featureName, booking);
        }, () => { }, false);
    }
}

