return {
    Search: async function (com, entity) {
        if (!entity.SearchNo) {
            return;
        }
        var elementSea = document.querySelector(".search-body-sea");
        var elementAir = document.querySelector(".search-body-air");
        elementSea.style.display = "none";
        elementAir.style.display = "none";
        var datas = await com.RunQuerys();
        this.EditForm.Entity = datas[0][0];
        if (!datas[0] || !datas[0][0]) {
            this.Toast.Warning("Unable to find the requested data.")
            return;
        }
        this.UpdateView(true, true);
        if (this.EditForm.Entity.TypeId == 3) {
            var element = document.querySelector(".search-body-sea");
            element.style.display = "block";
        }
        else {
            var element = document.querySelector(".search-body-air");
            element.style.display = "block";
        }
    },
    EditHbl: function (btn, entity) {
        if (entity.TypeId == 3) {
            this.OpenPopup("track-trace-sea", entity);
        }
        else {
            this.OpenPopup("track-trace-air", entity);
        }
    }
}

