return {
    AddDetail: function () {
        var newItem = JSON.parse(JSON.stringify(this.EditForm.Meta.GridPolicies.filter(x => x.EntityId == "AddCharge")));
        this.EditForm.Entity.SearchData = null;
        this.EditForm.Entity.VendorIds = this.EditForm.Entity.VendorIds || this.EditForm.Entity.CompanyId;
        if (!this.EditForm.Entity.TypeId) {
            this.Toast.Warning("Please choose voucher type!");
            return;
        }
        var form = this.EditForm.OpenConfig("Please select fee!", async () => {
            var grid = this.GET("SearchData");
            var gridParent = this.GET("VoucherDetail");
            var feeIds = gridParent.Item.map(x => x.Entity.ShipmentFeeId);
            var datas = grid.AllListViewItem
                .filter(x => x.Selected && !feeIds.includes(x.Entity.Id))
                .map(x => x.Entity);
            var exUSD = this.Decimal(this.EditForm.Entity.ExchangeRateINV);
            form.DisposeAfterYes = true;
            if (!datas || datas.length == 0) {
                this.Toast.Warning("Please select fee VAT Invoice");
                form.DisposeAfterYes = false;
                return;
            }
            if (!this.EditForm.Entity.IsMultiple) {
                const vats = new Set(datas.map(x => x.Vat));
                if (vats.size > 1) {
                    this.Toast.Warning("There are multiple VAT types. Please select only one VAT type.");
                    form.DisposeAfterYes = false;
                    return;
                }
                this.EditForm.Entity.VatInv = datas[0].Vat;
            }
            const exchangeRateINVs = new Set(
                datas.filter(x => x.CurrencyCode == "USD").map(x => x.ExchangeRateINV).filter(x => x && Number(x) !== 1)
            );
            if (exchangeRateINVs.size > 1) {
                this.EditForm.OpenConfig("There are multiple ExchangeRate types. Please select only one ExchangeRate.", () => {
                }, () => { }, false, [], true);
            }
            const firstExchangeRateINV = Array.from(exchangeRateINVs)[0] || null;
            this.EditForm.Entity.ExchangeRateINV = firstExchangeRateINV || this.Decimal(1).div(this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateUSD"))["VND"]));
            this.EditForm.UpdateView(false, false, "VatInv", "ExchangeRateINV");
            datas.forEach(item => {
                item.ShipmentFeeId = item.Id;
                item.Id = this.Uuid7.NewGuid();
                item.VoucherId = this.EditForm.Entity.Id;
                item.IsManual = true;
                item.IsLock = false;
                item.NoSubmit = false;
                item.IsInvoice = false;
                item.IsPayment = false;
                item.IsInvoice = false;
                item.IsPaymentAcc = false;
                item.IsDebtAcc = false;
                item.IsPaid = false;
                item.FileNo = item.File.Code;
                item.HblNo = item.Shipment.HblNo;
                item.DescriptionIdText = item.Description ? item.Description.Name : null;
                if (item.CurrencyId == this.EditForm.Entity.CurrencyId) {
                    item.ExchangeRateINV = this.Decimal(1);
                }
                else {
                    item.ExchangeRateINV = this.Decimal(item.ExchangeRateINV || this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))[item.CurrencyCode])) || this.EditForm.Entity.ExchangeRateINV;
                }
                item.ExchangeRateINV2 = item.ExchangeRateINV.div(exUSD);
                var vat = this.Decimal(item.Vat || 0);
                item.ExTotalPaid = this.Decimal(item.ExTotalPaid || 0);
                if (item.TotalPaid > 0) {
                    item.ExTotalAmount = this.Decimal(item.ExTotalRemain).toDP(0);
                    item.ExTotalAmountTax = this.Decimal(item.ExTotalRemain).toDP(0);
                    item.Quantity = this.Decimal(1);
                    item.Vat = null;
                    item.TotalAmount = item.ExTotalAmount.div(item.ExchangeRateINV);
                    item.Amount = item.TotalAmount.div(item.Quantity);
                    item.ExAmount = item.Amount.times(item.ExchangeRateINV).toDP(0);
                    item.ExAmountTax = this.Decimal(0);
                    item.AmountTax = this.Decimal(0);
                    item.TotalAmountTax = item.TotalAmount;
                }
                else {
                    item.Quantity = this.Decimal(item.Quantity);
                    item.ExAmount = this.Decimal(item.Amount).times(item.ExchangeRateINV).toDP(0);
                    item.ExTotalAmount = this.Decimal(item.ExAmount).times(item.Quantity).toDP(0);
                    if (vat.isNegative()) {
                        if (item.ExAmount.isNegative()) {
                            item.ExAmountTax = vat.times(item.ExchangeRateINV).toDP(0);
                        }
                        else {
                            item.ExAmountTax = vat.abs().times(item.ExchangeRateINV).toDP(0);
                        }
                    } else {
                        item.ExAmountTax = this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toDP(0);
                    }
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax).toDP(0);
                item.InvoiceNo = item.InvoiceCode || item.InvoiceNo || item.ShipmentInvoiceCode || this.EditForm.Entity.InvoiceNo;
                item.InvoiceCode = item.InvoiceCode || this.EditForm.Entity.InvoiceNo;
                item.InvoiceDate = this.EditForm.Entity.InvoiceDate;
                item.SeriNo = this.EditForm.Entity.SeriNo;
                item.Form = this.EditForm.Entity.Form;
                item.PartnerId = this.EditForm.Entity.PartnerId;
                item.EinvoiceLink = this.EditForm.Entity.EinvoiceLink;
                item.EinvoiceCode = this.EditForm.Entity.EinvoiceCode;
            });
            gridParent.LoadMasterData(datas).then(() => {
                gridParent.ClearSelected();
                gridParent.AddRows(datas).then(() => {
                    this.EditForm.Dirty = true;
                    if (!this.EditForm.Entity.IsMultiple) {
                        this.EditForm.UpdateOneVat();
                    }
                    else {
                        this.EditForm.ShowSimple();
                    }
                });
            });
        }, () => { }, true, newItem, false, { Column: 24 });
    }
}

