    CreateRE: function () {
        if (this.EditForm.Entity.DebtAccId) {
            this.OpenPopup("debt-accounting-editor", { Id: this.EditForm.Entity.DebtAccId }, true);
            return;
        }
        var exUSD = this.Decimal(this.EditForm.Entity.ExchangeRateINV);
        var voucher = JSON.parse(JSON.stringify(this.EditForm.Entity));
        voucher.Id = this.Uuid7.NewGuid();
        voucher.DebtAccId = this.EditForm.Entity.Id;
        voucher.Code = null;
        voucher.VoucherTypeId = 14;
        voucher.FeeTypeId = "1,2,3,4";
        voucher.InsertedBy = this.Token.UserId;
        voucher.TypeId = 4;
        voucher.ActionId = 2;
        voucher.IsPaid = false;
        voucher.UserApprovedIds = null;
        voucher.ProgressId = 1;
        voucher.IsMultiple = true;
        voucher.PostDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        voucher.VoucherDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        voucher.VendorId = this.EditForm.Entity.VendorIds;
        voucher.CompanyId = this.EditForm.Entity.VendorIds;
        voucher.CompanyAddress = this.EditForm.Entity.VendorIdsMasterData ? this.EditForm.Entity.VendorIdsMasterData.AddressInv : null;
        voucher.PartnerId = this.EditForm.Entity.VendorIds;
        voucher.VendorName = this.EditForm.Entity.VendorIdsMasterData ? this.EditForm.Entity.VendorIdsMasterData.Name : null;
        voucher.Person = this.EditForm.Entity.BuyerName;
        var gridParent = this.GET("ShipmentInvoiceDetail");
        var voucherDetail = JSON.parse(JSON.stringify(gridParent.Item.map(x => x.Entity)));
        var check = [...new Set(gridParent.Item.map(x => x.Entity.InvoiceNo).filter(x => x != null))];
        voucher.ReferencesIds = check ? check.join(",") : null;
        voucher.Notes = null;
        voucher.InvoiceDate = null;
        voucher.InvNotes = this.EditForm.Entity.Notes;
        voucherDetail.forEach(item => {
            item.ShipmentFeeId = item.ShipmentFeeId;
            item.Id = this.Uuid7.NewGuid();
            item.InsertedBy = this.Token.UserId;
            item.DescriptionIdText = item.Description ? item.Description.Name : null;
            item.HblNo = item.Shipment.HblNo;
            item.FileNo = item.File.Code;
            if (this.EditForm.Entity.CurrencyCode != "VND") {
                item.ExchangeRateINV = this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))[item.CurrencyCode]);
                item.ExchangeRateINV2 = item.ExchangeRateINV.div(exUSD);
                var vat = this.Decimal(item.Vat || 0);
                item.ExAmount = (this.Decimal(item.Amount).times(item.ExchangeRateINV)).toDP(0);
                item.ExTotalAmount = (this.Decimal(item.ExAmount).times(item.Quantity)).toDP(0);
                if (vat.isNegative()) {
                    item.ExAmountTax = (this.Decimal(item.Vat).abs().times(item.ExchangeRateINV)).toDP(0);
                } else {
                    item.ExAmountTax = (this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100)))).toDP(0);
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax);
            }
            else {
                item.ExchangeRateINV = item.ExchangeRateINV;
                item.ExchangeRateINV2 = item.ExchangeRateINV2;
            }
            if (item.TypeId === 1 || ([3, 4].includes(item.TypeId) && item.IsAdvCustomer)) {
                item.Amount = -item.Amount;
                item.TotalAmount = -item.TotalAmount;
                item.AmountTax = -item.AmountTax;
                item.TotalAmountTax = item.TotalAmountTax;
                item.ExAmount = -item.ExAmount;
                item.ExTotalAmount = -item.ExTotalAmount;
                item.ExAmountTax = -item.ExAmountTax;
                item.ExTotalAmountTax = item.ExTotalAmountTax;
            }
            item.IsManual = true;
            item.IsLock = false;
            item.NoSubmit = false;
            item.IsInvoice = false;
            item.IsPayment = false;
            item.IsInvoice = false;
            item.IsPaymentAcc = false;
            item.IsDebtAcc = false;
            item.IsPaid = false;
        });
        voucher.VoucherDetail = voucherDetail;
        const calculateTotal = (field) => voucher.VoucherDetail.reduce((acc, x) => acc.plus(this.Decimal(x[field] || 0)), this.Decimal(0));
        const totalAmount2 = calculateTotal("ExTotalAmount").toDP(0);
        const amountTax2 = calculateTotal("ExAmountTax").toDP(0);
        var tempTotalAmountTax = totalAmount2.plus(amountTax2);
        var rateAmout = this.Decimal(voucher.DebitAmount).sub(tempTotalAmountTax);
        if (!rateAmout.eq(this.Decimal(0))) {
            var item = {
                Id: this.Uuid7.NewGuid(),
                InsertedBy: this.Token.UserId,
                DescriptionIdText: `Tiền chênh lệch`,
                CurrencyId: voucher.CurrencyId,
                CurrencyCode: voucher.CurrencyCode,
                ExchangeRateVND: this.Decimal(1),
                Vat: null,
                Quantity: 1,
                Amount: rateAmout,
                TotalAmount: rateAmout,
                TotalAmountTax: rateAmout,
                AmountTax: null,
            };
            item.ExchangeRateINV = item.ExchangeRateVND;
            var vat = this.Decimal(item.Vat || 0);
            item.Quantity = this.Decimal(item.Quantity);
            item.ExAmount = this.Decimal(item.Amount).times(item.ExchangeRateINV).toDP(0);
            item.ExTotalAmount = this.Decimal(item.ExAmount).times(item.Quantity).toDP(0);
            if (vat.isNegative()) {
                item.ExAmountTax = vat.abs().times(item.ExchangeRateINV).toDP(0);
            } else {
                item.ExAmountTax = this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toDP(0);
            }
            item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax);
            item.IsManual = true;
            item.IsLock = false;
            item.NoSubmit = false;
            item.IsInvoice = false;
            item.IsPayment = false;
            item.IsInvoice = false;
            item.IsPaymentAcc = false;
            item.IsDebtAcc = false;
            item.IsPaid = false;
            voucher.VoucherDetail.push(item);
        }
        if (voucher.VoucherDetail && voucher.VoucherDetail.length > 0) {
            if (voucher.IsMultiple) {
                const totalAmount = calculateTotal("ExTotalAmount").toDP(0);
                const amountTax = calculateTotal("ExAmountTax").toDP(0);
                voucher.TotalAmountTax = totalAmount.plus(amountTax);
                voucher.TotalAmount = totalAmount;
                voucher.AmountTax = amountTax;
            }
            else {
                const totalAmount = calculateTotal("ExTotalAmount").toDP(0);
                const amountTax = (totalAmount.times(voucher.VatInv || new this.Decimal(0)).div(100)).toDP(0);
                const totalAmountTax = totalAmount.plus(amountTax);
                voucher.TotalAmountTax = totalAmountTax;
                voucher.TotalAmount = totalAmount;
                voucher.AmountTax = amountTax;
            }
        }
        else {
            voucher.TotalAmountTax = this.Decimal(0);
            voucher.TotalAmount = this.Decimal(0);
            voucher.AmountTax = this.Decimal(0);
        }
        var check = [...new Set(voucher.VoucherDetail.map(x => x.InvoiceNo).filter(x => x != null))];
        if (check.length > 1) {
            voucher.InvoiceNo = null;
        }
        else {
            var detail = voucher.VoucherDetail.find(x => x.InvoiceNo);
            if (detail) {
                voucher.InvoiceNo = detail.InvoiceNo;
            }
        }
        if (check) {
            this.EditForm.Entity.ReferencesIds = check.join(",");
        }
        const grouped = voucher.VoucherDetail.filter(x => x.FileNo).reduce((acc, x) => {
            const key = `${x.FileNo}_${x.HblNo}`;
            if (!acc[key]) acc[key] = [];
            acc[key].push(x.Description ? x.Description.Description : null);
            return acc;
        }, {});
        const descriptions = Object.entries(grouped)
            .map(([key, names]) => {
                const [fileNo, hblNo] = key.split("_");
                const nameString = names.length > 1 ? `${names.join(", ")}` : names[0];
                return `${nameString} (Số lô: ${fileNo}, Số HBL: ${hblNo})`;
            })
            .join("; ");
        voucher.Description = descriptions;
        this.OpenPopup("debt-accounting-editor", voucher);
    },
