return {
    Add: function () {
        var entity = {
            StatusId: this.SalesFunction["APPROVE_PAYMENT_REQUEST"] ? 3 : 1,
            NoApproved: this.SalesFunction["APPROVE_PAYMENT_REQUEST"] ? true : false,
            VoucherTypeId: 11,
            InsertedBy: this.Token.UserId,
            FeeTypeId: "ds.TypeId in (1,2,3,4)",
            TypeId: 2,
            ObhTypeId: 1,
            PostDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss'),
            InvoiceDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss'),
            ExchangeRateINV: this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))["USD"]),
        }
        this.OpenPopup("payment-request-editor", entity);
    },
    Edit: function (btn, entity) {
        this.OpenPopup("payment-request-editor", { Id: entity.Id }, true);
    }
}

