return {
    Init: function () {
        const formatDate = (date) => this.dayjs(date).format("YYYY-MM-DD");
        let today = this.dayjs();
        let firstDayOfMonth = today.startOf("month");
        let lastDayOfMonth = today.endOf("month").add(1, "day");
        this.Entity = {
            InsertedBy: this.Token.UserId,
            Id: this.Uuid7.NewGuid(),
            FromDate: firstDayOfMonth,
            ToDate: lastDayOfMonth
        };
    },
    OpenFilter: function () {
        var newItem = this.EditForm.GroupTree.find(x => x.FieldName == "SearchForm");
        var form = this.EditForm.OpenConfig("Filter report!", async () => {
            var component = this.EditForm.ChildCom.find(x => x.Meta.FieldName == "Shipment");
            component.Meta.GroupBy = form.Entity.Group ? form.Entity.Group.GroupBy : component.Meta.GroupBy;
            component.Meta.GroupFormat = form.Entity.Group ? form.Entity.Group.GroupFormat : component.Meta.GroupFormat;
            component.ReloadData().then(() => {
                if (component.Meta.GroupBy == "ParentId") {
                    component.HideColumn([]);
                }
                else if (component.Meta.GroupBy == "SaleId") {
                    component.HideColumn("ShipmentTypeId", "CustomerId", "TypeId");
                }
                else if (component.Meta.GroupBy == "CustomerId") {
                    component.HideColumn("ShipmentTypeId", "SaleId", "TypeId");
                }
                else if (component.Meta.GroupBy == "ShipmentTypeId") {
                    component.HideColumn("CustomerId", "SaleId", "TypeId");
                }
                else if (component.Meta.GroupBy == "TypeId") {
                    component.HideColumn("CustomerId", "SaleId", "ShipmentTypeId");
                }
            })
        }, () => { }, true, [], false, newItem);
    }
}

