return {
    Customer: function (form, gridview) {
        this.EditForm.OpenConfig("Switch to official customer!", () => {
            this.EditForm.Entity.TypeId = 3;
            this.EditForm.Dirty = true;
            this.EditForm.SavePatch(true).then(() => {
                this.EditForm.Dispose();
                this.OpenFrom.OpenPopup("customer-editor", this.EditForm.Entity);
            });
        }, []);
    }
}

