return {
    OnSave: function (form, gridview) {
        var voucherDetail = this.GET("VoucherDetail");
        if (voucherDetail.Item && voucherDetail.Item.length > 0) {
            voucherDetail.Item.forEach(item => {
                item.Entity.VendorId = item.Entity.VendorId || this.EditForm.Entity.UserId;
            });
        }
    },
    CalcTotalAmount: function (com, entity) {
        if (com.Item && com.Item.length > 0) {
            const totalAmount = com.Item.reduce((acc, x) => {
                const total = this.Decimal(x.Entity.Amount || 0);
                return acc.plus(total);
            }, this.Decimal(0));
            this.EditForm.Entity.AdvanceAmount = totalAmount;
            this.EditForm.Entity.RemainingAmount = this.Decimal(this.EditForm.Entity.AdvanceAmount || 0).sub(totalAmount);
            this.EditForm.UpdateView(false, false, "AdvanceAmount", "RemainingAmount");
        }
        else {
            this.EditForm.Entity.AdvanceAmount = this.Decimal(0);
            this.EditForm.Entity.RemainingAmount = this.Decimal(0).sub(this.Decimal(this.EditForm.Entity.SettlementAmount));
            this.EditForm.UpdateView(false, false, "AdvanceAmount", "RemainingAmount");
        }
    },
    CalcTotalAmount2: function (com, entity) {
        if (com.Item && com.Item.length > 0) {
            const totalAmount = com.Item.reduce((acc, x) => {
                const total = this.Decimal(x.Entity.TotalAmountTax || 0);
                return acc.plus(total);
            }, this.Decimal(0));
            this.EditForm.Entity.SettlementAmount = totalAmount;
            this.EditForm.Entity.RemainingAmount = this.Decimal(this.EditForm.Entity.AdvanceAmount || 0).sub(totalAmount);
            this.EditForm.UpdateView(false, false, "SettlementAmount", "RemainingAmount");
        }
        else {
            this.EditForm.Entity.SettlementAmount = this.Decimal(0);
            this.EditForm.Entity.RemainingAmount = this.Decimal(this.EditForm.Entity.AdvanceAmount || 0).sub(this.Decimal(0));
            this.EditForm.UpdateView(false, false, "AdvanceAmount", "RemainingAmount");
        }
    },
    AddDetail: function (gridview) {
        var gridviewAdv = this.GET("ReimbursementDetail");
        this.EditForm.Entity.AdvanceId = null;
        this.EditForm.Entity.Advance = null;
        var newItem = JSON.parse(JSON.stringify(this.EditForm.Meta.GridPolicies.filter(x => x.EntityId == "CopyAdvanceRequest")));
        var form = this.EditForm.OpenConfig("Please select Advance Request!", async () => {
            if (!this.EditForm.Entity.AdvanceId) {
                return;
            }
            var service = this.GET("ServiceCopyAdvance");
            var datas = await service.RunQuerys();
            var advs = datas[0];
            advs.forEach(item => {
                item.VoucherId = this.EditForm.Entity.Id;
            });
            gridviewAdv.LoadMasterData(advs).then(() => {
                gridviewAdv.ClearSelected();
                gridviewAdv.AddRows(advs).then(rows => {
                    rows.forEach(row => {
                        row.Selected = true;
                    });
                    if (rows[0]) {
                        rows[0].Element.focus();
                    }
                });
            });
            var gridview = this.GET("VoucherDetail");
            datas = await service.RunQuerys();
            var newFees = datas[1];
            if (newFees) {
                newFees.forEach(item => {
                    item.ParentId = item.VoucherId;
                    item.Id = this.Uuid7.NewGuid();
                    item.VoucherId = this.EditForm.Entity.Id;
                    item.Type = 1;
                });
                debugger;
                gridview.LoadMasterData(newFees).then(() => {
                    gridview.ClearSelected();
                    gridview.AddRows(newFees).then(rows => {
                        rows.forEach(row => {
                            row.Selected = true;
                        });
                        if (rows[0]) {
                            rows[0].Element.focus();
                        }
                    });
                });
            }

        }, () => { }, true, newItem);
    },
    AddContextMenu: function (gridview, context) {
        var data = gridview.Item.filter(x => x.Selected);
        var gridview2 = this.GET("VoucherDetail");
        context.MenuItems.push({
            Icon: "fas fa-page-break",
            Text: "Split Advance Request",
            Click: async () => {
                var parentIds = data.map(x => x.Entity.Id);
                for (const element of parentIds) {
                    gridview.RemoveRowById(element);
                    let patchVM = {
                        Table: "Voucher",
                        Changes: [
                            { Field: "Id", Value: element },
                            { Field: "VoucherId", Value: null }
                        ]
                    };
                    await this.Client.PatchAsync(patchVM);
                };
                this.EditForm.Dirty = true;
                var deleteItem = gridview2.Item.filter(item => parentIds.includes(item.Entity.ParentId));
                if (deleteItem && deleteItem.length > 0) {
                    await gridview2.HardDeleteConfirmed(deleteItem.map(x => x.Entity));
                }
                this.CalcTotalAmount(gridview);
                if (!this.EditForm.Entity.Id.startsWith('-')) {
                    await this.EditForm.SavePatch();
                }
            }
        });
    },
    CheckObh: function (com, entity) {
        if (entity.IsObh) {

        }
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.Amount = this.Decimal(entity.Amount || 0);
        entity.TotalAmount = (entity.Quantity || new this.Decimal(0)).times(entity.Amount || new this.Decimal(0));
        if ((entity.Vat || new this.Decimal(0)).isNegative()) {
            entity.AmountTax = this.Decimal(entity.Vat).abs();
        } else {
            entity.AmountTax = (entity.TotalAmount).times(entity.Vat || new this.Decimal(0)).div(100);
        }
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.AmountTax);
        com.Parent.UpdateView(false);
    },
    BeforeCreated: function (rowSection, entity, listView) {
        if (listView) {
            var item = listView.Item[listView.Item.length - 1];
            if (item) {
                entity.VendorId = entity.VendorId || item.Entity.VendorId;
            }
            else {
                entity.VendorId = this.EditForm.Entity.UserId;
            }
        }
    },
    CreateRP: function () {
        if (this.EditForm.Entity.PaymentAccId) {
            this.OpenPopup("payment-accounting-editor", { Id: this.EditForm.Entity.PaymentAccId }, true);
            return;
        }
        var voucher = JSON.parse(JSON.stringify(this.EditForm.Entity));
        voucher.Id = this.Uuid7.NewGuid();
        voucher.ReId = this.EditForm.Entity.Id;
        voucher.IsDescription = true;
        voucher.Code = null;
        voucher.ActionId = 2;
        voucher.VoucherTypeId = 15;
        voucher.FeeTypeId = "1";
        voucher.InsertedBy = this.Token.UserId;
        voucher.TypeId = 6;
        voucher.ProgressId = 1;
        voucher.IsPaid = true;
        voucher.UserApprovedIds = null;
        voucher.IsMultiple = true;
        voucher.InvoiceDate = null;
        voucher.PaidDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        voucher.PostDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        voucher.VoucherDate = this.dayjs().format('YYYY-MM-DDTHH:mm:ss');
        voucher.VendorId = this.Token.UserId;
        voucher.PartnerId = this.Token.UserId;
        voucher.CompanyId = this.EditForm.Entity.UserId;
        voucher.CompanyAddress = this.EditForm.Entity.User ? this.EditForm.Entity.User.Address : null;
        voucher.VendorName = this.Token.FullName;
        voucher.Person = this.EditForm.Entity.User ? this.EditForm.Entity.User.FullName : null;
        voucher.ReferencesIds = this.EditForm.Entity.Code;
        var gridParent = this.GET("VoucherDetail");
        var gridReimbursementDetail = this.GET("ReimbursementDetail");
        var voucherDetail = JSON.parse(JSON.stringify(gridParent.Item.map(x => x.Entity)));
        var reimbursementDetail = JSON.parse(JSON.stringify(gridReimbursementDetail.Item.map(x => x.Entity)));
        var adv = reimbursementDetail.map(x => x.Code).join(",");
        if (voucherDetail && voucherDetail.length == 0) {
            voucherDetail.push({
                Id: this.Uuid7.NewGuid(),
                InsertedBy: this.Token.UserId,
                DescriptionIdText: voucher.Description,
                Quantity: this.Decimal(1),
                Amount: this.Decimal(voucher.Amount),
                CurrencyId: voucher.CurrencyId,
                ExchangeRateINV: this.Decimal(1)
            });
            voucherDetail.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.InsertedBy = this.Token.UserId;
                item.DescriptionIdText = item.Description ? item.Description.Name : null;
                item.HblNo = item.Shipment ? item.Shipment.HblNo : null;
                item.FileNo = item.File ? item.File.Code : null;
                item.ExchangeRateINV = this.Decimal(item.ExchangeRateVND || item.ExchangeRateVND);
                var vat = this.Decimal(item.Vat || 0);
                item.Quantity = this.Decimal(item.Quantity);
                item.Amount = this.Decimal(item.Amount);
                item.ExAmount = this.Decimal(item.Amount).times(item.ExchangeRateINV).toDP(0);
                item.ExTotalAmount = this.Decimal(item.ExAmount).times(item.Quantity).toDP(0);
                if (vat.isNegative()) {
                    item.ExAmountTax = vat.abs().times(item.ExchangeRateINV).toDP(0);
                } else {
                    item.ExAmountTax = this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toDP(0);
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax);
                item.IsManual = true;
                item.IsLock = false;
                item.NoSubmit = false;
                item.IsInvoice = false;
                item.IsPayment = false;
                item.IsInvoice = false;
                item.IsPaymentAcc = false;
                item.IsDebtAcc = false;
                item.IsPaid = false;
            });
        }
        else {
            debugger;
            var check = [...new Set(gridParent.Item.map(x => x.Entity.InvoiceNo).filter(x => x != null))];
            voucher.Notes = null;
            voucher.InvNotes = this.EditForm.Entity.Notes;
            voucherDetail.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.InsertedBy = this.Token.UserId;
                item.DescriptionIdText = item.Description ? item.Description.Name : null;
                item.HblNo = item.Shipment ? item.Shipment.HblNo : null;
                item.FileNo = item.File ? item.File.Code : null;
                item.ExchangeRateINV = this.Decimal(item.ExchangeRateVND || item.ExchangeRateVND);
                var vat = this.Decimal(item.Vat || 0);
                item.Quantity = this.Decimal(item.Quantity);
                item.Amount = this.Decimal(item.Amount);
                item.ExAmount = this.Decimal(item.Amount).times(item.ExchangeRateINV).toDP(0);
                item.ExTotalAmount = this.Decimal(item.ExAmount).times(item.Quantity).toDP(0);
                if (vat.isNegative()) {
                    item.ExAmountTax = vat.abs().times(item.ExchangeRateINV).toDP(0);
                } else {
                    item.ExAmountTax = this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toDP(0);
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax)
                item.IsManual = true;
                item.IsLock = false;
                item.NoSubmit = false;
                item.IsInvoice = false;
                item.IsPayment = false;
                item.IsInvoice = false;
                item.IsPaymentAcc = false;
                item.IsDebtAcc = false;
                item.IsPaid = false;
            });
            if (this.Decimal(voucher.RemainingAmount).gt(this.Decimal(0)) && adv) {
                var item = {
                    Id: this.Uuid7.NewGuid(),
                    InsertedBy: this.Token.UserId,
                    DescriptionIdText: `Thu lại tiền tạm ứng nhân viên (Số tạm ứng: ${adv})`,
                    CurrencyId: voucher.CurrencyId,
                    CurrencyCode: voucher.CurrencyCode,
                    ExchangeRateVND: voucher.ExchangeRateVND || this.Decimal(1),
                    Vat: null,
                    Quantity: 1,
                    Amount: this.Decimal(voucher.RemainingAmount),
                    TotalAmount: this.Decimal(voucher.RemainingAmount),
                    TotalAmountTax: this.Decimal(voucher.RemainingAmount),
                    AmountTax: null,
                };
                item.ExchangeRateINV = this.Decimal(item.ExchangeRateVND || item.ExchangeRateVND);
                var vat = this.Decimal(item.Vat || 0);
                item.Quantity = this.Decimal(item.Quantity);
                item.Amount = this.Decimal(item.Amount);
                item.ExAmount = this.Decimal(item.Amount).times(item.ExchangeRateINV).toDP(0);
                item.ExTotalAmount = this.Decimal(item.ExAmount).times(item.Quantity).toDP(0);
                if (vat.isNegative()) {
                    item.ExAmountTax = vat.abs().times(item.ExchangeRateINV).toDP(0);
                } else {
                    item.ExAmountTax = this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toDP(0);
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax);
                item.IsManual = true;
                item.IsLock = false;
                item.NoSubmit = false;
                item.IsInvoice = false;
                item.IsPayment = false;
                item.IsInvoice = false;
                item.IsPaymentAcc = false;
                item.IsDebtAcc = false;
                item.IsPaid = false;
                voucherDetail.push(item);
            }
            if (this.Decimal(voucher.RemainingAmount).lt(this.Decimal(0)) && adv) {
                var item = {
                    Id: this.Uuid7.NewGuid(),
                    InsertedBy: this.Token.UserId,
                    DescriptionIdText: `Thanh toán tiền tạm ứng nhân viên (Số tạm ứng: ${adv})`,
                    CurrencyId: voucher.CurrencyId,
                    CurrencyCode: voucher.CurrencyCode,
                    ExchangeRateVND: voucher.ExchangeRateVND || this.Decimal(1),
                    Vat: null,
                    Quantity: 1,
                    Amount: this.Decimal(voucher.RemainingAmount),
                    TotalAmount: this.Decimal(voucher.RemainingAmount),
                    TotalAmountTax: this.Decimal(voucher.RemainingAmount),
                    AmountTax: null,
                };
                item.ExchangeRateINV = this.Decimal(item.ExchangeRateVND || item.ExchangeRateVND);
                var vat = this.Decimal(item.Vat || 0);
                item.Quantity = this.Decimal(item.Quantity);
                item.Amount = this.Decimal(item.Amount);
                item.ExAmount = this.Decimal(item.Amount).times(item.ExchangeRateINV).toDP(0);
                item.ExTotalAmount = this.Decimal(item.ExAmount).times(item.Quantity).toDP(0);
                if (vat.isNegative()) {
                    item.ExAmountTax = vat.abs().times(item.ExchangeRateINV).toDP(0);
                } else {
                    item.ExAmountTax = this.Decimal(item.ExTotalAmount).times(vat.div(this.Decimal(100))).toDP(0);
                }
                item.ExTotalAmountTax = this.Decimal(item.ExTotalAmount).plus(item.ExAmountTax);
                item.IsManual = true;
                item.IsLock = false;
                item.NoSubmit = false;
                item.IsInvoice = false;
                item.IsPayment = false;
                item.IsInvoice = false;
                item.IsPaymentAcc = false;
                item.IsDebtAcc = false;
                item.IsPaid = false;
                voucherDetail.push(item);
            }
        }
        voucher.VoucherDetail = voucherDetail;
        if (voucher.VoucherDetail && voucher.VoucherDetail.length > 0) {
            const calculateTotal = (field) => voucher.VoucherDetail.filter(x => !x.IsAbs).reduce((acc, x) => acc.plus(this.Decimal(x[field] || 0)), this.Decimal(0));
            if (voucher.IsMultiple) {
                const totalAmount = calculateTotal("ExTotalAmount").toDP(0);
                const amountTax = calculateTotal("ExAmountTax").toDP(0);
                voucher.TotalAmountTax = totalAmount.plus(amountTax)
                voucher.TotalAmount = totalAmount;
                voucher.AmountTax = amountTax;
            }
            else {
                const totalAmount = calculateTotal("ExTotalAmount").toDP(0);
                const amountTax = (totalAmount.times(new this.Decimal(voucher.VatInv || 0)).div(100)).toDP(0);
                const totalAmountTax = totalAmount.plus(amountTax);
                voucher.TotalAmountTax = totalAmountTax;
                voucher.TotalAmount = totalAmount;
                voucher.AmountTax = amountTax;
            }
        }
        else {
            voucher.TotalAmountTax = this.Decimal(0);
            voucher.TotalAmount = this.Decimal(0);
            voucher.AmountTax = this.Decimal(0);
        }
        this.OpenPopup("payment-accounting-editor", voucher);
    }
};

