Script:
return {
    Init: function () {
        if (!this.ShipmentDetail) {
            var id = this.Uuid7.NewGuid();
            this.ShipmentDetail = {
                Id: id,
                ParentId: this.Entity.Id,
                Code: this.Entity.Code,
                TypeId: this.Entity.TypeId,
                CyId: this.Entity.CyId,
                PolId: this.Entity.PolId,
                PodId: this.Entity.PodId,
                EtaDate: this.Entity.EtaDate,
                EtdDate: this.Entity.EtdDate,
                Vessel: `${(this.Entity.Vessel || '')}/${(this.Entity.VesselVoy || '')}`,
                VesselVoy: this.Entity.VesselVoy,
                OceanVessel: `${(this.Entity.OceanVessel || '')}/${(this.Entity.OceanVesselVoy || '')}`,
                OceanVesselVoy: this.Entity.OceanVesselVoy,
                CommodityId: this.Entity.CommodityId,
                PortTransitId: this.Entity.PortTransitId,
                PlaceReceiptId: this.Entity.PolId,
                PlaceDeliveryId: this.Entity.PodId,
                SaillingDate: this.Entity.EtdDate,
                IssueDate: this.Entity.EtaDate,
                PlaceIssueId: this.Entity.PolId
            };
        }
        window.setTimeout(async () => {
            var portTransit = this.GET("EntityTransit");
            let agent = this.Entity.Agent;
            let agentText = "";
            if (agent) {
                agentText += `${(agent.CompanyName || '')}\n`;
                agentText += `${(agent.Address || '')}\n`;
                agentText += `Tell: ${(agent.PhoneNumber || '')} Fax: ${(agent.Fax || '')}\n`;
                agentText += `Contact person: ${(agent.ContactName || '')}`;
            }
            this.ShipmentDetail.ForDeliveryGoodsId = this.Entity.AgentId;
            this.EditForm.ShipmentDetail.ForDeliveryGoodsDes = agentText.trim();
            this.UpdateView2(false, false, "ShipmentDetail", "ForDeliveryGoodsId", "ForDeliveryGoodsDes");
            if (this.EditForm.ShipmentDetail.BookingLocalId) {
                var serviceBookingLocal = this.GET("ServiceBookingLocal");
                var datas = await serviceBookingLocal.RunQuerys();
                this.EditForm.BookingLocalDetail = datas[0];
            }
            else {
                this.EditForm.BookingLocalDetail = [];
            }
        }, 1000);
    },
    OnSave: function (form, gridview) {
        var hbl = this.GET("ShipmentDetail");
        if (hbl.Item && hbl.Item.length > 0) {
            hbl.Item.forEach(item => {
                item.Entity.VendorId = this.Entity.VendorId;
                item.Entity.MblNo = this.Entity.MblNo;
                item.Entity.MblTypeId = this.Entity.MblTypeId;
                item.Entity.CommodityId = item.Entity.CommodityId || this.Entity.CommodityId;
                item.Entity.ShipmentTypeId = item.Entity.ShipmentTypeId || this.Entity.ShipmentTypeId;
                item.Entity.CarrierId = item.Entity.CarrierId || this.Entity.CarrierId;
                item.Entity.DeliveryTermId = item.Entity.DeliveryTermId || this.Entity.DeliveryTermId;
                item.Entity.IncotermId = item.Entity.IncotermId || this.Entity.IncotermId;
            });
            this.Entity.SaleIds = [...new Set(hbl.Item.map(x => x.Entity.SaleId).filter(x => x != null))].join(",");
        }
        if (!this.Entity.SaleIds) {
            this.Entity.SaleIds = this.EditForm.ShipmentDetail.SaleId;
        }
        var costFee = this.EditForm.ChildCom.find(x => x.Parent.Meta.Label == "Costing");
        var revenue = this.EditForm.ChildCom.find(x => x.Parent.Meta.Label == "Revenue");
        if (costFee && costFee.Item && costFee.Item.length > 0) {
            costFee.Item.filter(x => !x.GroupRow).forEach((item, index) => {
                item.Entity.Order = index;
            });
        }
        if (revenue && revenue.Item && revenue.Item.length > 0) {
            revenue.Item.filter(x => !x.GroupRow).forEach((item, index) => {
                item.Entity.Order = index;
            });
        }
    },
    CheckSaveDetail: function () {
        var costFee = this.EditForm.ChildCom.find(x => x.Parent.Meta.Label == "Costing");
        var revenue = this.EditForm.ChildCom.find(x => x.Parent.Meta.Label == "Revenue");
        if (costFee && costFee.Item) {
            var data = costFee.Item.filter(x => !x.GroupRow);
            if (data && data.length > 0) {
                const seen = new Set();
                const duplicates = data.filter(item => {
                    const key = `${item.Entity.DescriptionId}-${item.Entity.VendorId}-${item.Entity.UnitId}`;
                    if (seen.has(key)) {
                        return true;
                    } else {
                        seen.add(key);
                        return false;
                    }
                });
                if (duplicates.length > 0) {
                    this.EditForm.OpenConfig("Duplicate cost fees have been detected. Please double-check and confirm if the data is correct. Data has been saved !", () => {
                    }, () => { }, false, [], true);
                    costFee.ClearSelected();
                    duplicates.forEach(x => {
                        x.Selected = true;
                        x.Element.focus();
                    })
                    return false;
                }
                return false;
            }

        }
        if (revenue && revenue.Item) {
            var data = revenue.Item.filter(x => !x.GroupRow);
            if (data && data.length > 0) {
                const seen = new Set();
                const duplicates = data.filter(item => {
                    const key = `${item.Entity.DescriptionId}-${item.Entity.VendorId}-${item.Entity.UnitId}`;
                    if (seen.has(key)) {
                        return true;
                    } else {
                        seen.add(key);
                        return false;
                    }
                });
                if (duplicates.length > 0) {
                    this.EditForm.OpenConfig("Duplicate revenue fees have been detected. Please double-check and confirm if the data is correct. Data has been saved !", () => {
                    }, () => { }, false, [], true);
                    revenue.ClearSelected();
                    duplicates.forEach(x => {
                        x.Selected = true;
                        x.Element.focus();
                    })
                    return false;
                }
                return false;
            }
        }
        return false;

    },
    ChangeTransitPort: function (com, entity) {
        this.Entity.PortTransitId = entity.PortTransitId;
        this.DefaultDetail();
    },
    FirstLoadHBL: function () {
        window.setTimeout(() => {
            var hbl = this.GET("ShipmentDetail");
            if (this.EditForm.HblEntity) {
                var item = hbl.Item.find(x => x.Entity.Id == this.EditForm.HblEntity.Id);
                if (hbl && hbl.Item && item) {
                    item.Element.click();
                }
            }
            else {
                if (hbl && hbl.Item && hbl.Item[0]) {
                    hbl.Item[0].Element.click();
                }
            }
        }, 1000);
    },
    AddDetail: function () {
        if (this.EditForm.ShipmentDetail.Id.startsWith("-")) {
            return;
        }
        var hbl = this.GET("ShipmentDetail");
        hbl.ClearSelected();
        var id = this.Uuid7.NewGuid();
        let agent = this.Entity.Agent;
        let agentText = "";
        if (agent) {
            agentText += `${(agent.CompanyName || '')}\n`;
            agentText += `${(agent.Address || '')}\n`;
            agentText += `Tell: ${(agent.PhoneNumber || '')} Fax: ${(agent.Fax || '')}\n`;
            agentText += `Contact person: ${(agent.ContactName || '')}`;
        }
        this.ShipmentDetail = {
            Id: id,
            ParentId: this.Entity.Id,
            Code: this.Entity.Code,
            TypeId: this.Entity.TypeId,
            PolId: this.Entity.PolId,
            PodId: this.Entity.PodId,
            EtaDate: this.Entity.EtaDate,
            EtdDate: this.Entity.EtdDate,
            Vessel: `${(this.Entity.Vessel || '')}/${(this.Entity.VesselVoy || '')}`,
            VesselVoy: this.Entity.VesselVoy,
            OceanVessel: `${(this.Entity.OceanVessel || '')}/${(this.Entity.OceanVesselVoy || '')}`,
            OceanVesselVoy: this.Entity.OceanVesselVoy,
            CommodityId: this.Entity.CommodityId,
            PortTransitId: this.Entity.PortTransitId,
            PlaceReceiptId: this.Entity.PolId,
            PlaceDeliveryId: this.Entity.PodId,
            SaillingDate: this.Entity.EtdDate,
            IssueDate: this.Entity.EtaDate,
            PlaceIssueId: this.Entity.PolId,
            ForDeliveryGoodsId: this.Entity.AgentId,
            ForDeliveryGoodsDes: agentText.trim()
        };
        this.UpdateView2(false, false, "ShipmentDetail");
        this.UpdateView(false, false, "btnIssueInvoice", "btnShippingInstruction", "btnPrint");
    },
    DefaultDetail: function () {
        if (this.EditForm.ShipmentDetail.Id.startsWith("-")) {
            let agent = this.Entity.Agent;
            let agentText = "";
            if (agent) {
                agentText += `${(agent.CompanyName || '')}\n`;
                agentText += `${(agent.Address || '')}\n`;
                agentText += `Tell: ${(agent.PhoneNumber || '')} Fax: ${(agent.Fax || '')}\n`;
                agentText += `Contact person: ${(agent.ContactName || '')}`;
            }
            this.EditForm.ShipmentDetail.ParentId = this.Entity.Id;
            this.EditForm.ShipmentDetail.TypeId = this.Entity.TypeId;
            this.EditForm.ShipmentDetail.PolId = this.Entity.PolId;
            this.EditForm.ShipmentDetail.PodId = this.Entity.PodId;
            this.EditForm.ShipmentDetail.EtaDate = this.Entity.EtaDate;
            this.EditForm.ShipmentDetail.EtdDate = this.Entity.EtdDate;
            this.EditForm.ShipmentDetail.Vessel = `${(this.Entity.Vessel || '')}/${(this.Entity.VesselVoy || '')}`;
            this.EditForm.ShipmentDetail.VesselVoy = this.Entity.VesselVoy;
            this.EditForm.ShipmentDetail.OceanVessel = `${(this.Entity.OceanVessel || '')}/${(this.Entity.OceanVesselVoy || '')}`;
            this.EditForm.ShipmentDetail.OceanVesselVoy = this.Entity.OceanVesselVoy;
            this.EditForm.ShipmentDetail.CommodityId = this.EditForm.ShipmentDetail.CommodityId || this.Entity.CommodityId;
            this.EditForm.ShipmentDetail.PortTransitId = this.EditForm.ShipmentDetail.PortTransitId || this.Entity.PortTransitId;
            this.EditForm.ShipmentDetail.PlaceReceiptId = this.EditForm.ShipmentDetail.PlaceReceiptId || this.Entity.PolId;
            this.EditForm.ShipmentDetail.PlaceDeliveryId = this.EditForm.ShipmentDetail.PlaceDeliveryId || this.Entity.PodId;
            this.EditForm.ShipmentDetail.SaillingDate = this.EditForm.ShipmentDetail.SaillingDate || this.Entity.EtdDate;
            this.EditForm.ShipmentDetail.IssueDate = this.Entity.EtaDate;
            this.EditForm.ShipmentDetail.PlaceIssueId = this.Entity.PolId;
            this.EditForm.ShipmentDetail.ForDeliveryGoodsId = this.Entity.AgentId;
            this.EditForm.ShipmentDetail.ForDeliveryGoodsDes = agentText.trim();
            this.UpdateView2(false, false, "ShipmentDetail", "PolId", "PodId", "EtaDate", "EtdDate"
                , "Vessel", "VesselVoy", "OceanVessel", "OceanVesselVoy", "CommodityId", "PortTransitId", "PlaceReceiptId"
                , "PlaceDeliveryId", "SaillingDate", "IssueDate", "PlaceIssueId", "ForDeliveryGoodsId", "ForDeliveryGoodsDes");
        }
    },
    ViewDetail: async function (com, entity) {
        this.ShipmentDetail = entity;
        if (this.EditForm.ShipmentDetail.BookingLocalId) {
            var serviceBookingLocal = this.GET("ServiceBookingLocal");
            var datas = await serviceBookingLocal.RunQuerys();
            this.EditForm.BookingLocalDetail = datas[0];
        }
        else {
            this.EditForm.BookingLocalDetail = [];
        }
        if (this.Dirty) {
            this.SaveEntity("ShipmentDetail", "Shipment").then(async () => {
                this.ShipmentDetail.Code = this.EditForm.Entity.Code;
                this.UpdateView2(false, false, "ShipmentDetail");
                this.UpdateView(false, false, "btnIssueInvoice", "btnShippingInstruction", "btnPrint", "btnSubmitSI");

            });
        }
        else {
            this.ShipmentDetail.Code = this.EditForm.Entity.Code;
            this.UpdateView2(false, false, "ShipmentDetail");
            this.UpdateView(false, false, "btnIssueInvoice", "btnShippingInstruction", "btnPrint", "btnSubmitSI");
        }
    },
    OnSaved: function () {
        this.ShipmentDetail.ParentId = this.Entity.Id;
        this.ShipmentDetail.TypeId = this.Entity.TypeId;
        this.SaveEntity("ShipmentDetail", "Shipment");
    },
    SetDefaultQuantity: function (com, entity) {
        if (entity.Unit && entity.Unit.IsCBM) {
            entity.Quantity = this.Decimal(this.EditForm.ShipmentDetail.CBM || 1);
        }
        else if (entity.Unit && entity.Unit.IsKGS) {
            entity.Quantity = this.Decimal(this.EditForm.ShipmentDetail.CW || 1);
        }
        else if (entity.Unit && entity.Unit.IsGW) {
            entity.Quantity = this.Decimal(this.EditForm.ShipmentDetail.GW || 1);
        }
        if (entity.Unit && entity.UnitId && entity.UnitId == this.EditForm.ShipmentDetail.UnitId) {
            entity.Quantity = this.Decimal(this.EditForm.ShipmentDetail.Quantity || 1);
        }
        entity.Amount = this.Decimal(entity.Amount || 0);
        entity.TotalAmount = this.Decimal(entity.Quantity || 0).times(entity.Amount || this.Decimal(0));
        if (this.Decimal(entity.Vat || 0).isNegative()) {
            entity.AmountTax = this.Decimal(entity.Vat).abs();
        } else {
            entity.AmountTax = entity.TotalAmount.times(this.Decimal(entity.Vat || 0).div(100));
        }
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.AmountTax);
        com.Parent.UpdateView(false);
    },
    CalcPrice: function (com, entity) {
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.Amount = this.Decimal(entity.Amount || 0);
        entity.TotalAmount = (entity.Quantity || this.Decimal(0)).times(entity.Amount || this.Decimal(0));
        if (this.Decimal(entity.Vat || 0).isNegative()) {
            entity.AmountTax = this.Decimal(entity.Vat).abs();
        } else {
            entity.AmountTax = (entity.TotalAmount).times(this.Decimal(entity.Vat || 0).div(100));
        }
        entity.TotalAmountTax = entity.TotalAmount.plus(entity.AmountTax);
        com.Parent.UpdateView(false);
    },
    CalcUnitPrice: function (com, entity) {
        entity.IsManual = true;
        entity.Quantity = this.Decimal(entity.Quantity || 0);
        entity.TotalAmountTax = this.Decimal(entity.TotalAmountTax || 0);
        if (this.Decimal(entity.Vat || 0).isNegative()) {
            entity.AmountTax = this.Decimal(entity.Vat).abs();
            entity.TotalAmount = entity.TotalAmountTax.minus(entity.AmountTax);
        } else {
            entity.TotalAmount = entity.TotalAmountTax.div(this.Decimal(1).plus(this.Decimal(entity.Vat || 0).div(100)));
            entity.AmountTax = entity.TotalAmount.times(this.Decimal(entity.Vat || 0).div(100));
        }
        entity.Amount = entity.TotalAmount.div(entity.Quantity || this.Decimal(1));
        com.Parent.UpdateView(false);
    },
    Finnish: function (com, entity) {
        entity.FinishDate = this.dayjs();
        com.Parent.UpdateView(false);
    },
    CalcContainerText: function (com, entity) {
        var grid = this.GET("EntityContainer");
        const result = grid.Item.filter(x => x.Entity.ContainerType).reduce((acc, x) => {
            const typeName = x.Entity.ContainerType.Name;
            const quantity = this.Decimal(1);
            if (!acc[typeName]) {
                acc[typeName] = this.Decimal(0);
            }
            acc[typeName] = acc[typeName].add(quantity);
            return acc;
        }, {});
        const summary = Object.keys(result).map(typeName =>
            `${result[typeName]} x ${typeName}`
        ).join(" & ");
        this.Entity.ContainerText = summary;
        this.UpdateView(false, false, "ContainerText");
        if (this.Entity.IsDirectMaster) {
            this.CalcDirectMaster();
        }
    },
    CalcDirectMaster: function () {
        var com = this.GET("EntityContainer")
        if (this.Entity.IsDirectMaster) {
            const result = com.Item.filter(x => x.Entity.ContainerType).reduce((acc, x) => {
                const typeName = x.Entity.ContainerType.Name;
                const quantity = this.Decimal(x.Entity.PKG || 0);
                const qty = this.Decimal(1);
                const cbm = this.Decimal(x.Entity.CBM || 0);
                const gw = this.Decimal(x.Entity.Weight || 0);
                const unit = x.Entity.UnitId;
                if (!acc[typeName]) {
                    acc[typeName] = {
                        PKG: this.Decimal(0),
                        Qty: this.Decimal(0),
                        CBM: this.Decimal(0),
                        Weight: this.Decimal(0),
                        UnitId: null
                    };
                }
                acc[typeName].Qty = acc[typeName].Qty.add(qty);
                acc[typeName].PKG = acc[typeName].PKG.add(quantity);
                acc[typeName].CBM = acc[typeName].CBM.add(cbm);
                acc[typeName].Weight = acc[typeName].Weight.add(gw);
                acc[typeName].UnitId = unit;
                return acc;
            }, {});
            const totalQuantity = Object.keys(result).reduce((acc, typeName) => acc.add(result[typeName].PKG), this.Decimal(0));
            const totalCBM = Object.keys(result).reduce((acc, typeName) => acc.add(result[typeName].CBM), this.Decimal(0));
            const totalGW = Object.keys(result).reduce((acc, typeName) => acc.add(result[typeName].Weight), this.Decimal(0));
            const totalUnit = result[Object.keys(result)[0]] ? result[Object.keys(result)[0]].UnitId : '';
            this.EditForm.Entity.Quantity = totalQuantity;
            this.EditForm.Entity.CBM = totalCBM;
            this.EditForm.Entity.GW = totalGW;
            this.EditForm.Entity.UnitId = totalUnit;
            this.UpdateView(false, false, "Quantity", "CBM", "GW", "UnitId");
        }
    },
    CalcContainerDetailText: function (com, entity) {
        var grid = this.GET("EntityContainer1");
        const result = grid.Item.filter(x => x.Entity.ContainerType).reduce((acc, x) => {
            const typeName = x.Entity.ContainerType.Name + x.Entity.IsPartial;
            const quantity = this.Decimal(x.Entity.PKG || 0);
            const qty = this.Decimal(1);
            const cbm = this.Decimal(x.Entity.CBM || 0);
            const gw = this.Decimal(x.Entity.Weight || 0);
            const unit = x.Entity.UnitId;
            if (!acc[typeName]) {
                acc[typeName] = {
                    PKG: this.Decimal(0),
                    Qty: this.Decimal(0),
                    CBM: this.Decimal(0),
                    Weight: this.Decimal(0),
                    UnitId: null,
                    IsPartial: false
                };
            }
            acc[typeName].Qty = acc[typeName].Qty.add(qty);
            acc[typeName].PKG = acc[typeName].PKG.add(quantity);
            acc[typeName].CBM = acc[typeName].CBM.add(cbm);
            acc[typeName].Weight = acc[typeName].Weight.add(gw);
            acc[typeName].UnitId = unit;
            acc[typeName].IsPartial = x.Entity.IsPartial;
            return acc;
        }, {});
        const totalQuantity = Object.keys(result).reduce((acc, typeName) => acc.add(result[typeName].PKG), this.Decimal(0));
        const totalCBM = Object.keys(result).reduce((acc, typeName) => acc.add(result[typeName].CBM), this.Decimal(0));
        const totalGW = Object.keys(result).reduce((acc, typeName) => acc.add(result[typeName].Weight), this.Decimal(0));
        const totalUnit = result[Object.keys(result)[0]] ? result[Object.keys(result)[0]].UnitId : '';
        this.EditForm.ShipmentDetail.Quantity = totalQuantity;
        this.EditForm.ShipmentDetail.CBM = totalCBM;
        this.EditForm.ShipmentDetail.GW = totalGW;
        this.EditForm.ShipmentDetail.UnitId = totalUnit;
        const summary = Object.keys(result).map(typeName => {
            var data = result[typeName];
            var name = typeName.replaceAll(data.IsPartial.toString().toLowerCase(), "")
            return `${(data.IsPartial ? `A PARTIAL ${name}` : `${data.Qty} x ${name}`)}`;
        }).join(" & ");
        this.EditForm.ShipmentDetail.ContainerText = summary;
        this.EditForm.UpdateView2(false, false, "ShipmentDetail", "ContainerText", "Quantity", "CBM", "GW", "UnitId");
        this.EditForm.CalcTotalParent(this);
    },
    CalcTotalParent: function () {
        var hbl = this.GET("ShipmentDetail");
        var total = JSON.parse(JSON.stringify(hbl.Item.map(x => x.Entity)));
        if (this.EditForm.ShipmentDetail.Id.startsWith("-")) {
            total.push(this.EditForm.ShipmentDetail);
        }
        if (!total || total.length == 0) {
            this.EditForm.Entity.Quantity = null;
            this.EditForm.Entity.CBM = null;
            this.EditForm.Entity.GW = null;
            this.EditForm.Entity.CW = null;
            this.EditForm.Entity.UnitId = null;
        }
        else {
            this.EditForm.Entity.Quantity = total.reduce((acc, x) => acc.add(this.Decimal(x.Quantity || 0)), this.Decimal(0));
            this.EditForm.Entity.CBM = total.reduce((acc, x) => acc.add(this.Decimal(x.CBM || 0)), this.Decimal(0));
            this.EditForm.Entity.GW = total.reduce((acc, x) => acc.add(this.Decimal(x.GW || 0)), this.Decimal(0));
            this.EditForm.Entity.CW = total.reduce((acc, x) => acc.add(this.Decimal(x.CW || 0)), this.Decimal(0));
            this.EditForm.Entity.UnitId = total[0].UnitId;
        }
        this.EditForm.UpdateView(false, false, "Quantity", "CBM", "GW", "CW", "UnitId");
    },
    ProofDelivery: function () {
        var proofDelivery = {
            Id: this.EditForm.ShipmentDetail.ShipmentDOId || this.Uuid7.NewGuid(),
            ShipmentId: this.EditForm.ShipmentDetail.Id,
            TypeId: this.EditForm.ShipmentDetail.TypeId
        }
        this.OpenPopup("proof-of-delivery-editor", proofDelivery, this.EditForm.ShipmentDetail.ShipmentDOId ? true : false);
    },
    ShippingInstruction: function () {
        let agent = this.EditForm.Entity.Agent;
        let agentText = "";
        if (agent) {
            agentText += `${(agent.CompanyName || '')}\n`;
            agentText += `${(agent.Address || '')}\n`;
            agentText += `Tell: ${(agent.PhoneNumber || '')} Fax: ${(agent.Fax || '')}\n`;
            agentText += `Contact person: ${(agent.ContactName || '')}`;
        }
        let customer = this.EditForm.ShipmentDetail.Customer;
        let customerText = "";
        if (customer) {
            customerText += `${(customer.CompanyName || '')}\n`;
            customerText += `${(customer.Address || '')}\n`;
            customerText += `Tell: ${(customer.PhoneNumber || '')} Fax: ${(customer.Fax || '')}\n`;
            customerText += `Contact person: ${(customer.ContactName || '')}`;
        }
        var hbl = this.GET("ShipmentDetail");
        var des = hbl.Item.map(x => x.Entity).filter(x => x.DetailsGoods).map(x => x.DetailsGoods).join("\n");
        var packge = "";
        var unit = this.EditForm.Entity.Unit;
        if (unit) {
            packge = this.EditForm.Entity.Quantity + " " + unit.Name;
        }
        var shippingInstruction = {
            Package: packge,
            Id: this.EditForm.Entity.ShipmentSIId || this.Uuid7.NewGuid(),
            ShipmentId: this.EditForm.Entity.Id,
            BookingNo: this.EditForm.Entity.BookingNo,
            IssueDate: this.dayjs().format('YYYY-MM-DDTHH:mm:ss'),
            LoadingDate: this.EditForm.Entity.EtdDate,
            MblTypeId: this.EditForm.Entity.MblTypeId,
            TypeId: 1,
            PolId: this.EditForm.Entity.PolId,
            PodId: this.EditForm.Entity.PodId,
            PlaceDeliveryId: this.EditForm.Entity.PolId,
            GW: this.EditForm.Entity.GW,
            PIC: this.EditForm.Entity.Vendor ? this.EditForm.Entity.Vendor.ContactName : "",
            CBM: this.EditForm.Entity.CBM,
            FinalDestinationId: this.EditForm.Entity.PodId,
            Vessel: this.EditForm.Entity.Vessel,
            VesselVoy: this.EditForm.Entity.VesselVoy,
            VendorId: this.EditForm.Entity.VendorId,
            RealShipperId: this.EditForm.Entity.IsDirectMaster ? this.EditForm.ShipmentDetail.CustomerId : null,
            RealShipperDes: this.EditForm.Entity.IsDirectMaster ? customerText : null,
            RealConsigneeId: this.EditForm.ShipmentDetail.ConsigneeId || this.EditForm.Entity.AgentId,
            RealConsigneeDes: agentText,
            ConsigneeDes: agentText,
            ConsigneeId: this.EditForm.Entity.AgentId,
            DetailsGoods: des
        }
        var shipmentContainerGrid = this.GET("EntityContainer");
        var newContainer = JSON.parse(JSON.stringify(shipmentContainerGrid.Item.map(x => x.Entity)));
        newContainer.forEach(row => {
            row.Id = this.Uuid7.NewGuid();
            row.EntityId = shippingInstruction.Id;
        });
        shippingInstruction.EntityContainer = newContainer;
        this.OpenPopup("shipping-instruction-editor", shippingInstruction, this.EditForm.Entity.ShipmentSIId ? true : false);
    },
    SubmitSI: function () {
