    LoadShipmentContainer: async function (com) {
        var datas = await com.RunQuerys();
        var entityContainer = datas[0];
        var shipmentContainerGrid = this.GET("EntityContainer");
        var newItems = [];
        var uniqueItems = new Map();
        var newUniqueItems = new Map();
        if (shipmentContainerGrid.Item && shipmentContainerGrid.Item.length > 0) {
            shipmentContainerGrid.Item.map(item => {
                var entity = item.Entity;
                entity.PKG = this.Decimal(0);
                entity.Weight = this.Decimal(0);
                entity.CBM = this.Decimal(0);
                entity.Tare = this.Decimal(0);
                var uniqueKey = `${entity.ContainerTypeId}-${entity.ContainerNo}`;
                uniqueItems.set(uniqueKey, entity);
            });
        }
        entityContainer.forEach(entity => {
            var uniqueKey = `${entity.ContainerTypeId}-${entity.ContainerNo}`;
            if (!uniqueItems.has(uniqueKey)) {
                var newCont = JSON.parse(JSON.stringify(entity));
                newCont.Id = this.Uuid7.NewGuid();
                newCont.EntityId = this.EditForm.Entity.Id;
                newCont.TableName = "ShipmentDetail";
                newUniqueItems.set(uniqueKey, newCont);
            }
            else {
                var existingCont = uniqueItems.get(uniqueKey);
                existingCont.PKG = this.Decimal(existingCont.PKG || 0).add(this.Decimal(entity.PKG || 0));
                existingCont.Weight = this.Decimal(existingCont.Weight || 0).add(this.Decimal(entity.Weight || 0));
                existingCont.CBM = this.Decimal(existingCont.CBM || 0).add(this.Decimal(entity.CBM || 0));
                existingCont.Tare = this.Decimal(existingCont.Tare || 0).add(this.Decimal(entity.Tare || 0));
            }
        });
        newItems = Array.from(newUniqueItems.values());
        shipmentContainerGrid.LoadMasterData(newItems).then(() => {
            shipmentContainerGrid.ClearSelected();
            shipmentContainerGrid.AddRows(newItems, shipmentContainerGrid.Item.length).then(rows => {
                rows.forEach(row => {
                    row.Selected = true;
                });
                shipmentContainerGrid.Dirty = true;
                if (rows[0]) {
                    rows[0].Element.focus();
                }
            });
        });
        shipmentContainerGrid.UpdateView(false, false);
        window.setTimeout(() => {
            this.EditForm.CalcTotalParent(this);
        }, 500);
    },
    LoadShipmentDetailContainer: function () {
        var shipmentContainerGrid = this.GET("EntityContainer");
        var shipmentDetailContainerGrid = this.GET("EntityContainer1");
