    CalcTax: function (gridview) {
        var selecteds = gridview.Item.filter(x => !x.GroupRow && x.Selected);
        var numerator = this.Decimal(5).div(100);
        var denominator = this.Decimal(1).minus(numerator);
        var vat = numerator.div(denominator);
        for (var itemView of selecteds) {
            itemView.Entity.TotalAmountTax = this.Decimal(itemView.Entity.TotalAmountTax || 0);
            itemView.Entity.AmountTax = itemView.Entity.TotalAmountTax.times(vat).div(vat.plus(1)).toDecimalPlaces(0);
            itemView.Entity.Amount = itemView.Entity.TotalAmountTax.sub(itemView.Entity.AmountTax);
            itemView.Entity.Vat = itemView.Entity.AmountTax.negated();
            itemView.Entity.IsManual = true;
            itemView.UpdateView(false);
            itemView.Dirty = true;
        }
        window.clearTimeout(this.EditForm.IntUpdateFee);
        this.EditForm.IntUpdateFee = window.setTimeout(() => {
            this.ReloadSummary();
        }, 500)
    },
    DuplicateFee: function (gridview) {
        var newItem = JSON.parse(JSON.stringify(gridview.Header.filter(x => x.FieldName == "VendorId" || x.FieldName == "IsNoDocs")));
        var costFee = gridview.EditForm.ChildCom.find(x => x.Parent.Meta.Label == "Costing");
        var revenue = gridview.EditForm.ChildCom.find(x => x.Parent.Meta.Label == "Revenue");
        if (gridview.Meta.Id == costFee.Meta.Id) {
            if (!this.Token.RoleNames.some(x => x.toUpperCase() == "CRUD REVENUE")) {
                newItem = newItem.filter(x => x.FieldName == "VendorId");
            }
            gridview.EditForm.ShipmentDetail.ViewIsBuying = false;
        }
        else {
            if (!this.Token.RoleNames.some(x => x.toUpperCase() == "CRUD COSTING")) {
                gridview.EditForm.ShipmentDetail.ViewIsBuying = false;
                newItem = newItem.filter(x => x.FieldName == "VendorId");
            }
            else {
                gridview.EditForm.ShipmentDetail.ViewIsBuying = true;
            }
        }
        newItem.forEach(item => {
            if (item.FieldName == "IsNoDocs") {
                item.FieldName = "IsBuying";
                item.Label = "Buying";
            }
            item.FieldName = "View" + item.FieldName;
        })
        var form = gridview.EditForm.OpenConfig("Please select Payer/Payee!", () => {
            if (!form.EditForm.ShipmentDetail.ViewVendorId) {
