                    f.Amount = null;
                    f.Vat = null;
                    f.TotalAmount = null;
                    f.AmountTax = null;
                    f.TotalAmountTax = null;
                    f.IsLock = false;
                    f.ParentId = null;
                    this.EditForm.ResetFee(f);
                });
                costFee.LoadMasterData(newFees).then(() => {
                    costFee.ClearSelected();
                    costFee.AddRows(newFees).then(rows => {
                        rows.forEach(row => {
                            row.Selected = true;
                        });
                        if (rows[0]) {
                            rows[0].Element.focus();
                        }
                    });
                });
            }
            else {
                newFees.forEach(f => {
                    f.Id = form.EditForm.Uuid7.NewGuid();
                    f.VendorId = form.EditForm.ShipmentDetail.ViewVendorId;
                    f.TypeId = 1;
                    f.Amount = null;
                    f.Vat = null;
                    f.TotalAmount = null;
                    f.AmountTax = null;
                    f.TotalAmountTax = null;
                    f.IsLock = false;
                    f.ParentId = null;
                    this.EditForm.ResetFee(f);
                });
                revenue.LoadMasterData(newFees).then(() => {
                    revenue.ClearSelected();
                    revenue.AddRows(newFees).then(rows => {
                        rows.forEach(row => {
                            row.Selected = true;
                        });
                        if (rows[0]) {
                            rows[0].Element.focus();
                        }
                    });
                });
            }
        }, () => { }, true, newItem);
    },
