            var transitGridMbl = this.GET("EntityTransit");
            var containerMbl = containerGridMbl.Item.map(x => x.Entity);
            var transitMbl = transitGridMbl.Item.map(x => x.Entity);
            mbl.Id = this.Uuid7.NewGuid();
            mbl.InsertedBy = this.Token.UserId;
            mbl.Code = null;
            mbl.SeqKey = null;
            mbl.SeqDate = null;
            var datas = await com.RunQuerys();
            var hbl = datas[0];
            var fee = datas[1];
            var containerHbl = datas[2];
            await this.SubmitObject(mbl, 'Shipment');
            if (containerMbl && containerMbl.length > 0) {
                for (var item of containerMbl) {
                    item.Id = this.Uuid7.NewGuid();
                    item.EntityId = mbl.Id.slice(1);
                    item.InsertedBy = this.Token.UserId;
                    await this.SubmitObject(item, 'EntityContainer');
                }
            }
            if (transitMbl && transitMbl.length > 0) {
                for (var item of transitMbl) {
                    item.Id = this.Uuid7.NewGuid();
                    item.EntityId = mbl.Id.slice(1);
                    item.InsertedBy = this.Token.UserId;
                    await this.SubmitObject(item, 'EntityTransit');
                }
            }
            if (hbl && hbl.length > 0) {
                for (var item of hbl) {
                    var hblFee = fee.filter(f => f.ShipmentId == item.Id);
                    var hblContainer = containerHbl.filter(f => f.EntityId == item.Id);
                    item.Code = null;
                    item.HblNo = null;
                    item.SeqKey = null;
                    item.SeqDate = null;
                    item.ParentId = mbl.Id.slice(1);
                    item.InsertedBy = this.Token.UserId;
                    await this.SubmitObject(item, 'Shipment');
                    if (hblFee && hblFee.length > 0) {
                        for (var f of hblFee) {
                            f.Id = this.Uuid7.NewGuid();
                            f.ShipmentId = item.Id.slice(1);
                            f.IsLock = false;
                            f.ParentId = null;
                            this.EditForm.ResetFee(f);
                            await this.SubmitObject(f, 'ShipmentFee');
                        }
                    }
                    if (hblContainer && hblContainer.length > 0) {
                        for (var f of hblContainer) {
                            f.Id = this.Uuid7.NewGuid();
                            f.EntityId = item.Id.slice(1);
                            f.InsertedBy = this.Token.UserId;
                            await this.SubmitObject(f, 'EntityContainer');
                        }
                    }
                }
            }
            hblGrid.ReloadData().then(() => {
                this.CalcTotalParent(this);
                this.EditForm.Dirty = true;
                this.EditForm.SavePatch().then(x => {
                    var grid = this.EditForm.Parent.FirstCom("Shipment");
                    grid.ReloadData();
                    this.EditForm.Dirty = false;
                    this.Toast.Success("Split hbl success!");
                    this.EditForm.Parent.OpenPopup("sea-export-shipment-editor", { Id: mbl.Id.slice(1) }, true);
                    this.EditForm.DirtyCheckAndCancel();
                });
            });

        }
        else {
            var rs = await this.EditForm.Client.PostAsync({ ShipmentId: this.EditForm.Entity.IsNew ? null : this.EditForm.Entity.NewShipmentId, ShipmentDetailId: hbl.map(x => x.Id) }, "/api/MoveHBL");
            if (rs) {
                hblGrid.ApplyFilter().then(() => {
                    this.CalcTotalParent(this);
                    this.EditForm.Dirty = true;
                    this.EditForm.SavePatch();
                });
            }
        }
    },
