Components.Query:
var revenueGrid = this.EditForm.FirstCom("ShipmentFee");
var costingGrid = this.EditForm.FirstCom("ShipmentFee2");
var advGrid = this.EditForm.FirstCom("ShipmentFee3");
var estRevenue = this.EditForm.BookingLocalDetail.filter(x => !x.IsBuying && !x.IsAdvCustomer && !x.IsAdvProvider && !x.IsObh && x.CurrencyId);
var estCost = this.EditForm.BookingLocalDetail.filter(x => x.IsBuying && !x.IsAdvCustomer && !x.IsAdvProvider && !x.IsObh && x.CurrencyId);
var revenue = revenueGrid.Item.map(x => x.Entity).filter(x => !x.ObhId && !x.ParentId && x.CurrencyId);
var adv = advGrid.Item.map(x => x.Entity).filter(x => !x.IsAdvCustomer && !x.IsAdvProvider && x.CurrencyId);
var costing = costingGrid.Item.map(x => x.Entity).concat(adv).filter(x => !x.ObhId && !x.ParentId && x.CurrencyId);
var vnd = {
    CurrencyId: "VND",
    EstimateRevenue: (estRevenue.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount || 0).times(b.ExchangeRateVND) || 0), this.Decimal(0))),
    ActualRevenue: (revenue.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount || 0).times(b.ExchangeRateVND) || 0), this.Decimal(0))),
    EstimateCost: (estCost.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount || 0).times(b.ExchangeRateVND) || 0), this.Decimal(0))),
    ActualCost: (costing.reduce((a, b) => a.plus(this.Decimal(b.TotalAmount || 0).times(b.ExchangeRateVND) || 0), this.Decimal(0))),
    EstimateProfit: 0,
    EstimatePerProfit: 0,
    ActualProfit: 0,
    ActualPerProfit: 0
}
vnd.ActualProfit = vnd.ActualRevenue.sub(vnd.ActualCost);
vnd.ActualPerProfit = vnd.ActualRevenue.eq(0) ? this.Decimal(0) : (vnd.ActualProfit.div(vnd.ActualRevenue || this.Decimal(1))).times(this.Decimal(100));
vnd.EstimateProfit = vnd.EstimateRevenue.sub(vnd.EstimateCost);
vnd.EstimatePerProfit = vnd.ActualRevenue.eq(0) ? this.Decimal(0) : (vnd.EstimateProfit.div(vnd.EstimateRevenue || this.Decimal(1))).times(this.Decimal(100));
var usd = {
    CurrencyId: "USD",
    EstimateRevenue: (estRevenue.reduce((a, b) => {
        let rate = this.Decimal(b.ExchangeRateUSD);
        return a.plus(rate.eq(0) ? this.Decimal(0) : this.Decimal(b.TotalAmount || 0)['times'](rate));
    }, this.Decimal(0))),
    ActualRevenue: (revenue.reduce((a, b) => {
        let rate = this.Decimal(b.ExchangeRateUSD);
        return a.plus(rate.eq(0) ? this.Decimal(0) : this.Decimal(b.TotalAmount || 0)['times'](rate));
    }, this.Decimal(0))),
    EstimateCost: (estCost.reduce((a, b) => {
        let rate = this.Decimal(b.ExchangeRateUSD);
        return a.plus(rate.eq(0) ? this.Decimal(0) : this.Decimal(b.TotalAmount || 0)['times'](rate));
    }, this.Decimal(0))),
    ActualCost: (costing.reduce((a, b) => {
        let rate = this.Decimal(b.ExchangeRateUSD);
        return a.plus(rate.eq(0) ? this.Decimal(0) : this.Decimal(b.TotalAmount || 0)['times'](rate));
    }, this.Decimal(0))),
    EstimateProfit: 0,
    EstimatePerProfit: 0,
    ActualProfit: 0,
    ActualPerProfit: 0
};

usd.ActualProfit = usd.ActualRevenue.sub(usd.ActualCost);
usd.ActualPerProfit = usd.ActualRevenue.eq(0) ? this.Decimal(0) : (usd.ActualProfit.div(usd.ActualRevenue || this.Decimal(1))).times(this.Decimal(100));
usd.EstimateProfit = usd.EstimateRevenue.sub(usd.EstimateCost);
usd.EstimatePerProfit = usd.EstimateRevenue.eq(0) ? this.Decimal(0) : (usd.EstimateProfit.div(usd.EstimateRevenue || this.Decimal(1))).times(this.Decimal(100));
return [vnd, usd];

var revenueGrid = this.EditForm.FirstCom("ShipmentFee");
var costingGrid = this.EditForm.FirstCom("ShipmentFee2");
var advGrid = this.EditForm.FirstCom("ShipmentFee3");
var feeItems = this.EditForm.BookingLocalDetail.filter(x => !x.IsBuying && !x.IsAdvCustomer && !x.IsAdvProvider && !x.IsObh && x.CurrencyId);
var feeItems2 = this.EditForm.BookingLocalDetail.filter(x => x.IsBuying && !x.IsAdvCustomer && !x.IsAdvProvider && !x.IsObh && x.CurrencyId);
var revenue = revenueGrid.Item.map(x => x.Entity).filter(x => !x.ObhId && !x.ParentId && x.CurrencyId);
var adv = advGrid.Item.map(x => x.Entity).filter(x => !x.IsAdvCustomer && !x.IsAdvProvider && x.CurrencyId);
var costing = costingGrid.Item.map(x => x.Entity).concat(adv).filter(x => !x.ObhId && !x.ParentId && x.CurrencyId);
// Hm tnh tng hp theo DescriptionId (bao gm c Estimate v Actual)
function groupByDescriptionIdAndCurrency(data, isEstimate = false, exchangeRates = {}) {
    return data.reduce((acc, curr) => {
        const key = `${curr.DescriptionId}`; // Ghp DescriptionId v CurrencyId thnh kha duy nht
        if (!acc[key]) {
            acc[key] = {
                Estimate: this.Decimal(0),
                Actual: this.Decimal(0)
            };
        }

        // Ly t gi: u tin t exchangeRates hoc t chnh curr.ExchangeRateVND
        const exchangeRate = this.Decimal(curr.ExchangeRateVND || 0);

        if (isEstimate) {
            // Tng hp Estimate
            acc[key].Estimate = acc[key].Estimate.plus(
                this.Decimal(curr.TotalAmount || 0).times(exchangeRate)
            );
        } else {
            // Tng hp Actual
            acc[key].Actual = acc[key].Actual.plus(
                this.Decimal(curr.TotalAmount || 0).times(exchangeRate)
            );
        }

        return acc;
    }, {});
}

// 1. Tng hp doanh thu (revenueGrouped) v lu li ExchangeRateVND
var revenueGrouped = groupByDescriptionIdAndCurrency.call(this, revenue, false); // Actual Revenue

// To danh sch t gi t revenueGrouped
var exchangeRates = Object.keys(revenueGrouped).reduce((acc, key) => {
    acc[key] = this.Decimal(revenue.find(r => `${r.DescriptionId}` === key).ExchangeRateVND || 0);
    return acc;
}, {});

// 2. Tng hp chi ph v Estimate s dng t gi t revenueGrouped
var costingGrouped = groupByDescriptionIdAndCurrency.call(this, costing, false, exchangeRates); // Actual Cost
var estRevenueGrouped = groupByDescriptionIdAndCurrency.call(this, feeItems, true, exchangeRates); // Estimate Revenue
var estCostingGrouped = groupByDescriptionIdAndCurrency.call(this, feeItems2, true, exchangeRates); // Estimate Cost

// 3. Kt hp d liu revenue v costing
var result = Object.keys({ ...revenueGrouped, ...costingGrouped, ...estRevenueGrouped, ...estCostingGrouped }).map(key => {
    const [descriptionId] = key.split('_');

    const revenueData = revenueGrouped[key] || { Estimate: this.Decimal(0), Actual: this.Decimal(0) };
    const costingData = costingGrouped[key] || { Estimate: this.Decimal(0), Actual: this.Decimal(0) };
    const estRevenueData = estRevenueGrouped[key] || { Estimate: this.Decimal(0), Actual: this.Decimal(0) };
    const estCostingData = estCostingGrouped[key] || { Estimate: this.Decimal(0), Actual: this.Decimal(0) };

    // Tnh ton li nhun
    const estimateProfit = estRevenueData.Estimate.minus(estCostingData.Estimate);
    const actualProfit = revenueData.Actual.minus(costingData.Actual);

    // Tnh phn trm li nhun
    const estimatePerProfit = !estRevenueData.Estimate.isZero()
        ? estimateProfit.dividedBy(estRevenueData.Estimate).times(this.Decimal(100))
        : this.Decimal(0);
    const actualPerProfit = !revenueData.Actual.isZero()
        ? actualProfit.dividedBy(revenueData.Actual).times(this.Decimal(100))
        : this.Decimal(0);

    return {
        DescriptionId: descriptionId,
        EstimateRevenue: estRevenueData.Estimate,
        ActualRevenue: revenueData.Actual,
        EstimateCost: estCostingData.Estimate,
        ActualCost: costingData.Actual,
        EstimateProfit: estimateProfit,
        EstimatePerProfit: estimatePerProfit,
        ActualProfit: actualProfit,
        ActualPerProfit: actualPerProfit,
    };
});

return result;

