return {
    OnSave: function (form, gridview) {
        var detailFee = this.GET("PricingDetail");
        if (detailFee && detailFee.AllListViewItem.length > 0) {
            detailFee.AllListViewItem.filter(x => !x.GroupRow).forEach((item, index) => {
                item.Entity.Order = index;
            });
            var text20 = [];
            var text40 = [];
            var textOther = [];
            detailFee.AllListViewItem.filter(x => x.GroupRow).forEach((item, index) => {
                if (item.GroupText.textContent.includes('2')) {
                    text20.push(item.GroupText.textContent + ' : ' + item.Children.find(x => x.Element.textContent != "").Element.textContent)
                }
                else if (item.GroupText.textContent.includes('4')) {
                    text40.push(item.GroupText.textContent + ' : ' + item.Children.find(x => x.Element.textContent != "").Element.textContent)
                } else {
                    textOther.push(item.GroupText.textContent + ' : ' + item.Children.find(x => x.Element.textContent != "").Element.textContent)
                }
            });
            this.Entity.Cont20Text = text20.join(';');
            this.Entity.Cont40Text = text40.join(';');
            this.Entity.ContText = textOther.join(';');
        }
    },
    UpdateParent: function (form, gridview) {
        if (this.OpenFrom.Meta.Name == "sea-inquiry-detail-editor") {
            var pricing = this.OpenFrom.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "Pricing");
            pricing.ClearSelected();
            pricing.ActionFilter();
        }
    },
    SaveAs: async function (btn) {
        var newEntity = JSON.parse(JSON.stringify(this.Entity));
        this.Entity = newEntity;
        this.Entity.Id = this.Uuid7.NewGuid();
        this.Entity.Code = null;
        this.Entity.InsertedBy = this.Token.UserId;
        this.Entity.InsertedByMasterData = null;
        this.Entity.StatusId = 1;
        var inquiryDetail = this.GET("PricingDetail");
        if (inquiryDetail && inquiryDetail.AllListViewItem.length > 0) {
            inquiryDetail.AllListViewItem.forEach(row => {
                row.Entity.Id = this.Uuid7.NewGuid();
                row.Entity.PricingId = this.Entity.Id;
                row.Children.forEach(cell => {
                    cell.Entity = row.Entity;
                });
                row.UpdateView(true);
            });
        }
        this.UpdateView(true);
        this.Dirty = true;
        this.Toast.Success("Save as success");
    },
    CheckCurrency: function (com, entity) {
        com.Parent.UpdateView(false);
    },
    OnSaveAdd: function (form, gridview) {
        this.Entity = {
            Id: this.Uuid7.NewGuid(),
            Code: null,
            StatusId: 1
        }
    },
    BeforeCreated: function (rowSection, entity) {
        var inquiryDetail = this.GET("PricingDetail");
        var first = inquiryDetail.AllListViewItem[0];
        if (inquiryDetail.AllListViewItem && inquiryDetail.AllListViewItem.length == 0) {
            return;
        }
        var lastItem = inquiryDetail.AllListViewItem[inquiryDetail.AllListViewItem.length - 1]
        entity.UnitId = entity.UnitId == null ? lastItem.Entity.UnitId || '' : entity.UnitId;
    },
    AddDuplicateMenu: function (form, gridview) {
        gridview.MenuItems.push({
            Icon: "fal fa-folders",
            Text: "Duplicate",
            Click: () => this.DuplicateFee(form, gridview)
        });
    },
    DuplicateFee: function (form, gridview) {
        var pricingDetail = this.GET("PricingDetail");
        var form = this.EditForm.OpenConfig("Select group unit type!", () => {
            var selecteds = pricingDetail.AllListViewItem.filter(x => !x.GroupRow && x.Selected);
            var newFees = JSON.parse(JSON.stringify(selecteds.map(x => x.Entity)));
            newFees.forEach(item => {
                item.Id = this.Uuid7.NewGuid();
                item.PricingId = this.Entity.Id;
                item.UnitId = form.Entity.UnitId;
                item.__groupkey__ = form.Entity.UnitId;
            });
            pricingDetail.LoadMasterData(newFees).then(() => {
                pricingDetail.ClearSelected();
                pricingDetail.AddRows(newFees).then(rows => {
                    rows.forEach(row => {
                        row.Selected = true;
                    });
                    rows[0].Element.focus();
                });
            });
        }, () => { }, true, pricingDetail.Header.filter(x => x.FieldName == "UnitId"));
    },
    UpdateGroupFee: function (data) {
        data.Parent.Entity.GroupFee = (data.Entity.Description.Group ? data.Entity.Description.Group.Name : 'Local charge');
        var pricingDetail = this.GET("PricingDetail");
        if (data.Matched) {
            data.Parent.Entity.IsFreight = data.Matched.IsFreight;
            data.Parent.Entity.OtherUnitId = data.Matched.OtherUnitId;
            data.Parent.Entity.CurrencyId = data.Matched.CurrencyId;
            data.Parent.Entity.IsObh = data.Matched.IsObh;
        }
        pricingDetail.LoadMasterData([data.Parent.Entity]).then(() => {
            data.Parent.UpdateView(false, false, "GroupFee", "OtherUnitId", "CurrencyId", "IsObh");
        });
    },
    CreateQuote: async function (com, entity) {
        var gridView = this.GET("PricingDetail");
        if (gridView.AllListViewItem.length == 0) {
            this.Toast.Warning("Please add pricing fee details!");
            return;
        }
        var pricing = this.Entity;
        if (!pricing.Pol || !pricing.Pod) {
            this.Toast.Warning("Please choose termial!");
            return;
        }
        var selecteds = gridView.AllListViewItem.filter(x => !x.GroupRow).map(x => x.Entity);
        var newFees = JSON.parse(JSON.stringify(selecteds));
        var typeId = 2;
        var name = "sea-quote-editor";
        typeId = 2;
        var quote = {
            Id: this.Uuid7.NewGuid(),
            PricingId: pricing.Id,
            InquiryDetailId: pricing.Id,
            StatusId: this.SalesFunction["APPROVE_QUOTATION"] ? 3 : 1,
            NoApproved: this.SalesFunction["APPROVE_QUOTATION"] ? true : false,
            TypeId: typeId,
            VoucherTypeId: 2,
            FromDate: pricing ? pricing.FromDate : null,
            ToDate: pricing ? pricing.ToDate : null,
            ServiceDate: this.dayjs().format('YYYY-MM-DD'),
            AgentId: (pricing && pricing.Vendor) ? (pricing.Vendor.PartnerTypeIdsText && pricing.Vendor.PartnerTypeIdsText.includes('Agent')
                && pricing.Vendor.ResidenceTypeIdText && pricing.Vendor.ResidenceTypeIdText.toUpperCase().includes('OVERSEA') ? pricing.VendorId : null) : null,
            CustomerId: null,
            CommodityId: pricing.CommodityId,
            IncotermId: pricing.IncotermId,
            DeliveryTerm: pricing.DeliveryTerm,
            Quantity: pricing.Quantity,
            Note: pricing.Note,
            UnitId: pricing.UnitId,
            DeminsionText: pricing.DeminsionText,
            CW: pricing.CW,
            GW: pricing.GW,
            CBM: pricing.CBM,
            Note: pricing.Note,
            ServiceId: 11,
            PolId: pricing.PolId,
            PodId: pricing.PodId,
            DemFree: pricing.DemFree,
            DetFree: pricing.DetFree,
            PkEmptyId: pricing.PkEmptyId,
            ReturnsId: pricing.ReturnsId,
            IsSimple: false
        };
        var config = JSON.parse(localStorage.getItem("SalesFunction"));
        var containerGrid = this.OpenFrom.GET("EntityContainer");
        newFees.forEach(item => {
            item.Id = this.Uuid7.NewGuid();
            item.DescriptionId = item.DescriptionId;
            item.InquiryId = quote.Id;
            item.RouteId = `${(pricing.Pol.Code || '')} => ${(pricing.Pod.Code || '')}`;
            item.Route = {
                Name: item.RouteId,
                Id: item.RouteId,
            };
            item.Cost = item.UnitPrice;
            item.MinLCLCost = item.MinUnitPrice;
            item.MinQuantityCost = item.MinUnitPrice;
            if (item.IsObh) {
                item.MinLCLRate = item.MinLCLCost;
                item.Rate = item.Cost;
                item.MinQuantityRate = item.MinQuantityCost;
            }
            item.PolId = pricing.PolId;
            item.PodId = pricing.PodId;
            item.FinalDestinationId = pricing.PodId;
            item.PlaceDeliveryId = pricing.PodId;
            item.PlaceReceiptId = pricing.PolId;
            item.PayerId = item.VendorId != pricing.VendorId ? item.VendorId : null;
            item.DisableRow = true;
            item.OtherFee = true;
        });
        quote.InquiryDetail = [{
            Id: this.Uuid7.NewGuid(),
            InquiryId: quote.Id,
            OtherFee: false,
            PolId: pricing.PolId,
            PodId: pricing.PodId,
            FinalDestinationId: pricing.PodId,
            PlaceDeliveryId: pricing.PodId,
            PlaceDeliveryId: pricing.PodId,
            ScheduleIds: pricing ? pricing.ScheduleIds : null,
            VendorId: pricing ? pricing.VendorId : null,
            TransitTime: pricing ? pricing.TransitTime : null,
            ViaId: pricing ? pricing.ViaId : null,
            CarrierId: pricing ? pricing.CarrierId : null,
            PlaceReceiptId: pricing.PolId,
            DisableRow: true,
        }];
        quote.DefaultFee = newFees;
        this.OpenPopup(name, quote);
    }
}

