        LCL: ((item.Unit.Name.includes("LCL") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),

        Cont40USD: ((item.Unit.Name.includes('40') && !item.Unit.Name.includes("40'HC") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        Cont20USD: ((item.Unit.Name.includes('20') ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        Cont45USD: ((item.Unit.Name.includes('45') > 0 ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        Cont40HCUSD: ((item.Unit.Name.includes("40'HC") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        MinLCLUSD: ((new this.Decimal(item.MinLCLRate || 0)).times(item.ExchangeRateUSD || 1)),
        LCLUSD: ((item.Unit.Name.includes("LCL") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
    }
    if (!groupedItems[key]) {
        groupedItems[key] = newItem;
    }
    else {
        groupedItems[key].Cont40 = groupedItems[key].Cont40.plus(newItem.Cont40);
        groupedItems[key].Cont20 = groupedItems[key].Cont20.plus(newItem.Cont20);
        groupedItems[key].Cont45 = groupedItems[key].Cont45.plus(newItem.Cont45);
        groupedItems[key].Cont40HC = groupedItems[key].Cont40HC.plus(newItem.Cont40HC);
        groupedItems[key].MinLCL = groupedItems[key].MinLCL.plus(newItem.MinLCL);
        groupedItems[key].LCL = groupedItems[key].LCL.plus(newItem.LCL);
        groupedItems[key].Cont40USD = groupedItems[key].Cont40USD.plus(newItem.Cont40USD);
        groupedItems[key].Cont20USD = groupedItems[key].Cont20USD.plus(newItem.Cont20USD);
        groupedItems[key].Cont45USD = groupedItems[key].Cont45USD.plus(newItem.Cont45USD);
        groupedItems[key].Cont40HCUSD = groupedItems[key].Cont40HCUSD.plus(newItem.Cont40HCUSD);
        groupedItems[key].MinLCLUSD = groupedItems[key].MinLCLUSD.plus(newItem.MinLCLUSD);
        groupedItems[key].LCLUSD = groupedItems[key].LCLUSD.plus(newItem.LCLUSD);
    }

});
var result = Object.values(groupedItems).map(item => {
    return {
        PolId: item.PolId,
        Pol: item.Pol,
        PodId: item.PodId,
        Pod: item.Pod,
        VendorId: item.VendorId,
        Vendor: item.Vendor,
        Cont40: item.Cont40,
        Cont20: item.Cont20,
        Cont45: item.Cont45,
        Cont40HC: item.Cont40HC,
        LCL: item.LCL,
        MinLCL: item.MinLCL,
        Cont40USD: item.Cont40USD,
        Cont20USD: item.Cont20USD,
        Cont45USD: item.Cont45USD,
        Cont40HCUSD: item.Cont40HCUSD,
        LCLUSD: item.LCLUSD,
        MinLCLUSD: item.MinLCLUSD,
        PlaceReceiptId: item.PlaceReceiptId,
        PlaceReceipt: item.PlaceReceipt,
        PlaceDeliveryId: item.PlaceDeliveryId,
        PlaceDelivery: item.PlaceDelivery,
        FinalDestinationId: item.FinalDestinationId,
        FinalDestination: item.FinalDestination,
    };
});

return result;

