var detail = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
var fee = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "DefaultFee");
var listItem = fee.AllListViewItem.filter(x => !x.GroupRow && !x.Entity.IsObh && (x.Entity.Rate || x.Entity.Cost)).map(x => x.Entity);
var groupedItems = {};
listItem.forEach(item => {
    var key = `${item.PolId}-${item.PodId}`;
    var sumDetail = detail.AllListViewItem.map(x => x.Entity).find(x => x.PolId == item.PolId && x.PodId == item.PodId);
    var allUnitPrice = item.Unit.Name.includes('ALL') ? new this.Decimal(item.Rate || 0) : new this.Decimal(0);
    var newItem = {
        PolId: sumDetail.PolId,
        Pol: sumDetail.Pol,
        PodId: sumDetail.PodId,
        Pod: sumDetail.Pod,
        VendorId: sumDetail.VendorId,
        Vendor: sumDetail.Vendor,
        Cont40: ((item.Unit.Name.includes('40') && !item.Unit.Name.includes("40'HC") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),
        Cont20: ((item.Unit.Name.includes('20') ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),
        Cont45: ((item.Unit.Name.includes('45') > 0 ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),
        Cont40HC: ((item.Unit.Name.includes("40'HC") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),
        LTL: ((["CBM", "KGS"].includes(item.Unit.Name) ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),
        FTL: ((item.Unit.Name.endsWith("T") && !item.Unit.IsContainer ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateVND || 1)).plus(allUnitPrice.times(item.ExchangeRateVND || 1)),

        Cont40USD: ((item.Unit.Name.includes('40') && !item.Unit.Name.includes("40'HC") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        Cont20USD: ((item.Unit.Name.includes('20') ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        Cont45USD: ((item.Unit.Name.includes('45') > 0 ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        Cont40HCUSD: ((item.Unit.Name.includes("40'HC") ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        LTLUSD: ((["CBM", "KGS"].includes(item.Unit.Name) ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
        FTLUSD: ((item.Unit.Name.endsWith("T") && !item.Unit.IsContainer ? new this.Decimal(item.Rate || 0) : new this.Decimal(0)).times(item.ExchangeRateUSD || 1)).plus(allUnitPrice.times(item.ExchangeRateUSD || 1)),
    }
    if (!groupedItems[key]) {
        groupedItems[key] = newItem;
    }
    else {
        groupedItems[key].Cont40 = groupedItems[key].Cont40.plus(newItem.Cont40);
        groupedItems[key].Cont20 = groupedItems[key].Cont20.plus(newItem.Cont20);
        groupedItems[key].Cont45 = groupedItems[key].Cont45.plus(newItem.Cont45);
        groupedItems[key].Cont40HC = groupedItems[key].Cont40HC.plus(newItem.Cont40HC);
        groupedItems[key].LTL = groupedItems[key].LTL.plus(newItem.LTL);
        groupedItems[key].FTL = groupedItems[key].FTL.plus(newItem.FTL);
        groupedItems[key].Cont40USD = groupedItems[key].Cont40USD.plus(newItem.Cont40USD);
        groupedItems[key].Cont20USD = groupedItems[key].Cont20USD.plus(newItem.Cont20USD);
        groupedItems[key].Cont45USD = groupedItems[key].Cont45USD.plus(newItem.Cont45USD);
        groupedItems[key].Cont40HCUSD = groupedItems[key].Cont40HCUSD.plus(newItem.Cont40HCUSD);
        groupedItems[key].LTLUSD = groupedItems[key].LTLUSD.plus(newItem.LTLUSD);
        groupedItems[key].FTLUSD = groupedItems[key].FTLUSD.plus(newItem.FTLUSD);
    }

});
var result = Object.values(groupedItems).map(item => {
    return {
        PolId: item.PolId,
        Pol: item.Pol,
        PodId: item.PodId,
        Pod: item.Pod,
        VendorId: item.VendorId,
        Vendor: item.Vendor,
        Cont40: item.Cont40,
        Cont20: item.Cont20,
        Cont45: item.Cont45,
        Cont40HC: item.Cont40HC,
        LTL: item.LTL,
        FTL: item.FTL,
        Cont40USD: item.Cont40USD,
        Cont20USD: item.Cont20USD,
        Cont45USD: item.Cont45USD,
        Cont40HCUSD: item.Cont40HCUSD,
        LTLUSD: item.LTLUSD,
        FTLUSD: item.FTLUSD
    };
});

return result;

