var detail = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "DefaultFee");
var inquiryDetail = this.EditForm.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "InquiryDetail");
var listItem = detail.AllListViewItem.filter(x => !x.GroupRow).map(x => x.Entity);
var sellingRates = listItem.filter(x => x.Rate && !x.IsObh);
var buyingRates = listItem.filter(x => x.Cost && !x.IsObh);
var routes = inquiryDetail.AllListViewItem.filter(x => !x.GroupRow).map(x => x.Entity);
var datas = [];
routes.forEach(item => {
    var sellingRate = sellingRates.filter(x => x.PolId == item.PolId && x.PodId == item.PodId);
    var buyingRate = buyingRates.filter(x => x.PolId == item.PolId && x.PodId == item.PodId);
    var vnd = {
        PolId: item.PolId,
        Pol: item.Pol,
        PodId: item.PodId,
        Pod: item.Pod,
        CurrencyText: "VND",
        RevenueAll: (sellingRate.filter(x => x.Unit.Name == 'ALL').reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        CostAll: (buyingRate.filter(x => x.Unit.Name == 'ALL').reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        RevenueFTL: (sellingRate.filter(x => x.Unit.Name.endsWith("T") && !item.Unit.IsContainer).reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        CostFTL: (buyingRate.filter(x => x.Unit.Name.endsWith("T") && !item.Unit.IsContainer).reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        RevenueLTL: (sellingRate.filter(x => ["CBM", "KGS"].includes(x.Unit.Name)).reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        CostLTL: (buyingRate.filter(x => ["CBM", "KGS"].includes(x.Unit.Name)).reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Revenue20: (sellingRate.filter(x => x.Unit.Name.includes('20')).reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Cost20: (buyingRate.filter(x => x.Unit.Name.includes('20')).reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Revenue40: (sellingRate.filter(x => x.Unit.Name.includes('40') && !x.Unit.Name.includes("40'HC")).reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Cost40: (buyingRate.filter(x => x.Unit.Name.includes('40') && !x.Unit.Name.includes("40'HC")).reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Revenue40HC: (sellingRate.filter(x => x.Unit.Name.includes("40'HC")).reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Cost40HC: (buyingRate.filter(x => x.Unit.Name.includes("40'HC")).reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Revenue45: (sellingRate.filter(x => x.Unit.Name.includes('45')).reduce((a, b) => a.plus(this.Decimal(b.Rate || 0).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
        Cost45: (buyingRate.filter(x => x.Unit.Name.includes('45')).reduce((a, b) => a.plus(this.Decimal((b.Cost || 0)).times(b.ExchangeRateVND || 1).times(b.Quantity || 1) || 0), this.Decimal(0))),
    }

    vnd.RevenueFTL = vnd.RevenueFTL.plus(vnd.RevenueAll);
    vnd.CostFTL = vnd.CostFTL.plus(vnd.CostAll);
    vnd.ProfitFTL = vnd.RevenueFTL.sub(vnd.CostFTL);
    vnd.ProfitRateFTL = (vnd.ProfitFTL.div(vnd.RevenueFTL || this.Decimal(1))).times(this.Decimal(100));

    vnd.RevenueLTL = vnd.RevenueLTL.plus(vnd.RevenueAll);
    vnd.CostLTL = vnd.CostLTL.plus(vnd.CostAll);
    vnd.ProfitLTL = vnd.RevenueLTL.sub(vnd.CostLTL);
    vnd.ProfitRateLTL = (vnd.ProfitLTL.div(vnd.RevenueLTL || this.Decimal(1))).times(this.Decimal(100));

    vnd.Revenue20 = vnd.Revenue20.plus(vnd.RevenueAll);
