return {
    SaveAs: function (btn) {
        var newEntity = JSON.parse(JSON.stringify(this.Entity));
        this.Entity = newEntity;
        this.Entity.Id = this.Uuid7.NewGuid();
        this.Entity.FromDate = null;
        this.Entity.ToDate = null;
        this.Entity.PostDate = this.dayjs();
        var detailGrid = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "ExchangeRateHistoryDetail");
        if (detailGrid && detailGrid.AllListViewItem.length > 0) {
            detailGrid.AllListViewItem.forEach(row => {
                row.Entity.Id = this.Uuid7.NewGuid();
                row.Entity.HistoryId = this.Entity.Id;
                row.Children.forEach(cell => {
                    cell.Entity = row.Entity;
                });
                row.UpdateView(true);
            });
        }
        this.UpdateView(true);
        this.Dirty = true;
        this.Toast.Success("Save as success");
    },
    CalcRateUSD: function (btn, entity) {
        var exUSD = this.Decimal(this.Entity.RateVND);
        var exVND = this.Decimal(1);
        entity.RateUSD = this.Decimal(1).div(entity.RateVND);
        var detailGrid = this.ChildCom.find(x => x.IsListView && x.Meta.FieldName == "ExchangeRateHistoryDetail");
        detailGrid.Item.forEach(item => {
            item.Entity.RateVND = this.Decimal(item.Entity.RateVND);
            item.Entity.RateUSD = item.Entity.RateVND.div(exUSD);
            item.UpdateView(false, false, "RateUSD");
        });
    },
    UpdateExchangeRate: function (btn, entity) {
        var exUSD = this.Decimal(this.EditForm.Entity.RateVND);
        var exVND = this.Decimal(1);
        entity.RateVND = this.Decimal(entity.RateVND);
        entity.RateUSD = entity.RateVND.div(exUSD);
        btn.Parent.UpdateView(false, false, "RateUSD");
    },
    UpdateExchangeRate2: function (btn, entity) {
        debugger;
        var exUSD = this.Decimal(this.EditForm.Entity.RateVND);
        var exVND = this.Decimal(1);
        entity.RateVND = this.Decimal(JSON.parse(localStorage.getItem("ExchangeRateVND"))[entity.CurrencyCode]);
        entity.RateUSD = entity.RateVND.div(exUSD);
        btn.Parent.UpdateView(false, false, "RateUSD");
    }
}

